/// Tauri IPC Commands for Synapse Integration Gateway
use crate::synapse::connection_manager::ConnectionManager;
use crate::synapse::models::*;
use crate::synapse::plugin_manager::PluginManager;
use crate::synapse::security::{SecurityBoundary, SecuritySummary};
use std::collections::HashMap;
use std::sync::Arc;
use tauri::State;
use tokio::sync::RwLock;

/// State container for Synapse managers
pub struct SynapseState {
    pub plugin_manager: Arc<PluginManager>,
    pub connection_manager: Arc<ConnectionManager>,
    pub security: Arc<SecurityBoundary>,
}

// ============================================================================
// Plugin Commands
// ============================================================================

pub async fn synapse_install_plugin(
    request: CreatePluginRequest,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<SynapsePlugin, String> {
    let state = state.read().await;
    state.plugin_manager.install_plugin(request.manifest).await
}

pub async fn synapse_enable_plugin(
    plugin_id: String,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<SynapsePlugin, String> {
    let state = state.read().await;
    state.plugin_manager.enable_plugin(&plugin_id).await
}

pub async fn synapse_disable_plugin(
    plugin_id: String,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<SynapsePlugin, String> {
    let state = state.read().await;
    state.plugin_manager.disable_plugin(&plugin_id).await
}

pub async fn synapse_get_plugin(
    plugin_id: String,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<SynapsePlugin, String> {
    let state = state.read().await;
    state.plugin_manager.get_plugin(&plugin_id).await
}

pub async fn synapse_list_plugins(
    enabled_only: Option<bool>,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<Vec<SynapsePlugin>, String> {
    let state = state.read().await;
    state
        .plugin_manager
        .list_plugins(enabled_only.unwrap_or(false))
        .await
}

pub async fn synapse_uninstall_plugin(
    plugin_id: String,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<(), String> {
    let state = state.read().await;
    state.plugin_manager.uninstall_plugin(&plugin_id).await
}

pub async fn synapse_update_plugin_permissions(
    plugin_id: String,
    permissions: Vec<String>,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<SynapsePlugin, String> {
    let state = state.read().await;
    state
        .plugin_manager
        .update_permissions(&plugin_id, permissions)
        .await
}

// ============================================================================
// Connection Commands
// ============================================================================

pub async fn synapse_create_connection(
    request: CreateConnectionRequest,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<SynapseConnection, String> {
    let state = state.read().await;

    // Check network permission before creating connection
    if !state.security.check_network_permission(&request.plugin_id, &request.endpoint).await? {
        return Err(format!("Plugin does not have permission to access {}", request.endpoint));
    }

    state.connection_manager.create_connection(request).await
}

pub async fn synapse_get_connection(
    connection_id: String,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<SynapseConnection, String> {
    let state = state.read().await;
    state
        .connection_manager
        .get_connection(&connection_id)
        .await
}

pub async fn synapse_list_connections(
    plugin_id: Option<String>,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<Vec<SynapseConnection>, String> {
    let state = state.read().await;
    state.connection_manager.list_connections(plugin_id).await
}

pub async fn synapse_test_connection(
    connection_id: String,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<TestConnectionResponse, String> {
    let state = state.read().await;
    state
        .connection_manager
        .test_connection(&connection_id)
        .await
}

pub async fn synapse_delete_connection(
    connection_id: String,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<(), String> {
    let state = state.read().await;
    state
        .connection_manager
        .delete_connection(&connection_id)
        .await
}

pub async fn synapse_update_connection_credentials(
    connection_id: String,
    credentials: HashMap<String, String>,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<(), String> {
    let state = state.read().await;
    state
        .connection_manager
        .update_credentials(&connection_id, credentials)
        .await
}

// ============================================================================
// Security Commands
// ============================================================================

pub async fn synapse_check_permission(
    plugin_id: String,
    resource: String,
    action: String,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<bool, String> {
    let state = state.read().await;
    state
        .security
        .check_permission(&plugin_id, &resource, &action)
        .await
}

pub async fn synapse_get_security_logs(
    plugin_id: Option<String>,
    connection_id: Option<String>,
    severity: Option<String>,
    limit: Option<i64>,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<Vec<SynapseSecurityLog>, String> {
    let state = state.read().await;

    let severity_enum = severity
        .map(|s| match s.to_lowercase().as_str() {
            "critical" => Some(SecuritySeverity::Critical),
            "warning" => Some(SecuritySeverity::Warning),
            "info" => Some(SecuritySeverity::Info),
            _ => None,
        })
        .flatten();

    state
        .security
        .get_logs(plugin_id, connection_id, severity_enum, limit)
        .await
}

pub async fn synapse_get_security_summary(
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<SecuritySummary, String> {
    let state = state.read().await;
    state.security.get_security_summary().await
}

pub async fn synapse_clear_old_security_logs(
    older_than: String,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<u64, String> {
    let state = state.read().await;
    state.security.clear_old_logs(&older_than).await
}

// ============================================================================
// Health & Status Commands
// ============================================================================

pub async fn synapse_get_gateway_status(
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<GatewayStatus, String> {
    let state = state.read().await;

    // Get counts
    let plugins = state.plugin_manager.list_plugins(false).await?;
    let enabled_plugins = plugins.iter().filter(|p| p.enabled).count();

    let connections = state.connection_manager.list_connections(None).await?;
    let connected_count = connections
        .iter()
        .filter(|c| matches!(c.status, ConnectionStatus::Connected))
        .count();

    let security_summary = state.security.get_security_summary().await?;

    Ok(GatewayStatus {
        online: true,
        total_plugins: plugins.len(),
        enabled_plugins,
        total_connections: connections.len(),
        connected_count,
        security_events_24h: security_summary.critical_last_24h as usize,
    })
}

#[derive(Debug, Clone, serde::Serialize, serde::Deserialize)]
pub struct GatewayStatus {
    pub online: bool,
    pub total_plugins: usize,
    pub enabled_plugins: usize,
    pub total_connections: usize,
    pub connected_count: usize,
    pub security_events_24h: usize,
}

