      expect(screen.getByText('Clear History')).toBeInTheDocument();
    });

    await user.click(screen.getByText('Clear History'));

    await waitFor(() => {
      expect(screen.getByText('Failed to clear history')).toBeInTheDocument();
    });
  });

  it('should disable clear history when no messages', async () => {
    bridgeInstance.getChatHistory.mockResolvedValue({ 
      success: true, 
      data: [] 
    });

    render(<AldenChatScreen userId="user-1" />);

    await waitFor(() => {
      const clearButton = screen.getByText('Clear History');
      expect(clearButton).toBeDisabled();
    });
  });

  it('should display message timestamps', async () => {
    render(<AldenChatScreen userId="user-1" />);

    await waitFor(() => {
      expect(screen.getByText('Hello Alden')).toBeInTheDocument();
      expect(screen.getByText('Hello! How can I help you today?')).toBeInTheDocument();
    });

    // Check that timestamps are displayed (format depends on locale)
    const timestamps = screen.getAllByText(/\d{1,2}:\d{2}/);
    expect(timestamps.length).toBeGreaterThan(0);
  });

  it('should display voice controls as disabled', () => {
    render(<AldenChatScreen userId="user-1" />);

