    expect(voiceButton).toBeDisabled();
    
    const soundButton = screen.getByTitle('Text-to-speech coming in Phase 4');
    expect(soundButton).toBeDisabled();
  });

  it('should handle component unmount', () => {
    const { unmount } = render(<AldenChatScreen userId="user-1" />);

    unmount();

    // Should not throw errors and bridge should be destroyed
    expect(bridgeInstance.destroy).toHaveBeenCalled();
  });

