> /// RAG (Retrieval-Augmented Generation) Engine
///
/// Provides vector-based semantic search and context retrieval for AI personas.
/// Integrates with memory slices to enable intelligent information retrieval.
use aes_gcm::aead::Aead;
use aes_gcm::{Aes256Gcm, KeyInit, Nonce};
use base64::{engine::general_purpose, Engine as _};
use futures::TryStreamExt;
use serde::{Deserialize, Serialize};
use sqlx::{FromRow, Row};
use std::io::Write;
use std::process::{Command, Stdio};
use std::sync::Arc;
use tokio::sync::RwLock;

use crate::db_manager::DatabaseManager;
use crate::key_manager::SecureKeyManager;

/// Vector embedding for semantic search
#[derive(Debug, Clone, Serialize, Deserialize, FromRow)]
pub struct VectorEmbedding {
    pub id: String,
    pub memory_slice_id: String,
    pub user_id: String,
    pub persona_id: String,
    pub embedding: String, // JSON array of floats
    pub dimension: i64,
    pub model_name: String, // e.g., "text-embedding-ada-002"
    pub chunk_index: i64,   // For large content split into chunks
    pub total_chunks: i64,
    pub metadata: Option<String>, // JSON object
    pub created_at: i64,
}

/// Search result with similarity score
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SearchResult {
    pub memory_slice_id: String,
    pub content: String,
    pub content_type: String,
    pub similarity_score: f64,
    pub chunk_index: i64,
    pub total_chunks: i64,
    pub metadata: Option<String>,
    pub tags: Option<String>,
    pub created_at: i64,
}

/// RAG search query parameters
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct RagQuery {
    pub query_embedding: Vec<f64>,
    pub user_id: String,
    pub persona_id: Option<String>,
    pub content_types: Option<Vec<String>>,
    pub tags: Option<Vec<String>>,
    pub min_similarity: f64,
    pub max_results: i64,
}

/// RAG Engine manager
pub struct RagEngine {
    db: Arc<RwLock<Option<DatabaseManager>>>,
    key_manager: Arc<SecureKeyManager>,
}

impl RagEngine {
    pub fn new(
        db: Arc<RwLock<Option<DatabaseManager>>>,
        key_manager: Arc<SecureKeyManager>,
    ) -> Self {
        Self { db, key_manager }
    }

    /// Initialize RAG Engine schema
    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db_guard = self.db.read().await;
        let db = db_guard.as_ref().ok_or("Database not initialized")?;

        // Vector embeddings table
        sqlx::query(
            r#"
            CREATE TABLE IF NOT EXISTS vector_embeddings (
                id TEXT PRIMARY KEY NOT NULL,
                memory_slice_id TEXT NOT NULL,
                user_id TEXT NOT NULL,
                persona_id TEXT NOT NULL,
                embedding TEXT NOT NULL,
                dimension INTEGER NOT NULL,
                model_name TEXT NOT NULL,
                chunk_index INTEGER NOT NULL DEFAULT 0,
                total_chunks INTEGER NOT NULL DEFAULT 1,
                metadata TEXT,
                created_at INTEGER NOT NULL,
                FOREIGN KEY (memory_slice_id) REFERENCES memory_slices(id) ON DELETE CASCADE,
                CHECK(dimension > 0),
                CHECK(chunk_index >= 0),
                CHECK(total_chunks > 0),
                CHECK(chunk_index < total_chunks)
            )
        "#,
        )
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to create vector_embeddings table: {}", e))?;

        // Indexes for performance
        sqlx::query(
            r#"
            CREATE INDEX IF NOT EXISTS idx_vector_memory_slice
            ON vector_embeddings(memory_slice_id)
        "#,
        )
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to create idx_vector_memory_slice: {}", e))?;

        sqlx::query(
            r#"
            CREATE INDEX IF NOT EXISTS idx_vector_user_persona
            ON vector_embeddings(user_id, persona_id)
        "#,
        )
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to create idx_vector_user_persona: {}", e))?;

        sqlx::query(
            r#"
            CREATE INDEX IF NOT EXISTS idx_vector_model
            ON vector_embeddings(model_name)
        "#,
        )
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to create idx_vector_model: {}", e))?;

        Ok(())
    }

    /// Store a vector embedding
    pub async fn store_embedding(&self, embedding: VectorEmbedding) -> Result<(), String> {
        let db_guard = self.db.read().await;
        let db = db_guard.as_ref().ok_or("Database not initialized")?;

        sqlx::query(
            r#"
            INSERT INTO vector_embeddings (
                id, memory_slice_id, user_id, persona_id, embedding, dimension,
                model_name, chunk_index, total_chunks, metadata, created_at
            ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
        "#,
        )
        .bind(&embedding.id)
        .bind(&embedding.memory_slice_id)
        .bind(&embedding.user_id)
        .bind(&embedding.persona_id)
        .bind(&embedding.embedding)
        .bind(embedding.dimension)
        .bind(&embedding.model_name)
        .bind(embedding.chunk_index)
        .bind(embedding.total_chunks)
        .bind(&embedding.metadata)
        .bind(embedding.created_at)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to store embedding: {}", e))?;

        Ok(())
    }

    /// Get embeddings for a memory slice
    pub async fn get_embeddings(
        &self,
        memory_slice_id: &str,
    ) -> Result<Vec<VectorEmbedding>, String> {
        let db_guard = self.db.read().await;
        let db = db_guard.as_ref().ok_or("Database not initialized")?;

        let embeddings = sqlx::query_as::<_, VectorEmbedding>(
            r#"
            SELECT * FROM vector_embeddings
            WHERE memory_slice_id = ?
            ORDER BY chunk_index ASC
        "#,
        )
        .bind(memory_slice_id)
        .fetch_all(&db.pool)
        .await
        .map_err(|e| format!("Failed to get embeddings: {}", e))?;

        Ok(embeddings)
    }

    /// Delete embeddings for a memory slice
    pub async fn delete_embeddings(&self, memory_slice_id: &str) -> Result<(), String> {
        let db_guard = self.db.read().await;
        let db = db_guard.as_ref().ok_or("Database not initialized")?;

        sqlx::query(
            r#"
            DELETE FROM vector_embeddings
            WHERE memory_slice_id = ?
        "#,
        )
        .bind(memory_slice_id)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to delete embeddings: {}", e))?;

        Ok(())
    }

    /// Perform semantic search using cosine similarity
    pub async fn search(&self, query: RagQuery) -> Result<Vec<SearchResult>, String> {
        let db_guard = self.db.read().await;
        let db = db_guard.as_ref().ok_or("Database not initialized")?;
        let max_results = query.max_results.max(1) as usize;
        let query_norm = Self::vector_magnitude(&query.query_embedding);

        if query_norm == 0.0 {
            return Ok(Vec::new());
        }

        let mut sql = String::from(
            r#"
            SELECT
                ve.memory_slice_id,
                ve.embedding,
                ve.chunk_index,
                ve.total_chunks,
                ve.metadata,
                ms.encrypted_content,
                ms.nonce,
                ms.memory_type AS content_type,
                ms.tags,
                ms.created_at
            FROM vector_embeddings ve
            JOIN memory_slices ms ON ve.memory_slice_id = ms.memory_id
            WHERE ve.user_id = ?
                AND ms.status = 'active'
        "#,
        );

        let mut params: Vec<String> = vec![query.user_id.clone()];

        if let Some(persona_id) = &query.persona_id {
            sql.push_str(" AND ve.persona_id = ?");
            params.push(persona_id.clone());
        }

        if let Some(content_types) = &query.content_types {
            if !content_types.is_empty() {
                sql.push_str(" AND ms.memory_type IN (");
                for (i, _) in content_types.iter().enumerate() {
                    if i > 0 {
                        sql.push_str(", ");
                    }
                    sql.push_str("?");
                }
                sql.push_str(")");
                params.extend(content_types.clone());
            }
        }

        let mut query_builder = sqlx::query(&sql);
        for param in &params {
            query_builder = query_builder.bind(param);
        }

        let mut rows = query_builder.fetch(&db.pool);
        let mut results: Vec<SearchResult> = Vec::new();

        while let Some(row) = rows
            .try_next()
            .await
            .map_err(|e| format!("Failed to fetch candidates: {}", e))?
        {
            let tags_value: Option<String> = row.try_get("tags").ok();
            if let Some(filter_tags) = &query.tags {
                if !Self::tags_match(filter_tags, tags_value.as_ref()) {
                    continue;
                }
            }

            let embedding_json: String = row
                .try_get("embedding")
                .map_err(|e| format!("Failed to get embedding: {}", e))?;

            let embedding: Vec<f64> = serde_json::from_str(&embedding_json)
                .map_err(|e| format!("Failed to parse embedding: {}", e))?;

            if embedding.len() != query.query_embedding.len() {
                continue;
            }

            let similarity = Self::cosine_similarity_precomputed(
                &query.query_embedding,
                query_norm,
                &embedding,
            );

            if similarity < query.min_similarity {
                continue;
            }

            let encrypted_content: String = row
                .try_get("encrypted_content")
                .map_err(|e| format!("Failed to get encrypted_content: {}", e))?;
            let nonce: String = row
                .try_get("nonce")
                .map_err(|e| format!("Failed to get nonce: {}", e))?;
            let plaintext_content = self
                .decrypt_memory_content(&encrypted_content, &nonce)
                .await?;

            let result = SearchResult {
                memory_slice_id: row
                    .try_get("memory_slice_id")
                    .map_err(|e| format!("Failed to get memory_slice_id: {}", e))?,
                content: plaintext_content,
                content_type: row
                    .try_get("content_type")
                    .map_err(|e| format!("Failed to get content_type: {}", e))?,
                similarity_score: similarity,
                chunk_index: row
                    .try_get("chunk_index")
                    .map_err(|e| format!("Failed to get chunk_index: {}", e))?,
                total_chunks: row
                    .try_get("total_chunks")
                    .map_err(|e| format!("Failed to get total_chunks: {}", e))?,
                metadata: row.try_get("metadata").ok(),
                tags: tags_value,
                created_at: row
                    .try_get("created_at")
                    .map_err(|e| format!("Failed to get created_at: {}", e))?,
            };

            results.push(result);
        }

        results.sort_by(|a, b| {
            b.similarity_score
                .partial_cmp(&a.similarity_score)
                .unwrap_or(std::cmp::Ordering::Equal)
        });

        if results.len() > max_results {
            results.truncate(max_results);
        }

        Ok(results)
    }

    /// Calculate cosine similarity between two vectors with precomputed magnitude for the query
    fn cosine_similarity_precomputed(a: &[f64], magnitude_a: f64, b: &[f64]) -> f64 {
        if a.len() != b.len() {
            return 0.0;
        }

        let dot_product: f64 = a.iter().zip(b.iter()).map(|(x, y)| x * y).sum();
        let magnitude_b: f64 = Self::vector_magnitude(b);

        if magnitude_a == 0.0 || magnitude_b == 0.0 {
            return 0.0;
        }

        dot_product / (magnitude_a * magnitude_b)
    }

    fn vector_magnitude(v: &[f64]) -> f64 {
        v.iter().map(|x| x * x).sum::<f64>().sqrt()
    }

    async fn decrypt_memory_content(
        &self,
        ciphertext_b64: &str,
        nonce_b64: &str,
    ) -> Result<String, String> {
        let key_bytes = self
            .key_manager
            .get_vault_key()
            .await
            .map_err(|e| format!("Failed to load vault key: {}", e))?;

        let cipher = Aes256Gcm::new_from_slice(&key_bytes)
            .map_err(|e| format!("Failed to create cipher: {}", e))?;

        let nonce_bytes = general_purpose::STANDARD
            .decode(nonce_b64)
            .map_err(|e| format!("Invalid nonce encoding: {}", e))?;

        let ciphertext = general_purpose::STANDARD
            .decode(ciphertext_b64)
            .map_err(|e| format!("Invalid ciphertext encoding: {}", e))?;

        let plaintext = cipher
            .decrypt(Nonce::from_slice(&nonce_bytes), ciphertext.as_ref())
            .map_err(|e| format!("Decryption failed: {}", e))?;

        String::from_utf8(plaintext).map_err(|e| format!("Invalid UTF-8: {}", e))
    }

    fn tags_match(filter_tags: &[String], stored_tags: Option<&String>) -> bool {
        if filter_tags.is_empty() {
            return true;
        }

        let Some(stored) = stored_tags else {
            return false;
        };

        let parsed: Vec<String> = serde_json::from_str(stored).unwrap_or_default();
        filter_tags.iter().any(|tag| parsed.iter().any(|stored| stored == tag))
    }

    /// Get embedding statistics
    pub async fn get_stats(
        &self,
        user_id: &str,
        persona_id: Option<&str>,
    ) -> Result<RagStats, String> {
        let db_guard = self.db.read().await;
        let db = db_guard.as_ref().ok_or("Database not initialized")?;

        let mut sql = String::from(
            r#"
            SELECT
                COUNT(DISTINCT ve.id) as total_embeddings,
                COUNT(DISTINCT ve.memory_slice_id) as total_memory_slices,
                COUNT(DISTINCT ve.model_name) as unique_models,
                AVG(ve.dimension) as avg_dimension
            FROM vector_embeddings ve
            WHERE ve.user_id = ?
        "#,
        );

        let mut query_builder = sqlx::query(&sql).bind(user_id);

        if let Some(persona) = persona_id {
            sql.push_str(" AND ve.persona_id = ?");
            query_builder = sqlx::query(&sql).bind(user_id).bind(persona);
        }

        let row = query_builder
            .fetch_one(&db.pool)
            .await
            .map_err(|e| format!("Failed to get stats: {}", e))?;

        Ok(RagStats {
            total_embeddings: row.try_get("total_embeddings").unwrap_or(0),
            total_memory_slices: row.try_get("total_memory_slices").unwrap_or(0),
            unique_models: row.try_get("unique_models").unwrap_or(0),
            avg_dimension: row.try_get("avg_dimension").unwrap_or(0.0),
        })
    }
}

