> /// DateTime Serialization Utilities
///
/// Provides serde custom serialization functions to convert between:
/// - Internal storage: i64 Unix timestamps (efficient storage, fast SQL operations)
/// - IPC boundary: ISO 8601 strings (TypeScript/frontend compatibility)
///
/// This ensures the Rust backend uses efficient integer timestamps while
/// the TypeScript frontend receives ISO 8601 strings as per the data contract.
///
/// Usage:
/// ```rust
/// #[derive(Serialize, Deserialize)]
/// pub struct Task {
///     #[serde(
///         serialize_with = "datetime_serde::serialize_timestamp",
///         deserialize_with = "datetime_serde::deserialize_timestamp"
///     )]
///     pub created_at: i64,
///
///     #[serde(
///         serialize_with = "datetime_serde::serialize_optional_timestamp",
///         deserialize_with = "datetime_serde::deserialize_optional_timestamp"
///     )]
///     pub due_date: Option<i64>,
/// }
/// ```

use chrono::{DateTime, TimeZone, Utc};
use serde::{Deserialize, Deserializer, Serializer};

/// Serialize an i64 Unix timestamp as an ISO 8601 string
///
/// Converts internal i64 timestamp to RFC 3339/ISO 8601 format for JSON serialization.
/// Example: 1733745000 ƒ+' "2025-12-09T10:30:00Z"
///
/// # Errors
/// Returns serialization error if timestamp is invalid or out of range
pub fn serialize_timestamp<S>(timestamp: &i64, serializer: S) -> Result<S::Ok, S::Error>
where
    S: Serializer,
{
    let dt = Utc
        .timestamp_opt(*timestamp, 0)
        .single()
        .ok_or_else(|| serde::ser::Error::custom(format!("Invalid timestamp: {}", timestamp)))?;

    serializer.serialize_str(&dt.to_rfc3339())
}

/// Deserialize an ISO 8601 string as an i64 Unix timestamp
///
/// Converts ISO 8601/RFC 3339 string from JSON to internal i64 timestamp.
/// Example: "2025-12-09T10:30:00Z" ƒ+' 1733745000
///
/// # Errors
/// Returns deserialization error if string is not valid ISO 8601 format
pub fn deserialize_timestamp<'de, D>(deserializer: D) -> Result<i64, D::Error>
where
    D: Deserializer<'de>,
{
    let s: String = Deserialize::deserialize(deserializer)?;
    DateTime::parse_from_rfc3339(&s)
        .map(|dt| dt.timestamp())
        .map_err(|e| serde::de::Error::custom(format!("Invalid ISO 8601 timestamp '{}': {}", s, e)))
}

/// Serialize an Option<i64> Unix timestamp as an Option<ISO 8601 string>
///
/// Handles optional timestamp fields by:
/// - Some(timestamp) ƒ+' Some("2025-12-09T10:30:00Z")
/// - None ƒ+' null
///
/// # Errors
/// Returns serialization error if timestamp is invalid or out of range
pub fn serialize_optional_timestamp<S>(
    timestamp: &Option<i64>,
    serializer: S,
) -> Result<S::Ok, S::Error>
where
    S: Serializer,
{
    match timestamp {
        Some(ts) => {
            let dt = Utc.timestamp_opt(*ts, 0).single().ok_or_else(|| {
                serde::ser::Error::custom(format!("Invalid timestamp: {}", ts))
            })?;
            serializer.serialize_some(&dt.to_rfc3339())
        }
        None => serializer.serialize_none(),
    }
}

/// Deserialize an Option<ISO 8601 string> as an Option<i64> Unix timestamp
///
/// Handles optional timestamp fields by:
/// - Some("2025-12-09T10:30:00Z") ƒ+' Some(1733745000)
/// - null ƒ+' None
///
/// # Errors
/// Returns deserialization error if string is not valid ISO 8601 format
pub fn deserialize_optional_timestamp<'de, D>(
    deserializer: D,
) -> Result<Option<i64>, D::Error>
where
    D: Deserializer<'de>,
{
    let opt: Option<String> = Option::deserialize(deserializer)?;
    opt.map(|s| {
        DateTime::parse_from_rfc3339(&s)
            .map(|dt| dt.timestamp())
            .map_err(|e| {
                serde::de::Error::custom(format!("Invalid ISO 8601 timestamp '{}': {}", s, e))
            })
    })
    .transpose()
}

