import { useCallback, useEffect, useRef, useState } from 'react';

interface VoiceState {
  isSupported: boolean;
  isListening: boolean;
  interimTranscript: string;
  finalTranscript: string;
  finalTranscriptSeq: number;
  isSpeaking: boolean;
  error: string | null;
}

export interface UseVoiceInputOptions {
  /**
   * Callback invoked when the hook detects a speech recognition error.
   */
  onError?: (message: string) => void;
}

export interface UseVoiceInputResult {
  isSupported: boolean;
  isListening: boolean;
  interimTranscript: string;
  finalTranscript: string;
  finalTranscriptSeq: number;
  isSpeaking: boolean;
  error: string | null;
  startListening: () => void;
  stopListening: () => void;
  toggleListening: () => void;
  speak: (text: string) => void;
  stopSpeaking: () => void;
  clearTranscripts: () => void;
}

const initialVoiceState: VoiceState = {
  isSupported: false,
  isListening: false,
  interimTranscript: '',
  finalTranscript: '',
  finalTranscriptSeq: 0,
  isSpeaking: false,
  error: null,
};



export const useVoiceInput = ({ onError }: UseVoiceInputOptions = {}): UseVoiceInputResult => {
  const [state, setState] = useState<VoiceState>(initialVoiceState);
  const recognitionRef = useRef<SpeechRecognition | null>(null);
  const stateRef = useRef<VoiceState>(initialVoiceState);

  useEffect(() => {
    stateRef.current = state;
  }, [state]);

  useEffect(() => {
    if (typeof window === 'undefined') {
      setState(prev => ({
        ...prev,
        error: 'Voice features are unavailable in this environment.',
        isSupported: false,
      }));
      return;
    }

    const Recognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!Recognition) {
      setState(prev => ({
        ...prev,
        error: 'Speech recognition is not supported by this browser.',
        isSupported: false,
      }));
      return;
    }

    setState(prev => ({ ...prev, isSupported: true, error: null }));

    const recognition = new Recognition();
    recognition.continuous = false;
    recognition.interimResults = true;
    recognition.lang = 'en-US';
    recognition.maxAlternatives = 1;

    recognition.onstart = () => {
      setState(prev => ({ ...prev, isListening: true, error: null }));
    };

    recognition.onresult = (event: SpeechRecognitionEvent) => {
      let interim = '';
      let finalText = '';

      for (let i = event.resultIndex; i < event.results.length; i += 1) {
        const result = event.results[i];
        const transcript = result[0]?.transcript?.trim() ?? '';

        if (result.isFinal) {
          finalText += `${transcript} `;
        } else {
          interim += `${transcript} `;
        }
      }

      if (finalText) {
        setState(prev => ({
          ...prev,
          finalTranscript: finalText.trim(),
          finalTranscriptSeq: prev.finalTranscriptSeq + 1,
          interimTranscript: '',
        }));
      } else if (interim) {
        setState(prev => ({ ...prev, interimTranscript: interim.trim() }));
      }
    };

    recognition.onerror = (event: SpeechRecognitionErrorEvent) => {
      const message = event.error || 'Unknown speech recognition failure';
      const errorMessage = `Speech recognition error: ${message}`;
      setState(prev => ({
        ...prev,
        error: errorMessage,
        isListening: false,
      }));
      if (onError) {
        onError(errorMessage);
      }
    };

    recognition.onend = () => {
      setState(prev => ({ ...prev, isListening: false }));
    };

    recognitionRef.current = recognition;

    return () => {
      recognition.stop();
      recognitionRef.current = null;
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const startListening = useCallback(() => {
    if (!recognitionRef.current || stateRef.current.isListening) {
      return;
    }

    try {
      recognitionRef.current.start();
      setState(prev => ({ ...prev, interimTranscript: '', finalTranscript: '' }));
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Failed to start speech recognition';
      setState(prev => ({ ...prev, error: message }));
      if (onError) {
        onError(message);
      }
    }
    // dependencies intentionally omit onError to keep reference stable
  }, [onError]);

  const stopListening = useCallback(() => {
    if (!recognitionRef.current || !stateRef.current.isListening) {
      return;
    }
    recognitionRef.current.stop();
  }, []);

  const toggleListening = useCallback(() => {
    if (stateRef.current.isListening) {
      stopListening();
    } else {
      startListening();
    }
  }, [startListening, stopListening]);

  const speak = useCallback((text: string) => {
    if (typeof window === 'undefined' || !('speechSynthesis' in window)) {
      setState(prev => ({
        ...prev,
        error: 'Text-to-speech is not supported in this environment.',
      }));
      return;
    }

    if (!text.trim()) {
      return;
    }

    window.speechSynthesis.cancel();

    const utterance = new SpeechSynthesisUtterance(text);
    utterance.onstart = () => {
      setState(prev => ({ ...prev, isSpeaking: true }));
    };
    utterance.onend = () => {
      setState(prev => ({ ...prev, isSpeaking: false }));
    };
    utterance.onerror = () => {
      setState(prev => ({ ...prev, isSpeaking: false, error: 'Text-to-speech encountered an error.' }));
    };

    window.speechSynthesis.speak(utterance);
  }, []);

  const stopSpeaking = useCallback(() => {
    if (typeof window === 'undefined' || !('speechSynthesis' in window)) {
      return;
    }

    window.speechSynthesis.cancel();
    setState(prev => ({ ...prev, isSpeaking: false }));
  }, []);

  const clearTranscripts = useCallback(() => {
    setState(prev => ({ ...prev, interimTranscript: '', finalTranscript: '' }));
  }, []);

  return {
    ...state,
    startListening,
    stopListening,
    toggleListening,
    speak,
    stopSpeaking,
    clearTranscripts,
  };
};

