import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { Mic, MicOff, Send, Volume2, VolumeX } from 'lucide-react';
import { AldenBridge } from '../../bridges';
import { ChatMessage } from '../../types/agents';
import { useVoiceInput } from '../../hooks/useVoiceInput';

interface AldenChatScreenProps {
  userId?: string;
  sessionId?: string;
}

type NormalizedChatMessage = {
  id: string;
  role: 'user' | 'alden' | 'system';
  content: string;
  createdAt: string;
  sessionId: string;
};

const normalizeChatMessage = (entry: any): NormalizedChatMessage => {
  const id =
    entry.messageId ||
    entry.message_id ||
    entry.id ||
    `${entry.userId || entry.user_id || 'chat'}-${Date.now()}-${Math.random().toString(36).slice(2)}`;

  const rawRole = String(entry.role || entry.sender || entry.from_agent || entry.author || 'alden').toLowerCase();
  const role: NormalizedChatMessage['role'] =
    rawRole === 'assistant' ? 'alden' : (rawRole as NormalizedChatMessage['role']);

  const content = entry.content ?? entry.message ?? entry.response ?? '';
  const createdAt =
    (typeof entry.createdAt === 'string' && entry.createdAt) ||
    (typeof entry.timestamp === 'string' && entry.timestamp) ||
    (typeof entry.created_at === 'string' && entry.created_at) ||
    new Date().toISOString();
  const sessionId =
    (typeof entry.sessionId === 'string' && entry.sessionId) ||
    (typeof entry.session_id === 'string' && entry.session_id) ||
    'session-default';

  return {
    id: String(id),
    role: role === 'user' || role === 'alden' ? role : 'system',
    content,
    createdAt,
    sessionId,
  };
};

const createLocalMessage = (content: string, sessionId: string): NormalizedChatMessage => ({
  id: `local-${Date.now()}-${Math.random().toString(36).slice(2)}`,
  role: 'user',
  content,
  createdAt: new Date().toISOString(),
  sessionId,
});

export const AldenChatScreen: React.FC<AldenChatScreenProps> = ({ userId = 'default_user', sessionId }) => {
  const [message, setMessage] = useState('');
  const [isSoundEnabled, setIsSoundEnabled] = useState(true);
  const [conversationHistory, setConversationHistory] = useState<NormalizedChatMessage[]>([]);
  const [isSending, setIsSending] = useState(false);
