/**
 * Authentication Context Provider
 *
 * Provides authentication state and user session management across the application.
 * Integrates with Sentry auth system for secure user identification.
 *
 * @version 1.0.0
 * @date 2025-11-15
 */

import React, { createContext, useContext, useState, useEffect, useCallback, useMemo } from 'react';
import { SentryBridge } from '../bridges/SentryBridge';

interface AuthContextType {
  userId: string | null;
  sessionId: string | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  login: (credentials: { userId: string; password: string }) => Promise<boolean>;
  logout: () => Promise<void>;
  autoLogin: () => Promise<boolean>;
  verifySession: () => Promise<boolean>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

let lastKnownUserId = 'default_user';

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [userId, setUserId] = useState<string | null>(null);
  const [sessionId, setSessionId] = useState<string | null>(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  // Initialize Sentry bridge
  const sentryBridge = useMemo(() => new SentryBridge(), []);

  /**
   * Auto-login on app startup
   * Attempts to restore previous session or create default user session
   */
  useEffect(() => {
    const initializeAuth = async () => {
      setIsLoading(true);
      try {
        // Try auto-login first (biometric or cached credentials)
        const autoLoginResult = await sentryBridge.autoLogin();

        if (autoLoginResult.success && autoLoginResult.data) {
          setUserId(autoLoginResult.data.userId);
          setSessionId(autoLoginResult.data.sessionId);
          setIsAuthenticated(true);
        } else {
          // Fallback: Check for existing session
          const sessionResult = await sentryBridge.getSession();

          if (sessionResult.success && sessionResult.data) {
            setUserId(sessionResult.data.userId);
            setSessionId(sessionResult.data.sessionId);
            setIsAuthenticated(true);
          } else {
            // No session found - use default user for now
            // TODO: Implement proper login screen
            setUserId('default_user');
            setIsAuthenticated(true);
          }
        }
      } catch (error) {
        console.error('[AuthContext] Failed to initialize auth:', error);
        // Graceful degradation - use default user
        setUserId('default_user');
        setIsAuthenticated(true);
      } finally {
        setIsLoading(false);
      }
    };

    initializeAuth();
  }, [sentryBridge]);

  useEffect(() => {
    if (userId) {
      lastKnownUserId = userId;
    }
  }, [userId]);

  /**
   * Login with credentials
   */
  const login = useCallback(async (credentials: { userId: string; password: string }): Promise<boolean> => {
    try {
      const result = await sentryBridge.login(credentials);

      if (!result.success) {
        console.error('[AuthContext] Login failed:', result.error);
        return false;
      }

      setUserId(result.data.userId);
      setSessionId(result.data.sessionId);
      setIsAuthenticated(true);
      return true;
    } catch (error) {
      console.error('[AuthContext] Login error:', error);
      return false;
    }
  }, [sentryBridge]);

  /**
   * Logout and clear session
   */
  const logout = useCallback(async (): Promise<void> => {
    try {
      await sentryBridge.logout();
      setUserId(null);
      setSessionId(null);
      setIsAuthenticated(false);
    } catch (error) {
      console.error('[AuthContext] Logout error:', error);
    }
  }, [sentryBridge]);

  /**
   * Auto-login (called after app restart or biometric prompt)
   */
  const autoLogin = useCallback(async (): Promise<boolean> => {
    try {
      const result = await sentryBridge.autoLogin();

      if (!result.success) {
        console.warn('[AuthContext] Auto-login failed:', result.error);
        return false;
      }

      setUserId(result.data.userId);
      setSessionId(result.data.sessionId);
      setIsAuthenticated(true);
      return true;
    } catch (error) {
      console.error('[AuthContext] Auto-login error:', error);
      return false;
    }
  }, [sentryBridge]);

  /**
   * Verify current session is still valid
   */
  const verifySession = useCallback(async (): Promise<boolean> => {
    if (!sessionId) return false;

    try {
      const result = await sentryBridge.verifySession(sessionId);

      if (result.success && result.data) {
        return result.data;
      } else {
        // Session invalid - clear auth state
        setUserId(null);
        setSessionId(null);
        setIsAuthenticated(false);
        return false;
      }
    } catch (error) {
      console.error('[AuthContext] Session verification error:', error);
      return false;
    }
  }, [sessionId, sentryBridge]);

  const value: AuthContextType = {
    userId,
    sessionId,
    isAuthenticated,
    isLoading,
    login,
    logout,
    autoLogin,
    verifySession,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

/**
 * Custom hook to use authentication context
 * @throws Error if used outside AuthProvider
 */
export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

/**
 * Helper function to get current user ID
 * Provides fallback for backwards compatibility
 */
export const getCurrentUserId = (): string => {
  return lastKnownUserId;
};

