import { useState, useEffect } from 'react';
import WorkspaceService, {
  WorkspaceContext,
  PersonaStatus,
  ActivityEntry,
  ProjectContext,
  OversightConfig,
  WorkspacePreferences,
} from '@/services/WorkspaceService';

/**
 * Hook to access and manage workspace state
 */
export function useWorkspace() {
  const [workspace, setWorkspace] = useState<WorkspaceContext | null>(WorkspaceService.getWorkspace());

  useEffect(() => {
    // Initial load
    setWorkspace(WorkspaceService.getWorkspace());

    // Listen for activity changes (workspace updates are logged as activities)
    const unsubscribe = WorkspaceService.onActivity(() => {
      setWorkspace(WorkspaceService.getWorkspace());
    });

    return unsubscribe;
  }, []);

  const updateWorkspace = (updates: Partial<WorkspaceContext>) => {
    WorkspaceService.updateWorkspace(updates);
    setWorkspace(WorkspaceService.getWorkspace());
  };

  const setProject = (project: ProjectContext) => {
    WorkspaceService.setProjectContext(project);
    setWorkspace(WorkspaceService.getWorkspace());
  };

  const updateProject = (updates: Partial<ProjectContext>) => {
    WorkspaceService.updateProjectContext(updates);
    setWorkspace(WorkspaceService.getWorkspace());
  };

  return {
    workspace,
    updateWorkspace,
    setProject,
    updateProject,
    currentProject: workspace?.currentProject,
  };
}

/**
 * Hook to access all persona statuses
 */
export function usePersonas() {
  const [personas, setPersonas] = useState<PersonaStatus[]>(WorkspaceService.getAllPersonas());

  useEffect(() => {
    // Initial load
    setPersonas(WorkspaceService.getAllPersonas());

    // Listen for persona updates
    const unsubscribe = WorkspaceService.onPersonaUpdate(() => {
      setPersonas(WorkspaceService.getAllPersonas());
    });

    return unsubscribe;
  }, []);

  const updatePersona = (personaId: string, updates: Partial<PersonaStatus>) => {
    WorkspaceService.updatePersonaStatus(personaId, updates);
  };

  const deactivatePersona = (personaId: string) => {
    WorkspaceService.deactivatePersona(personaId);
  };

  return {
    personas,
    updatePersona,
    deactivatePersona,
    getPersona: (id: string) => personas.find(p => p.id === id),
  };
}

/**
 * Hook to access a specific persona's status
 */
export function usePersona(personaId: string) {
  const [persona, setPersona] = useState<PersonaStatus | undefined>(
    WorkspaceService.getPersonaStatus(personaId)
  );

  useEffect(() => {
    // Initial load
    setPersona(WorkspaceService.getPersonaStatus(personaId));

    // Listen for updates to this specific persona
    const unsubscribe = WorkspaceService.onPersonaUpdate((updated) => {
      if (updated.id === personaId) {
        setPersona(updated);
      }
    });

    return unsubscribe;
  }, [personaId]);

  const updateStatus = (updates: Partial<PersonaStatus>) => {
    WorkspaceService.updatePersonaStatus(personaId, updates);
  };

  const deactivate = () => {
    WorkspaceService.deactivatePersona(personaId);
  };

  return {
    persona,
    updateStatus,
    deactivate,
    isActive: persona?.state === 'active',
    isBusy: persona?.state === 'busy',
    isOffline: persona?.state === 'offline',
  };
}

/**
 * Hook to access activity log
 */
export function useActivity(limit = 50) {
  const [activities, setActivities] = useState<ActivityEntry[]>(
    WorkspaceService.getRecentActivity(limit)
  );

  useEffect(() => {
    // Initial load
    setActivities(WorkspaceService.getRecentActivity(limit));

    // Listen for new activities
    const unsubscribe = WorkspaceService.onActivity(() => {
      setActivities(WorkspaceService.getRecentActivity(limit));
    });

    return unsubscribe;
  }, [limit]);

  const logActivity = (activity: Omit<ActivityEntry, 'id' | 'timestamp'>) => {
    WorkspaceService.logActivity(activity);
  };

  const clearActivity = () => {
    WorkspaceService.clearActivity();
  };

  const getPersonaActivity = (personaId: string, personaLimit = 20) => {
    return WorkspaceService.getPersonaActivity(personaId, personaLimit);
  };

  return {
    activities,
    logActivity,
    clearActivity,
    getPersonaActivity,
  };
}

/**
 * Hook to access oversight configuration
 */
export function useOversight() {
  const [config, setConfig] = useState<OversightConfig>(WorkspaceService.getOversightConfig());

  useEffect(() => {
    // Listen for config changes via activity log
    const unsubscribe = WorkspaceService.onActivity((activity) => {
      if (activity.action === 'oversight_config_updated') {
        setConfig(WorkspaceService.getOversightConfig());
      }
    });

    return unsubscribe;
  }, []);

  const updateConfig = (updates: Partial<OversightConfig>) => {
    WorkspaceService.updateOversightConfig(updates);
    setConfig(WorkspaceService.getOversightConfig());
  };

  return {
    config,
    updateConfig,
  };
}

/**
 * Hook to access workspace preferences
 */
export function useWorkspacePreferences() {
  const [preferences, setPreferences] = useState<WorkspacePreferences>(
    WorkspaceService.getPreferences()
  );

  useEffect(() => {
    // Listen for preference changes via activity log
    const unsubscribe = WorkspaceService.onActivity((activity) => {
      if (activity.action === 'preferences_updated') {
        setPreferences(WorkspaceService.getPreferences());
      }
    });

    return unsubscribe;
  }, []);

  const updatePreferences = (updates: Partial<WorkspacePreferences>) => {
    WorkspaceService.updatePreferences(updates);
    setPreferences(WorkspaceService.getPreferences());
  };

  return {
    preferences,
    updatePreferences,
  };
}

/**
 * Hook to export/import workspace
 */
export function useWorkspaceIO() {
  const exportWorkspace = () => {
    return WorkspaceService.exportWorkspace();
  };

  const importWorkspace = (data: string) => {
    try {
      WorkspaceService.importWorkspace(data);
      return { success: true, error: null };
    } catch (error) {
      return { success: false, error: error instanceof Error ? error.message : 'Import failed' };
    }
  };

  const downloadWorkspace = () => {
    const data = exportWorkspace();
    const blob = new Blob([data], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `hearthlink-workspace-${new Date().toISOString().split('T')[0]}.json`;
    link.click();
    URL.revokeObjectURL(url);
  };

  return {
    exportWorkspace,
    importWorkspace,
    downloadWorkspace,
  };
}

