/// Mimic Persona - Dynamic Persona Management
///
/// Migrated from Python (src/personas/mimic.py) to Rust
///
/// Provides adaptive persona capabilities through:
/// - Persona creation and lifecycle management
/// - Performance analytics and tier classification
/// - Trait updates with validation and audit logging
/// - Knowledge indexing with topic scores
/// - Plugin extension system
/// - Fork/merge capabilities for persona evolution
/// - Role switching for gap-filling (health, financial, legal, creative, technical)
/// - Correction events for learning and adaptation
/// - Growth statistics and usage tracking
use serde::{Deserialize, Serialize};
use sqlx::FromRow;
use std::collections::HashMap;
use std::sync::Arc;
use tauri::State;
use tokio::sync::RwLock;

// Import database manager
use crate::db_manager::DatabaseManager;

// Database row structs (for sqlx::query_as)
#[derive(Debug, FromRow)]
struct MimicPersonaRow {
    pub persona_id: String,
    pub user_id: String,
    pub name: String,
    pub description: String,
    pub traits: String,          // JSON string
    pub expertise_areas: String, // JSON string
    pub active: bool,
    pub created_at: String,
}

// API representation (deserialized JSON fields)
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MimicPersona {
    pub persona_id: String,
    pub user_id: String,
    pub name: String,
    pub description: String,
    pub traits: HashMap<String, f64>,
    pub expertise_areas: Vec<String>,
    pub active: bool,
    pub created_at: String,
}

// Database row for Adaptation
#[derive(Debug, FromRow)]
struct MimicAdaptationRow {
    pub adaptation_id: String,
    pub persona_id: String,
    pub context: String,
    pub changes: String, // JSON string
    pub effectiveness_score: f64,
    pub created_at: String,
}

// API representation for Adaptation
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MimicAdaptation {
    pub adaptation_id: String,
    pub persona_id: String,
    pub context: String,
    pub changes: HashMap<String, serde_json::Value>,
    pub effectiveness_score: f64,
    pub created_at: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct CreatePersonaRequest {
    pub user_id: String,
    pub name: String,
    pub description: String,
    pub traits: HashMap<String, f64>,
    pub expertise_areas: Vec<String>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct UpdatePersonaRequest {
    pub persona_id: String,
    pub user_id: String,
    pub name: Option<String>,
    pub description: Option<String>,
    pub traits: Option<HashMap<String, f64>>,
    pub expertise_areas: Option<Vec<String>>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SwitchPersonaRequest {
