    pub task: String,
    pub score: i32,
    pub user_feedback: String,
    pub success: bool,
    pub duration: f64,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct UpdateTraitRequest {
    pub persona_id: String,
    pub trait_name: String,
    pub new_value: f64,
    pub reason: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct CorrectionEventRequest {
    pub persona_id: String,
    pub event_type: String, // "positive" or "negative"
    pub description: String,
    pub impact_score: f64, // -1.0 to 1.0
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct RoleSwitchRequest {
    pub persona_id: String,
