    pub context: HashMap<String, String>,
}

// Additional Request structs for protocol uniformity
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct GetPersonasRequest {
    #[serde(rename = "userId")]
    pub user_id: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct GetActivePersonaRequest {
    #[serde(rename = "userId")]
    pub user_id: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DeletePersonaRequest {
    #[serde(rename = "userId")]
    pub user_id: String,
    #[serde(rename = "personaId")]
    pub persona_id: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct GetAdaptationsRequest {
    #[serde(rename = "personaId")]
    pub persona_id: String,
    pub limit: Option<i32>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SuggestAdaptationsRequest {
    #[serde(rename = "userId")]
    pub user_id: String,
    #[serde(rename = "personaId")]
    pub persona_id: String,
    pub context: Option<String>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct RateAdaptationRequest {
    #[serde(rename = "userId")]
    pub user_id: String,
    #[serde(rename = "adaptationId")]
    pub adaptation_id: String,
