pub struct MimicManager {
    db: Arc<RwLock<Option<DatabaseManager>>>,
}

impl MimicManager {
    pub fn new(db: Arc<RwLock<Option<DatabaseManager>>>) -> Self {
        Self { db }
    }

    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // Create Mimic tables
        let queries = vec![
            // Personas table
            r#"
                CREATE TABLE IF NOT EXISTS mimic_personas (
                    persona_id TEXT PRIMARY KEY,
                    user_id TEXT NOT NULL,
                    name TEXT NOT NULL,
                    description TEXT NOT NULL,
                    traits TEXT NOT NULL, -- JSON
                    expertise_areas TEXT NOT NULL, -- JSON
                    active BOOLEAN DEFAULT FALSE,
                    created_at TEXT NOT NULL,
                    updated_at TEXT NOT NULL
                )
            "#,
            // Adaptations table
            r#"
                CREATE TABLE IF NOT EXISTS mimic_adaptations (
                    adaptation_id TEXT PRIMARY KEY,
                    persona_id TEXT NOT NULL,
                    context TEXT NOT NULL,
                    changes TEXT NOT NULL, -- JSON
                    effectiveness_score REAL DEFAULT 0.0,
                    created_at TEXT NOT NULL,
                    FOREIGN KEY (persona_id) REFERENCES mimic_personas (persona_id)
                )
            "#,
            // Active persona tracking
            r#"
                CREATE TABLE IF NOT EXISTS mimic_active_persona (
                    user_id TEXT PRIMARY KEY,
                    persona_id TEXT NOT NULL,
                    activated_at TEXT NOT NULL,
                    FOREIGN KEY (persona_id) REFERENCES mimic_personas (persona_id)
                )
            "#,
            // Performance records (from Python implementation)
            r#"
                CREATE TABLE IF NOT EXISTS mimic_performance_records (
                    record_id TEXT PRIMARY KEY,
                    persona_id TEXT NOT NULL,
                    session_id TEXT NOT NULL,
                    task TEXT NOT NULL,
                    score INTEGER NOT NULL,
                    user_feedback TEXT NOT NULL,
                    success BOOLEAN NOT NULL,
                    duration REAL NOT NULL,
                    created_at TEXT NOT NULL,
                    FOREIGN KEY (persona_id) REFERENCES mimic_personas (persona_id)
                )
            "#,
            // Growth statistics
            r#"
                CREATE TABLE IF NOT EXISTS mimic_growth_stats (
                    persona_id TEXT PRIMARY KEY,
                    sessions_completed INTEGER DEFAULT 0,
                    unique_tasks INTEGER DEFAULT 0,
                    repeat_tasks INTEGER DEFAULT 0,
                    usage_streak INTEGER DEFAULT 0,
                    total_usage_time REAL DEFAULT 0.0,
                    growth_rate REAL DEFAULT 0.0,
                    skill_decay_rate REAL DEFAULT 0.05,
                    last_used TEXT,
                    FOREIGN KEY (persona_id) REFERENCES mimic_personas (persona_id)
                )
            "#,
            // Trait audit log
            r#"
                CREATE TABLE IF NOT EXISTS mimic_trait_audit (
                    audit_id TEXT PRIMARY KEY,
                    persona_id TEXT NOT NULL,
                    trait_name TEXT NOT NULL,
                    old_value REAL NOT NULL,
                    new_value REAL NOT NULL,
                    reason TEXT NOT NULL,
                    created_at TEXT NOT NULL,
                    FOREIGN KEY (persona_id) REFERENCES mimic_personas (persona_id)
                )
            "#,
            // Role switching history
            r#"
                CREATE TABLE IF NOT EXISTS mimic_role_switches (
                    switch_id TEXT PRIMARY KEY,
                    persona_id TEXT NOT NULL,
                    user_id TEXT NOT NULL,
                    specialist_role TEXT NOT NULL,
                    context TEXT NOT NULL,
                    created_at TEXT NOT NULL,
                    FOREIGN KEY (persona_id) REFERENCES mimic_personas (persona_id)
                )
            "#,
        ];

        for query in queries {
            sqlx::query(query)
                .execute(&db.pool)
                .await
                .map_err(|e| format!("Failed to create Mimic table: {}", e))?;
        }

        Ok(())
    }
}

