/// Switch to active persona
#[tauri::command]
pub async fn mimic_switch_persona(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: SwitchPersonaRequest,
) -> Result<MimicPersona, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    // First deactivate any existing active persona
    let deactivate_query = r#"
        DELETE FROM mimic_active_persona WHERE user_id = ?
    "#;
    sqlx::query(deactivate_query)
        .bind(&request.user_id)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to deactivate existing persona: {}", e))?;

    // Activate new persona
    let activated_at = chrono::Utc::now().to_rfc3339();
    let activate_query = r#"
        INSERT INTO mimic_active_persona (user_id, persona_id, activated_at)
        VALUES (?, ?, ?)
    "#;
    sqlx::query(activate_query)
        .bind(&request.user_id)
        .bind(&request.persona_id)
        .bind(&activated_at)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to activate persona: {}", e))?;

    // Get the persona details
    let query = r#"
        SELECT persona_id, user_id, name, description, traits, expertise_areas, active, created_at
        FROM mimic_personas
        WHERE persona_id = ? AND user_id = ?
    "#;

    let row = sqlx::query_as::<_, MimicPersonaRow>(query)
        .bind(&request.persona_id)
        .bind(&request.user_id)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to fetch switched persona: {}", e))?;

    let traits: HashMap<String, f64> =
        serde_json::from_str(&row.traits).map_err(|e| format!("Failed to parse traits: {}", e))?;
    let expertise_areas: Vec<String> = serde_json::from_str(&row.expertise_areas)
        .map_err(|e| format!("Failed to parse expertise areas: {}", e))?;

    Ok(MimicPersona {
        persona_id: row.persona_id,
        user_id: row.user_id,
        name: row.name,
        description: row.description,
        traits,
        expertise_areas,
        active: row.active,
        created_at: row.created_at,
    })
}

