/// Record performance for a persona session
#[tauri::command]
pub async fn mimic_record_performance(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: RecordPerformanceRequest,
) -> Result<PerformanceRecord, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let record_id = format!("perf_{}", chrono::Utc::now().timestamp_millis());
    let created_at = chrono::Utc::now().to_rfc3339();

    // Insert performance record
    let query = r#"
        INSERT INTO mimic_performance_records (
            record_id, persona_id, session_id, task, score, user_feedback, success, duration, created_at
        ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)
    "#;

    sqlx::query(query)
        .bind(&record_id)
        .bind(&request.persona_id)
        .bind(&request.session_id)
        .bind(&request.task)
        .bind(request.score)
        .bind(&request.user_feedback)
        .bind(request.success)
        .bind(request.duration)
        .bind(&created_at)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to record performance: {}", e))?;

    // Update growth stats
    let stats_query = r#"
        INSERT INTO mimic_growth_stats (persona_id, sessions_completed, last_used)
        VALUES (?, 1, ?)
        ON CONFLICT(persona_id) DO UPDATE SET
            sessions_completed = sessions_completed + 1,
            total_usage_time = total_usage_time + ?,
            last_used = ?
    "#;

    sqlx::query(stats_query)
        .bind(&request.persona_id)
        .bind(&created_at)
        .bind(request.duration / 3600.0) // Convert to hours
        .bind(&created_at)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to update growth stats: {}", e))?;

    Ok(PerformanceRecord {
        record_id,
        persona_id: request.persona_id,
        session_id: request.session_id,
        task: request.task,
        score: request.score,
        user_feedback: request.user_feedback,
        success: request.success,
        duration: request.duration,
        created_at,
    })
}

/// Get performance analytics for a persona
#[tauri::command]
#[allow(dead_code)]
pub async fn mimic_get_analytics(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: GetAnalyticsRequest,
) -> Result<serde_json::Value, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    // Get performance records
    let records_query = r#"
        SELECT score, success FROM mimic_performance_records
        WHERE persona_id = ?
        ORDER BY created_at DESC
        LIMIT 100
    "#;

    let records: Vec<(i32, bool)> = sqlx::query_as(records_query)
        .bind(&request.persona_id)
        .fetch_all(&db.pool)
        .await
        .map_err(|e| format!("Failed to fetch performance records: {}", e))?;

    // Get growth stats
    let stats_query = r#"
        SELECT sessions_completed, unique_tasks, repeat_tasks, usage_streak,
               total_usage_time, growth_rate, last_used
        FROM mimic_growth_stats
        WHERE persona_id = ?
    "#;

    let stats: Option<(i32, i32, i32, i32, f64, f64, Option<String>)> = sqlx::query_as(stats_query)
        .bind(&request.persona_id)
        .fetch_optional(&db.pool)
        .await
        .map_err(|e| format!("Failed to fetch growth stats: {}", e))?;

    // Calculate analytics
    let (average_score, success_rate, performance_trend) = if !records.is_empty() {
        let total_score: i32 = records.iter().map(|(score, _)| score).sum();
        let avg = total_score as f64 / records.len() as f64;
        let successes = records.iter().filter(|(_, success)| *success).count();
        let rate = successes as f64 / records.len() as f64;
        let trend: Vec<i32> = records.iter().take(10).map(|(score, _)| *score).collect();
        (avg, rate, trend)
    } else {
        (0.0, 0.0, vec![])
    };

    // Determine performance tier
    let tier = if average_score >= 90.0 && success_rate >= 0.95 && records.len() >= 20 {
        "excellent"
    } else if average_score >= 80.0 && success_rate >= 0.9 && records.len() >= 10 {
        "stable"
    } else if average_score >= 70.0 && success_rate >= 0.8 && records.len() >= 5 {
        "beta"
    } else if average_score >= 60.0 && success_rate >= 0.7 {
        "risky"
    } else {
        "unstable"
    };

    let analytics = serde_json::json!({
        "persona_id": request.persona_id,
        "average_score": average_score,
        "success_rate": success_rate,
        "performance_trend": performance_trend,
        "performance_tier": tier,
        "total_sessions": records.len(),
        "growth_stats": stats.map(|(sessions, unique, repeat, streak, time, rate, last)| {
            serde_json::json!({
                "sessions_completed": sessions,
                "unique_tasks": unique,
                "repeat_tasks": repeat,
                "usage_streak": streak,
                "total_usage_time": time,
                "growth_rate": rate,
                "last_used": last
            })
        })
    });

    Ok(analytics)
}

/// Update a trait value with validation and audit logging
#[tauri::command]
pub async fn mimic_update_trait(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: UpdateTraitRequest,
) -> Result<serde_json::Value, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    // Validate trait value (0.0-100.0)
    if request.new_value < 0.0 || request.new_value > 100.0 {
        return Err(format!(
            "Trait value must be between 0.0 and 100.0, got: {}",
            request.new_value
        ));
    }

    // Get current persona
    let persona_query = r#"
        SELECT traits FROM mimic_personas WHERE persona_id = ?
    "#;

    let current_traits: String = sqlx::query_scalar(persona_query)
        .bind(&request.persona_id)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to fetch persona: {}", e))?;

    let mut traits: HashMap<String, f64> = serde_json::from_str(&current_traits)
        .map_err(|e| format!("Failed to parse traits: {}", e))?;

    let old_value = traits.get(&request.trait_name).copied().unwrap_or(50.0);

    // Update trait
    traits.insert(request.trait_name.clone(), request.new_value);

    let updated_traits = serde_json::to_string(&traits).map_err(|e| e.to_string())?;
    let updated_at = chrono::Utc::now().to_rfc3339();

    // Update persona
    let update_query = r#"
        UPDATE mimic_personas
        SET traits = ?, updated_at = ?
        WHERE persona_id = ?
    "#;

    sqlx::query(update_query)
        .bind(&updated_traits)
        .bind(&updated_at)
        .bind(&request.persona_id)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to update persona: {}", e))?;

    // Log audit event
    let audit_id = format!("audit_{}", chrono::Utc::now().timestamp_millis());
    let audit_query = r#"
        INSERT INTO mimic_trait_audit (
            audit_id, persona_id, trait_name, old_value, new_value, reason, created_at
        ) VALUES (?, ?, ?, ?, ?, ?, ?)
    "#;

    sqlx::query(audit_query)
        .bind(&audit_id)
        .bind(&request.persona_id)
        .bind(&request.trait_name)
        .bind(old_value)
        .bind(request.new_value)
        .bind(&request.reason)
        .bind(&updated_at)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to log audit: {}", e))?;

    Ok(serde_json::json!({
        "persona_id": request.persona_id,
        "trait_name": request.trait_name,
        "old_value": old_value,
        "new_value": request.new_value,
        "reason": request.reason,
        "updated_at": updated_at
    }))
}

/// Record a correction event for persona learning
#[tauri::command]
pub async fn mimic_add_correction(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: CorrectionEventRequest,
) -> Result<PerformanceRecord, String> {
    if request.event_type != "positive" && request.event_type != "negative" {
        return Err(format!(
            "Invalid event type: {}. Must be 'positive' or 'negative'",
            request.event_type
        ));
    }

    if request.impact_score < -1.0 || request.impact_score > 1.0 {
        return Err(format!(
            "Impact score must be between -1.0 and 1.0, got: {}",
            request.impact_score
        ));
    }

    // Create correction as a performance record
    let perf_request = RecordPerformanceRequest {
        persona_id: request.persona_id.clone(),
        session_id: format!("correction_{}", chrono::Utc::now().timestamp_millis()),
        task: format!("Correction: {}", request.description),
        score: if request.event_type == "positive" {
            75
        } else {
            25
        },
        user_feedback: request.description,
        success: request.event_type == "positive",
        duration: 0.0, // Instantaneous
    };

    mimic_record_performance(db, perf_request).await
}

