const extractAvatarId = (url: string): string | undefined => {
  try {
    const match = url.match(/\/([a-f0-9-]+)\.glb/i);
    return match?.[1];
  } catch {
    return undefined;
  }
};

interface PersonaSelectorProps {
  personas: typeof STUDIO_PERSONAS;
  selectedId: string;
  onSelect: (id: string) => void;
  avatarStore: ReturnType<typeof useAvatarStore>;
}

const PersonaSelector = memo<PersonaSelectorProps>(({ personas, selectedId, onSelect, avatarStore }) => (
  <div className="space-y-2">
    <h3 className="text-xs font-bold text-[#a855f7] uppercase tracking-widest mb-3">
      Select Persona
    </h3>
    <div className="space-y-1">
      {personas.map(persona => {
        const hasAvatar = avatarStore.hasCustomAvatar(persona.id);
        const isSelected = selectedId === persona.id;

        return (
          <button
            key={persona.id}
            onClick={() => onSelect(persona.id)}
            className={`w-full flex items-center gap-3 px-3 py-2.5 rounded-lg transition-all ${
              isSelected
                ? 'bg-[#a855f7]/20 border border-[#a855f7]/50'
                : 'bg-slate-800/50 border border-transparent hover:border-[#a855f7]/30 hover:bg-slate-800'
            }`}
          >
            <div className="relative">
              <AgentAvatar
                persona={persona as Persona}
                size="sm"
                showStatus={false}
                showBorder={isSelected}
                className={isSelected ? 'ring-2 ring-[#a855f7]' : ''}
              />
              {hasAvatar && (
                <div className="absolute -bottom-1 -right-1 bg-green-500 rounded-full p-0.5 border border-[#0a0f1d]">
                  <Check size={8} className="text-white" />
                </div>
              )}
            </div>
            
            <div className="flex-1 text-left">
              <div className="text-sm font-semibold text-white">{persona.name}</div>
              <div className="text-[10px] text-slate-400 uppercase tracking-wider">{persona.subtitle}</div>
            </div>
          </button>
        );
      })}
    </div>
  </div>
));

PersonaSelector.displayName = 'PersonaSelector';

interface AvatarPreviewProps {
  personaId: string;
  personaName: string;
  avatarUrl?: string;
  onRemove: () => void;
  onEdit: () => void;
}

const AvatarPreview = memo<AvatarPreviewProps>(({ personaId, personaName, avatarUrl, onRemove, onEdit }) => {
  console.log('[AvatarPreview] Rendering with:', { personaId, personaName, avatarUrl });

  return (
    <div className="bg-slate-900/50 rounded-xl p-6 border border-white/10">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-sm font-bold text-white uppercase tracking-wider">Current Avatar</h3>
        {avatarUrl && (
        <div className="flex gap-2">
          <button
            onClick={onEdit}
            className="p-2 rounded-lg bg-[#a855f7]/20 text-[#a855f7] hover:bg-[#a855f7]/30 transition-colors"
            title="Edit avatar"
          >
            <RefreshCw size={14} />
          </button>
          <button
            onClick={onRemove}
            className="p-2 rounded-lg bg-red-500/20 text-red-400 hover:bg-red-500/30 transition-colors"
            title="Remove avatar"
          >
            <Trash2 size={14} />
          </button>
        </div>
      )}
    </div>

    <div className="flex flex-col items-center">
      {avatarUrl ? (
        <div className="relative w-full aspect-[3/4] max-w-xs">
          {/* Portrait frame */}
          <div className="absolute inset-0 rounded-xl border-4 border-[#a855f7]/30 bg-gradient-to-br from-slate-900/50 to-slate-800/50 shadow-lg shadow-[#a855f7]/20" />
          {/* Avatar viewport - rectangular portrait */}
          <div className="absolute inset-2 rounded-lg overflow-hidden bg-slate-950">
            <Avatar3D
              avatarUrl={avatarUrl}
              animationState="idle"
              size="full"
              showBackground={true}
              className="w-full h-full"
            />
          </div>
        </div>
      ) : (
        <div className="w-full aspect-[3/4] max-w-xs rounded-xl bg-slate-800 border-4 border-dashed border-slate-600 flex flex-col items-center justify-center">
          <User size={48} className="text-slate-500 mb-2" />
          <span className="text-xs text-slate-500">No avatar set</span>
        </div>
      )}
      <p className="mt-4 text-sm text-slate-300">{personaName}</p>
      <p className="text-[10px] text-slate-500 uppercase tracking-wider">
        {avatarUrl ? 'Ready Player Me Avatar' : 'Click "Create Avatar" below'}
      </p>
    </div>
  </div>
  );
});

AvatarPreview.displayName = 'AvatarPreview';

export interface AvatarStudioProps {
  className?: string;
}

export const AvatarStudio = memo<AvatarStudioProps>(({ className = '' }) => {
  const [selectedPersonaId, setSelectedPersonaId] = useState<string>('alden');
  const [isCreating, setIsCreating] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const avatarStore = useAvatarStore();

  // Debug: Track when selectedPersonaId changes
  React.useEffect(() => {
    console.log('[AvatarStudio] selectedPersonaId changed to:', selectedPersonaId);
  }, [selectedPersonaId]);

  const selectedPersona = useMemo(
    () => STUDIO_PERSONAS.find(p => p.id === selectedPersonaId) || STUDIO_PERSONAS[0],
    [selectedPersonaId]
  );

  // Get current avatar config - re-compute when avatars or selectedPersonaId changes
  const currentAvatarConfig = useMemo(
    () => {
      const config = avatarStore.avatars[selectedPersonaId] || null;
      console.log('[AvatarStudio] Computing currentAvatarConfig:', { selectedPersonaId, config });
      return config;
    },
    [avatarStore.avatars, selectedPersonaId]
  );

  // Debug: Log when currentAvatarConfig changes
  React.useEffect(() => {
    console.log('[AvatarStudio] currentAvatarConfig changed:', {
      selectedPersonaId,
      currentAvatarConfig,
      allAvatars: avatarStore.avatars
    });
  }, [currentAvatarConfig, selectedPersonaId, avatarStore.avatars]);

  // Avatar creator configuration
  const creatorConfig: AvatarCreatorConfig = useMemo(() => ({
    clearCache: !currentAvatarConfig,
    bodyType: 'fullbody',
    quickStart: false,
    language: 'en',
  }), [currentAvatarConfig]);

  // Handle successful avatar export
  const handleAvatarExported = useCallback((event: AvatarExportedEvent) => {
    try {
      console.log('[AvatarStudio] Avatar exported event:', event);
      const baseUrl = event.data.url;
      console.log('[AvatarStudio] Base URL:', baseUrl);

      const avatarUrl = buildAvatarUrl(baseUrl);
      console.log('[AvatarStudio] Built avatar URL:', avatarUrl);

      const avatarId = extractAvatarId(baseUrl);
      console.log('[AvatarStudio] Extracted avatar ID:', avatarId);

      const config: PersonaAvatarConfig = {
        personaId: selectedPersonaId,
        avatarUrl,
        avatarId,
        thumbnailUrl: baseUrl.replace('.glb', '.png'),
        createdAt: currentAvatarConfig?.createdAt || new Date().toISOString(),
        updatedAt: new Date().toISOString(),
      };

      console.log('[AvatarStudio] Saving avatar config:', config);
      avatarStore.setAvatar(config);
      console.log('[AvatarStudio] Avatar saved, closing creator');
      setIsCreating(false);
    } catch (err) {
      console.error('[AvatarStudio] Failed to process avatar:', err);
    }
  }, [selectedPersonaId, currentAvatarConfig, avatarStore]);

  const handleRemoveAvatar = useCallback(() => {
    avatarStore.removeAvatar(selectedPersonaId);
  }, [selectedPersonaId, avatarStore]);

  const handleExport = useCallback(() => {
    const data = avatarStore.exportAvatars();
    const blob = new Blob([data], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `hearthlink-avatars-${new Date().toISOString().split('T')[0]}.json`;
    a.click();
    URL.revokeObjectURL(url);
  }, [avatarStore]);

  const handleImport = useCallback(() => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = '.json';
    input.onchange = (e) => {
      const file = (e.target as HTMLInputElement).files?.[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = (ev) => {
          const content = ev.target?.result as string;
          avatarStore.importAvatars(content);
        };
        reader.readAsText(file);
      }
    };
    input.click();
  }, [avatarStore]);

  // Auto-dismiss loading state
  React.useEffect(() => {
    if (isCreating) {
      const timer = setTimeout(() => setIsLoading(false), 2000);
      return () => clearTimeout(timer);
    } else {
      setIsLoading(true);
    }
  }, [isCreating]);

  return (
    <div className={`flex flex-col h-full relative ${className}`}>
      {isCreating ? (
        /* Avatar Creator View - Full Container */
        <div className="absolute inset-0 flex flex-col bg-[#0a0f1d] z-40">
          {/* Creator Header */}
          <div className="flex items-center justify-between px-4 py-3 border-b border-[#a855f7]/20 bg-[#06101f]/80 shrink-0">
            <div className="flex items-center gap-3">
              <div
                className="w-8 h-8 rounded-full flex items-center justify-center"
                style={{ backgroundColor: `${selectedPersona.color}20`, border: `1px solid ${selectedPersona.color}50` }}
              >
                <Sparkles size={14} style={{ color: selectedPersona.color }} />
              </div>
              <div>
                <span className="text-sm font-bold text-white">Creating Avatar for {selectedPersona.name}</span>
                <p className="text-[10px] text-slate-400 uppercase tracking-wider">Powered by Ready Player Me</p>
              </div>
            </div>
            <button
              onClick={() => setIsCreating(false)}
              className="px-4 py-1.5 text-xs font-medium rounded-lg bg-slate-800 text-slate-300 hover:bg-slate-700 transition-colors"
            >
              Cancel
            </button>
          </div>

          {/* Creator iFrame - Full Height */}
          <div className="flex-1 relative overflow-hidden">
            {isLoading && (
              <div className="absolute inset-0 z-10 flex flex-col items-center justify-center bg-[#0a0f1d]">
                <div className="w-10 h-10 border-2 border-[#a855f7] border-t-transparent rounded-full animate-spin mb-4" />
                <p className="text-slate-400 text-sm uppercase tracking-wider">Loading Avatar Creator...</p>
              </div>
            )}
            <AvatarCreator
              subdomain={RPM_SUBDOMAIN}
              config={creatorConfig}
              style={{
                width: '100%',
                height: '100%',
                border: 'none',
                display: isLoading ? 'none' : 'block',
              }}
              onAvatarExported={handleAvatarExported}
            />
          </div>

          {/* Footer */}
          <div className="px-4 py-2 border-t border-[#a855f7]/10 bg-[#06101f]/50 shrink-0">
            <p className="text-[10px] text-slate-500 text-center">
              Customize your avatar's appearance, then click "Next" to save ƒ?› Avatars are stored locally
            </p>
          </div>
        </div>
      ) : (
        /* Studio View - Professional Two-Column Layout */
        <div className="flex h-full gap-6 overflow-hidden">
          {/* Left Sidebar - Persona Selector & Tools */}
          <div className="w-80 flex flex-col gap-6 shrink-0 overflow-y-auto pr-2 scrollbar-thin scrollbar-thumb-purple-900/50">
            {/* Persona Selector */}
            <PersonaSelector
              personas={STUDIO_PERSONAS}
              selectedId={selectedPersonaId}
              onSelect={setSelectedPersonaId}
              avatarStore={avatarStore}
            />

            {/* Avatar Statistics */}
            <div className="bg-slate-900/50 backdrop-blur-md rounded-xl p-5 border border-white/10">
              <h3 className="text-xs font-bold text-[#a855f7] uppercase tracking-widest mb-4">
                Avatar Statistics
              </h3>
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <span className="text-xs text-slate-400 uppercase tracking-wider">Created</span>
                  <span className="text-lg font-bold text-[#a855f7]">
                    {STUDIO_PERSONAS.filter(p => avatarStore.hasCustomAvatar(p.id)).length}
                  </span>
                </div>
                <div className="w-full h-px bg-white/10" />
                <div className="flex items-center justify-between">
                  <span className="text-xs text-slate-400 uppercase tracking-wider">Remaining</span>
                  <span className="text-lg font-bold text-slate-400">
                    {STUDIO_PERSONAS.length - STUDIO_PERSONAS.filter(p => avatarStore.hasCustomAvatar(p.id)).length}
                  </span>
                </div>
                <div className="w-full h-px bg-white/10" />
                <div className="flex items-center justify-between">
                  <span className="text-xs text-slate-400 uppercase tracking-wider">Total</span>
                  <span className="text-lg font-bold text-white">
                    {STUDIO_PERSONAS.length}
                  </span>
                </div>
              </div>
            </div>

            {/* Import/Export Tools */}
            <div className="bg-slate-900/50 backdrop-blur-md rounded-xl p-5 border border-white/10">
              <h3 className="text-xs font-bold text-[#a855f7] uppercase tracking-widest mb-4">
                Avatar Tools
              </h3>
              <div className="space-y-2">
                <button
                  onClick={handleImport}
                  className="w-full px-4 py-2.5 rounded-lg bg-slate-800 text-slate-300 border border-slate-700 hover:border-[#a855f7]/50 hover:bg-slate-700 transition-all flex items-center justify-center gap-2 text-sm font-medium"
                >
                  <Upload size={14} />
                  Import Avatars
                </button>
                <button
                  onClick={handleExport}
                  className="w-full px-4 py-2.5 rounded-lg bg-slate-800 text-slate-300 border border-slate-700 hover:border-[#a855f7]/50 hover:bg-slate-700 transition-all flex items-center justify-center gap-2 text-sm font-medium"
                >
                  <Download size={14} />
                  Export Avatars
                </button>
              </div>
            </div>
          </div>

          {/* Right Content - Avatar Display & Controls */}
          <div className="flex-1 flex flex-col min-w-0 overflow-hidden">
            {/* Avatar Preview Section */}
            <div className="flex-1 flex items-center justify-center px-12 py-8">
              {currentAvatarConfig?.avatarUrl ? (
                <div className="flex flex-col items-center gap-4 w-full h-full py-4 max-h-full">
                  {/* Persona Info Header */}
                  <div className="text-center space-y-1 shrink-0 z-10">
                    <h2 className="text-4xl font-bold text-white drop-shadow-lg">{selectedPersona.name}</h2>
                    <p className="text-sm text-[#a855f7] uppercase tracking-[0.25em] font-bold">
                      {selectedPersona.subtitle}
                    </p>
                  </div>

                  {/* Responsive Container: Height-driven with Aspect Ratio */}
                  <div className="relative flex-1 min-h-0 aspect-[9/16] max-w-full flex flex-col items-center">
                    
                    {/* Avatar Display - Takes most space */}
                    <div className="relative flex-1 w-full min-h-0 isolate flex items-center justify-center mb-2">
                       {/* Studio frame background */}
                      <div className="absolute inset-0 rounded-[2rem] bg-gradient-to-b from-[#a855f7]/5 to-[#0a0f1d] border-4 border-[#a855f7]/20 z-0 shadow-[0_0_30px_rgba(168,85,247,0.1)]" />
                      
                      {/* Inner Bevel */}
                      <div className="absolute inset-1 rounded-[1.8rem] border border-white/5 z-0 pointer-events-none" />
                      
                      {/* Glow effect */}
                      <div className="absolute inset-0 bg-[radial-gradient(circle_at_center,_var(--tw-gradient-stops))] from-[#a855f7]/20 via-transparent to-transparent opacity-60 z-0" />

                      {/* Avatar viewport */}
                      <div className="relative w-full h-full z-10 p-4">
                        <Avatar3D
                          avatarUrl={currentAvatarConfig.avatarUrl}
                          animationState="idle"
                          size="full"
                          showBackground={false}
                          className="w-full h-full object-contain drop-shadow-2xl"
                        />
                      </div>
                    </div>

                    {/* Controls Section - Attached to bottom of effective aspect ratio area */}
                    <div className="w-full shrink-0 flex flex-col items-center gap-3 z-20">
                      
                      {/* Status Badge */}
                      <div className="px-5 py-1.5 rounded-full bg-green-500/10 border-2 border-green-500/50 shadow-[0_0_15px_rgba(34,197,94,0.1)]">
                        <span className="text-[10px] font-bold text-green-400 uppercase tracking-[0.15em] flex items-center gap-2">
                          <Check size={12} className="flex-shrink-0" />
                          Avatar Active
                        </span>
                      </div>

                      {/* Action Buttons */}
                      <div className="flex items-center justify-center gap-3 w-full mt-1">
                        <button
                          onClick={() => setIsCreating(true)}
                          className="flex-1 px-3 py-2 rounded-lg bg-[#a855f7]/10 text-[#a855f7] border-2 border-[#a855f7]/50 hover:bg-[#a855f7]/20 hover:border-[#a855f7] transition-all font-bold text-[10px] uppercase tracking-wider flex items-center justify-center gap-1.5 shadow-lg shadow-[#a855f7]/10"
                        >
                          <RefreshCw size={12} className="flex-shrink-0" />
                          <span>Edit</span>
                        </button>
                        <button
                          onClick={handleRemoveAvatar}
                          className="flex-1 px-3 py-2 rounded-lg bg-red-500/10 text-red-400 border-2 border-red-500/50 hover:bg-red-500/20 hover:border-red-500 transition-all font-bold text-[10px] uppercase tracking-wider flex items-center justify-center gap-1.5 shadow-lg"
                        >
                          <Trash2 size={12} className="flex-shrink-0" />
                          <span>Remove</span>
                        </button>
                      </div>
                    </div>

                  </div>

                  {/* Footer Info */}
                  <div className="text-center shrink-0 z-10">
                    <p className="text-[10px] text-slate-600 uppercase tracking-wider">
                      Ready Player Me 3D Avatar ƒ?› Last Updated {new Date(currentAvatarConfig.updatedAt).toLocaleDateString()}
                    </p>
                  </div>
                </div>
              ) : (
                <div className="flex flex-col items-center gap-8 w-full max-w-2xl">
                  {/* Persona Info Header */}
                  <div className="text-center space-y-2">
                    <h2 className="text-4xl font-bold text-white">{selectedPersona.name}</h2>
                    <p className="text-sm text-[#a855f7] uppercase tracking-[0.2em] font-semibold">
                      {selectedPersona.subtitle}
                    </p>
                  </div>

                  {/* Empty State */}
                  <div className="w-full max-w-lg aspect-[3/4] rounded-3xl bg-slate-900/50 backdrop-blur-sm border-[8px] border-dashed border-slate-700/50 flex flex-col items-center justify-center gap-8 p-12">
                    <div className="w-32 h-32 rounded-full bg-slate-800/50 flex items-center justify-center border-4 border-slate-700/50 shadow-inner">
                      <User size={64} className="text-slate-600" />
                    </div>
                    <div className="text-center space-y-3">
                      <p className="text-2xl font-bold text-slate-300">No Avatar Created</p>
                      <p className="text-sm text-slate-500 max-w-sm leading-relaxed">
                        Create a personalized 3D avatar to represent {selectedPersona.name} in the system
                      </p>
                    </div>
                  </div>

                  {/* Create Button */}
                  <button
                    onClick={() => setIsCreating(true)}
                    className="px-12 py-4 rounded-xl bg-[#a855f7]/20 text-[#a855f7] border-2 border-[#a855f7]/50 hover:bg-[#a855f7]/30 hover:border-[#a855f7] hover:scale-105 transition-all font-bold text-base uppercase tracking-wider flex items-center justify-center gap-3 shadow-xl shadow-[#a855f7]/20"
                  >
                    <Sparkles size={22} className="flex-shrink-0" />
                    <span>Create Avatar</span>
                  </button>

                  {/* Info */}
                  <div className="text-center">
                    <p className="text-xs text-slate-500 uppercase tracking-wider">
                      Powered by Ready Player Me ƒ?› Avatars stored locally
                    </p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
});

AvatarStudio.displayName = 'AvatarStudio';

export default AvatarStudio;

