/**
 * useChatState - Hook for managing unified chat state
 * Handles both group (Agent Bridge) and private channel messages
 */
import { useState, useCallback, useRef, useEffect } from 'react';
import { AldenBridge } from '../../../bridges/AldenBridge';
import type {
  ChatMessage,
  Persona,
  SystemMetrics,
  PersonaStatus,
  SystemPersonaId,
  SYSTEM_PERSONAS,
  MODULE_RESIDENT_AI,
} from './types';
import { SYSTEM_PERSONAS as SystemPersonasConfig } from './types';

interface UseChatStateOptions {
  userId: string;
  moduleId: string;
  residentPersonaId: SystemPersonaId;
  onError?: (error: string) => void;
}

interface ChatState {
  // Personas
  personas: Persona[];
  activePersonaId: string;

  // Group chat
  groupMessages: ChatMessage[];
  groupLoading: boolean;
  groupError: string | null;
  groupTypingPersonas: string[];

  // Private chat
  privateMessages: ChatMessage[];
  privateLoading: boolean;
  privateError: string | null;
  privateTyping: boolean;

  // System metrics
  metrics: SystemMetrics;

  // Actions
  setActivePersona: (personaId: string) => void;
  sendGroupMessage: (content: string) => Promise<void>;
  sendPrivateMessage: (content: string) => Promise<void>;
  refreshHistory: () => Promise<void>;
}

// Generate a unique message ID
const generateMessageId = (): string =>
  `msg-${Date.now()}-${Math.random().toString(36).slice(2, 9)}`;

// Create a user message
const createUserMessage = (content: string, channelType: 'private' | 'group'): ChatMessage => ({
  id: generateMessageId(),
  content,
  senderId: 'user',
  senderName: 'You',
  senderType: 'user',
  timestamp: new Date().toISOString(),
  channelType,
});

// Normalize API response to ChatMessage
const normalizeApiMessage = (data: any, channelType: 'private' | 'group'): ChatMessage => ({
  id: data.messageId || data.message_id || data.id || generateMessageId(),
  content: data.content || data.message || data.response || '',
  senderId: data.personaId || data.persona_id || data.agent_id || 'alden',
  senderName: data.senderName || data.sender_name || data.from_agent || 'Alden',
  senderType: 'persona',
  timestamp: data.createdAt || data.created_at || data.timestamp || new Date().toISOString(),
  channelType,
  personaId: data.personaId || data.persona_id || data.agent_id || 'alden',
});

export function useChatState(options: UseChatStateOptions): ChatState {
  const { userId, moduleId, residentPersonaId, onError } = options;

  // Bridge ref
  const bridgeRef = useRef<AldenBridge | null>(null);
  const isMountedRef = useRef(true);

  // Persona state
  const [personas, setPersonas] = useState<Persona[]>(() => {
    // Initialize with system personas
    return Object.values(SystemPersonasConfig).map(p => ({
      ...p,
      status: 'idle' as PersonaStatus,
    }));
  });
  const [activePersonaId, setActivePersonaId] = useState<string>(residentPersonaId);

  // Group chat state
  const [groupMessages, setGroupMessages] = useState<ChatMessage[]>([]);
  const [groupLoading, setGroupLoading] = useState(false);
  const [groupError, setGroupError] = useState<string | null>(null);
  const [groupTypingPersonas, setGroupTypingPersonas] = useState<string[]>([]);

  // Private chat state
  const [privateMessages, setPrivateMessages] = useState<ChatMessage[]>([]);
  const [privateLoading, setPrivateLoading] = useState(false);
  const [privateError, setPrivateError] = useState<string | null>(null);
  const [privateTyping, setPrivateTyping] = useState(false);

  // System metrics (simulated for now - will connect to actual metrics later)
  const [metrics, setMetrics] = useState<SystemMetrics>({
    cpu: 45,
    gpu: 60,
    memory: 65,
    network: 80,
    cognitiveLoad: 72,
  });

  // Initialize bridge
  useEffect(() => {
    isMountedRef.current = true;
    const bridge = new AldenBridge({ userId });
    bridgeRef.current = bridge;

    const init = async () => {
      try {
        await bridge.initialize();
        if (isMountedRef.current) {
          await loadHistory();
        }
      } catch (err) {
        if (isMountedRef.current) {
          const errorMsg = err instanceof Error ? err.message : 'Failed to initialize chat';
          setPrivateError(errorMsg);
          onError?.(errorMsg);
        }
      }
    };

    init();

    // Simulate metrics updates
    const metricsInterval = setInterval(() => {
      if (isMountedRef.current) {
        setMetrics(prev => ({
          cpu: Math.max(20, Math.min(95, prev.cpu + (Math.random() - 0.5) * 10)),
          gpu: Math.max(30, Math.min(90, prev.gpu + (Math.random() - 0.5) * 8)),
          memory: Math.max(40, Math.min(85, prev.memory + (Math.random() - 0.5) * 5)),
          network: Math.max(50, Math.min(95, prev.network + (Math.random() - 0.5) * 15)),
          cognitiveLoad: Math.max(30, Math.min(95, prev.cognitiveLoad + (Math.random() - 0.5) * 12)),
        }));
      }
    }, 3000);

    return () => {
      isMountedRef.current = false;
      clearInterval(metricsInterval);
      if (bridge) {
        try {
          bridge.destroy();
        } catch (e) {
          console.warn('Failed to destroy bridge:', e);
        }
      }
      bridgeRef.current = null;
    };
  }, [userId]);

  // Load chat history
  const loadHistory = useCallback(async () => {
    if (!bridgeRef.current) return;

    setPrivateLoading(true);
    setPrivateError(null);

    try {
      const response = await bridgeRef.current.getChatHistory(userId);
      if (response.success && isMountedRef.current) {
        // Defensive: Ensure response.data is an array before mapping
        const rawData = response.data;
        const safeData = Array.isArray(rawData) ? rawData : [];

        if (!Array.isArray(rawData)) {
          console.warn('[useChatState] Expected array for chat history but got:', typeof rawData, rawData);
        }

        const normalized = safeData.map((entry: any) =>
          normalizeApiMessage(entry, 'private')
        );
        setPrivateMessages(normalized);
      } else if (!response.success && isMountedRef.current) {
        setPrivateError(response.error || 'Failed to load history');
      }
    } catch (err) {
      if (isMountedRef.current) {
        const errorMsg = err instanceof Error ? err.message : 'Failed to load chat history';
        setPrivateError(errorMsg);
      }
    } finally {
      if (isMountedRef.current) {
        setPrivateLoading(false);
      }
    }
  }, [userId]);

  // Set active persona
  const setActivePersona = useCallback((personaId: string) => {
    setActivePersonaId(personaId);
    // Update persona status to show it's active
    setPersonas(prev => prev.map(p => ({
      ...p,
      status: p.id === personaId ? 'thinking' : p.status === 'thinking' ? 'idle' : p.status,
    })));

    // Reset status after delay
    setTimeout(() => {
      if (isMountedRef.current) {
        setPersonas(prev => prev.map(p => ({
          ...p,
          status: p.id === personaId ? 'idle' : p.status,
        })));
      }
    }, 2000);
  }, []);

  // Send group message
  const sendGroupMessage = useCallback(async (content: string) => {
    if (!bridgeRef.current || !content.trim()) return;

    const userMessage = createUserMessage(content, 'group');
    setGroupMessages(prev => [...prev, userMessage]);
    setGroupTypingPersonas([activePersonaId]);

    // Update persona status
    setPersonas(prev => prev.map(p => ({
      ...p,
      status: p.id === activePersonaId ? 'thinking' : p.status,
    })));

    try {
      const response = await bridgeRef.current.sendChatMessage(
        `[Group:${activePersonaId}] ${content}`
      );

      if (response.success && response.data && isMountedRef.current) {
        // Defensive: Validate response.data before normalizing
        if (typeof response.data === 'object' && response.data !== null) {
          const aiMessage = normalizeApiMessage(response.data, 'group');
          setGroupMessages(prev => [...prev, aiMessage]);

          // Update persona status to speaking then idle
          setPersonas(prev => prev.map(p => ({
            ...p,
            status: p.id === activePersonaId ? 'speaking' : p.status,
          })));

          setTimeout(() => {
            if (isMountedRef.current) {
              setPersonas(prev => prev.map(p => ({
                ...p,
                status: p.id === activePersonaId ? 'idle' : p.status,
              })));
            }
          }, 3000);
        } else {
          console.warn('[useChatState] Expected object for group message response but got:', typeof response.data, response.data);
          setGroupError('Invalid response format from server');
        }
      } else if (!response.success && isMountedRef.current) {
        setGroupError(response.error || 'Failed to send message');
      }
    } catch (err) {
      if (isMountedRef.current) {
        const errorMsg = err instanceof Error ? err.message : 'Failed to send message';
        setGroupError(errorMsg);
        onError?.(errorMsg);
      }
    } finally {
      if (isMountedRef.current) {
        setGroupTypingPersonas([]);
      }
    }
  }, [activePersonaId, onError]);

  // Send private message
  const sendPrivateMessage = useCallback(async (content: string) => {
    if (!bridgeRef.current || !content.trim()) return;

    const userMessage = createUserMessage(content, 'private');
    setPrivateMessages(prev => [...prev, userMessage]);
    setPrivateTyping(true);
    setPrivateError(null);

    // Update resident persona status
    setPersonas(prev => prev.map(p => ({
      ...p,
      status: p.id === residentPersonaId ? 'thinking' : p.status,
    })));

    try {
      const response = await bridgeRef.current.sendChatMessage(content);

      if (response.success && response.data && isMountedRef.current) {
        // Defensive: Validate response.data before normalizing
        if (typeof response.data === 'object' && response.data !== null) {
          const aiMessage = normalizeApiMessage(response.data, 'private');
          setPrivateMessages(prev => [...prev, aiMessage]);

          // Update persona status
          setPersonas(prev => prev.map(p => ({
            ...p,
            status: p.id === residentPersonaId ? 'speaking' : p.status,
          })));

          setTimeout(() => {
            if (isMountedRef.current) {
              setPersonas(prev => prev.map(p => ({
                ...p,
                status: p.id === residentPersonaId ? 'idle' : p.status,
              })));
            }
          }, 3000);
        } else {
          console.warn('[useChatState] Expected object for private message response but got:', typeof response.data, response.data);
          setPrivateError('Invalid response format from server');
        }
      } else if (!response.success && isMountedRef.current) {
        setPrivateError(response.error || 'Failed to send message');
      }
    } catch (err) {
      if (isMountedRef.current) {
        const errorMsg = err instanceof Error ? err.message : 'Failed to send message';
        setPrivateError(errorMsg);
        onError?.(errorMsg);
      }
    } finally {
      if (isMountedRef.current) {
        setPrivateTyping(false);
      }
    }
  }, [residentPersonaId, onError]);

  return {
    personas,
    activePersonaId,
    groupMessages,
    groupLoading,
    groupError,
    groupTypingPersonas,
    privateMessages,
    privateLoading,
    privateError,
    privateTyping,
    metrics,
    setActivePersona,
    sendGroupMessage,
    sendPrivateMessage,
    refreshHistory: loadHistory,
  };
}

export default useChatState;

