/**
 * Unified Chat System Types
 * Shared type definitions for the multi-agent chat architecture
 */

// Persona status for avatar animations
export type PersonaStatus = 'idle' | 'thinking' | 'speaking' | 'offline';

// System personas - the core AI agents
export type SystemPersonaId = 'alden' | 'alice' | 'sentry' | 'mimic';

// Module to resident AI mapping
export const MODULE_RESIDENT_AI: Record<string, SystemPersonaId> = {
  alden: 'alden',
  alice: 'alice',
  mimic: 'mimic',
  sentry: 'sentry',
  vault: 'sentry',    // Vault guardian
  synapse: 'sentry',  // System integration guardian
  core: 'alden',      // Core projects managed by Alden
} as const;

// Persona definition
export interface Persona {
  id: string;
  name: string;
  subtitle: string;
  avatar: string;
  status: PersonaStatus;
  isSystem: boolean;
  // For Mimic personas
  mimicPersonaId?: string;
}

// System persona definitions
export const SYSTEM_PERSONAS: Record<SystemPersonaId, Omit<Persona, 'status'>> = {
  alden: {
    id: 'alden',
    name: 'Alden',
    subtitle: 'ADHD Support Core',
    avatar: '/assets/Alden.png',
    isSystem: true,
  },
  alice: {
    id: 'alice',
    name: 'Alice',
    subtitle: 'Behavioral Analysis',
    avatar: '/assets/Alice.png',
    isSystem: true,
  },
  sentry: {
    id: 'sentry',
    name: 'Sentry',
    subtitle: 'System Guardian',
    avatar: '/assets/Sentry.png',
    isSystem: true,
  },
  mimic: {
    id: 'mimic',
    name: 'Mimic',
    subtitle: 'Adaptive Persona',
    avatar: '/assets/Mimic.png',
    isSystem: true,
  },
};

// Chat message structure
export interface ChatMessage {
  id: string;
  content: string;
  senderId: string;
  senderName: string;
  senderType: 'user' | 'persona' | 'system';
  timestamp: string;
  channelType: 'private' | 'group';
  // For group chat, which persona responded
  personaId?: string;
  // Message state
  status?: 'sending' | 'sent' | 'error';
  // Metadata
  metadata?: Record<string, unknown>;
}

// System metrics for Active Agent panel
export interface SystemMetrics {
  cpu: number;
  gpu: number;
  memory: number;
  network: number;
  cognitiveLoad: number;
}

// Channel state for chat windows
export interface ChannelState {
  messages: ChatMessage[];
  isLoading: boolean;
  error: string | null;
  typingPersonas: string[];
}

// Props for the unified chat layout
export interface UnifiedChatLayoutProps {
  // Module context
  moduleId: string;
  userId: string;

  // Persona configuration
  residentPersonaId: SystemPersonaId;
  availablePersonas: Persona[];
  activePersonaId: string;
  onPersonaSelect: (personaId: string) => void;

  // Chat state
  privateChannel: ChannelState;
  groupChannel: ChannelState;
  onSendPrivateMessage: (content: string) => void;
  onSendGroupMessage: (content: string) => void;

  // System metrics
  metrics: SystemMetrics;

  // Layout options
  showGroupChat?: boolean;
  defaultCollapsed?: boolean;

  // Navigation
  onClose?: () => void;
}

// Avatar animation states
export interface AvatarAnimationState {
  isAnimating: boolean;
  animationType: 'idle' | 'thinking' | 'speaking' | 'wave';
  intensity: number; // 0-1 for animation intensity
}

// Props for avatar component
export interface AgentAvatarProps {
  persona: Persona;
  size: 'sm' | 'md' | 'lg' | 'xl';
  showStatus?: boolean;
  showBorder?: boolean;
  animationState?: AvatarAnimationState;
  onClick?: () => void;
  className?: string;
}

