  className?: string;
}

const modules: ModuleConfig[] = [
  { id: 'alden', label: 'Alden', icon: '/assets/Alden.png', color: 'alden' },
  { id: 'alice', label: 'Alice', icon: '/assets/Alice.png', color: 'alice' },
  { id: 'mimic', label: 'Mimic', icon: '/assets/Mimic.png', color: 'mimic' },
  { id: 'vault', label: 'Vault', icon: '/assets/Vault.png', color: 'vault' },
  { id: 'core', label: 'Core', icon: '/assets/Core.png', color: 'core' },
  { id: 'sentry', label: 'Sentry', icon: '/assets/Sentry.png', color: 'sentry' },
  { id: 'synapse', label: 'Synapse', icon: '/assets/Synapse.png', color: 'synapse' },
];

/**
 * ModuleNavigator - Top-left module switcher for Persona Interface mode
 *
 * Provides quick navigation between all 7 modules in immersive persona view.
 * Each module has a unique color accent for visual differentiation.
 *
 * @example
 * <ModuleNavigator
 *   activeModule="alden"
 *   onModuleChange={(module) => setActiveModule(module)}
 * />
 */
export const ModuleNavigator: React.FC<ModuleNavigatorProps> = ({
  activeModule,
  onModuleChange,
  className = '',
}) => {
  return (
    <nav
      className={`fixed top-6 left-6 z-50 flex gap-2 flex-wrap max-w-md ${className}`}
      aria-label="Module navigation"
    >
      {modules.map((module) => {
        const isActive = activeModule === module.id;

        return (
          <button
            key={module.id}
            onClick={() => onModuleChange(module.id)}
            style={{
              color: `var(--${module.color}-accent)`,
              backgroundColor: isActive ? 'color-mix(in oklch, var(--background) 90%, var(--foreground))' : 'color-mix(in oklch, var(--background) 60%, transparent)',
              borderColor: isActive ? `var(--${module.color}-accent)` : 'color-mix(in oklch, var(--border) 50%, transparent)',
            }}
            className={`
              group relative px-3 py-2 rounded-md font-medium text-sm
              transition-all duration-200 border
              ${isActive
                ? 'shadow-lg'
                : 'hover:bg-muted/50'
              }
            `}
            aria-label={`Switch to ${module.label} module`}
            aria-current={isActive ? 'page' : undefined}
          >
            <span className="flex items-center gap-2">
              <img
                src={module.icon}
                alt={`${module.label} icon`}
                className="w-4 h-4 object-contain"
              />
              <span className="hidden sm:inline">{module.label}</span>
            </span>

            {/* Active indicator */}
            {isActive && (
              <span
                className="absolute -bottom-1 left-1/2 transform -translate-x-1/2 w-4 h-0.5 rounded-full"
                style={{ backgroundColor: `var(--${module.color}-accent)` }}
                aria-hidden="true"
              />
            )}
          </button>
        );
      })}
    </nav>
  );
};

