import { useState } from 'react';
import { OnboardingConfig } from '@/components/OnboardingWizard';

const ONBOARDING_KEY = 'hearthlink_onboarding_completed';
const ONBOARDING_CONFIG_KEY = 'hearthlink_onboarding_config';

export interface OnboardingState {
  isComplete: boolean;
  config: OnboardingConfig | null;
  showOnboarding: boolean;
  isLoading: boolean;
}

// Initialize state synchronously from localStorage to prevent flash of wrong content
const getInitialState = (): OnboardingState => {
  const completed = localStorage.getItem(ONBOARDING_KEY);
  const savedConfig = localStorage.getItem(ONBOARDING_CONFIG_KEY);

  if (completed === 'true' && savedConfig) {
    try {
      const config = JSON.parse(savedConfig);
      return {
        isComplete: true,
        config,
        showOnboarding: false,
        isLoading: false,
      };
    } catch (error) {
      console.error('Failed to parse onboarding config:', error);
      return {
        isComplete: false,
        config: null,
        showOnboarding: true,
        isLoading: false,
      };
    }
  }

  // First time user - show onboarding
  return {
    isComplete: false,
    config: null,
    showOnboarding: true,
    isLoading: false,
  };
};

export function useOnboarding() {
  const [state, setState] = useState<OnboardingState>(getInitialState());

  // No longer need useEffect for initialization - we initialize synchronously
  // This prevents the flash of wrong content on first render

  const completeOnboarding = (config: OnboardingConfig) => {
    localStorage.setItem(ONBOARDING_KEY, 'true');
    localStorage.setItem(ONBOARDING_CONFIG_KEY, JSON.stringify(config));
    setState({
      isComplete: true,
      config,
      showOnboarding: false,
      isLoading: false,
    });

    // Apply theme preference
    applyTheme(config.theme as 'system' | 'adaptive' | 'light' | 'dark');

    // Apply other preferences (could trigger events, etc.)
    if (config.enableNotifications) {
      requestNotificationPermission();
    }
  };

  const skipOnboarding = () => {
    const defaultConfig: OnboardingConfig = {
      firstName: 'User',
      lastName: '',
      nickname: 'Operator',
      bio: '',
      familyContext: [],
      theme: 'system',
      enableVoice: false,
      enableSound: false,
      enableEmail: false,
      enableNotifications: false,
      modelPriority: 'balanced',
      personaSettings: {},
      primaryPersona: 'alden',
      analyticsConsent: false,
      privacy: { localOnly: true, allowTelemetry: false },
      completedAt: new Date(),
    };

    localStorage.setItem(ONBOARDING_KEY, 'true');
    localStorage.setItem(ONBOARDING_CONFIG_KEY, JSON.stringify(defaultConfig));
    setState({
      isComplete: true,
      config: defaultConfig,
      showOnboarding: false,
      isLoading: false,
    });
  };

  const resetOnboarding = () => {
    localStorage.removeItem(ONBOARDING_KEY);
    localStorage.removeItem(ONBOARDING_CONFIG_KEY);
    setState({
      isComplete: false,
      config: null,
      showOnboarding: true,
      isLoading: false,
    });
  };

  const updateConfig = (updates: Partial<OnboardingConfig>) => {
    if (!state.config) return;

    const updatedConfig = { ...state.config, ...updates };
    localStorage.setItem(ONBOARDING_CONFIG_KEY, JSON.stringify(updatedConfig));
    setState((prev) => ({
      ...prev,
      config: updatedConfig,
    }));

    // Apply theme if it changed
    if (updates.theme) {
      applyTheme(updates.theme as 'system' | 'adaptive' | 'light' | 'dark');
    }
  };

  return {
    ...state,
    completeOnboarding,
    skipOnboarding,
    resetOnboarding,
    updateConfig,
  };
}

// Helper function to apply theme
function applyTheme(theme: 'system' | 'adaptive' | 'light' | 'dark') {
  const root = document.documentElement;

  if (theme === 'system' || theme === 'adaptive') {
    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
    root.classList.toggle('dark', prefersDark);
  } else {
    root.classList.toggle('dark', theme === 'dark');
  }
}

// Helper function to request notification permission
async function requestNotificationPermission() {
  if ('Notification' in window && Notification.permission === 'default') {
    try {
      await Notification.requestPermission();
    } catch (error) {
      console.error('Failed to request notification permission:', error);
    }
  }
}

