  <div className={`space-y-1 ${className}`}>
    {label && <label className={`text-xs font-mono tracking-wide ${hasError ? 'text-red-400 font-bold' : 'text-cyan-400'}`}>{label}</label>}
    <input
      type="text"
      value={value}
      onChange={onChange}
      className={`w-full bg-white/10 border rounded shadow-[inset_0_2px_4px_rgba(0,0,0,0.5)] focus:bg-black/50 outline-none text-sm px-4 py-3 text-white placeholder-white/20 transition-all ${
