+import { TutorialEventId } from '@/types/tutorial';
+
+const EVENT_NAME = 'hl:tutorial';
+
+export function emitTutorialEvent(id: TutorialEventId) {
+  if (typeof window === 'undefined') return;
+  window.dispatchEvent(new CustomEvent(EVENT_NAME, { detail: { id } }));
+}
+
+export function subscribeTutorialEvents(
+  handler: (eventId: TutorialEventId) => void
+): () => void {
+  if (typeof window === 'undefined') return () => {};
+
+  const listener = (event: Event) => {
+    const detail = (event as CustomEvent<{ id?: TutorialEventId }>).detail;
+    if (detail?.id) {
+      handler(detail.id);
+    }
+  };
+
+  window.addEventListener(EVENT_NAME, listener);
+  return () => window.removeEventListener(EVENT_NAME, listener);
+}
