// FILE: assets-workspace/integration/src/components/SpriteAnimator.tsx
import React, { useEffect, useRef, useState, useCallback } from 'react';
import type { SpriteDefinition } from '../constants/assetMapping';
import { useReducedMotion } from '../hooks/useReducedMotion';

export interface SpriteAnimatorProps {
  sprite: SpriteDefinition;
  state?: 'idle' | 'happy' | 'sad' | 'eating' | 'playing';
  className?: string;
  alt?: string;
  size?: number;
  fps?: number;
  loop?: boolean;
  onAnimationEnd?: () => void;
  priority?: 'high' | 'low';
}

export function SpriteAnimator({
  sprite,
  state = 'idle',
  className = '',
  alt = 'Sprite',
  size,
  fps = 12,
  loop = true,
  onAnimationEnd,
  priority = 'low'
}: SpriteAnimatorProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [imageLoaded, setImageLoaded] = useState(false);
  const [currentFrame, setCurrentFrame] = useState(0);
  const imageRef = useRef<HTMLImageElement | null>(null);
  const animationFrameRef = useRef<number>();
  const lastFrameTimeRef = useRef<number>(0);
  const prefersReducedMotion = useReducedMotion();

  // Determine sprite state
  const spriteState = sprite.states?.[state] || sprite.states?.idle || { row: 0, frames: 1 };
  const totalFrames = spriteState.frames;
  const frameDuration = 1000 / fps;

  // Load image
  useEffect(() => {
    const img = new Image();
    img.src = sprite.path;

    // Priority loading
    if (priority === 'high') {
      img.loading = 'eager';
      img.fetchPriority = 'high';
    }

    img.onload = () => {
      imageRef.current = img;
      setImageLoaded(true);
    };

    img.onerror = () => {
      console.error(\`Failed to load sprite: \${sprite.path}\`);
    };

    return () => {
      img.onload = null;
      img.onerror = null;
    };
  }, [sprite.path, priority]);

  // Animation loop
  const animate = useCallback((timestamp: number) => {
    if (!canvasRef.current || !imageRef.current || prefersReducedMotion) {
      return;
    }

    if (timestamp - lastFrameTimeRef.current < frameDuration) {
      animationFrameRef.current = requestAnimationFrame(animate);
      return;
    }

    lastFrameTimeRef.current = timestamp;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Calculate source position in sprite sheet
    const frameWidth = sprite.width / (sprite.frames || 1);
    const frameHeight = sprite.height / (Object.keys(sprite.states || {}).length || 1);
    const srcX = currentFrame * frameWidth;
    const srcY = spriteState.row * frameHeight;

    // Draw current frame
    ctx.drawImage(
      imageRef.current,
      srcX,
      srcY,
      frameWidth,
      frameHeight,
      0,
      0,
      canvas.width,
      canvas.height
    );

    // Update frame
    setCurrentFrame((prev) => {
      const nextFrame = prev + 1;
      if (nextFrame >= totalFrames) {
        if (loop) {
          return 0;
        } else {
          onAnimationEnd?.();
          return prev;
        }
      }
      return nextFrame;
    });

    animationFrameRef.current = requestAnimationFrame(animate);
  }, [sprite, spriteState, currentFrame, totalFrames, frameDuration, loop, onAnimationEnd, prefersReducedMotion]);

  // Start/stop animation
  useEffect(() => {
    if (imageLoaded && !prefersReducedMotion) {
      animationFrameRef.current = requestAnimationFrame(animate);
    }

    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [imageLoaded, animate, prefersReducedMotion]);

  // Reset frame when state changes
  useEffect(() => {
    setCurrentFrame(0);
  }, [state]);

  // Fallback for reduced motion: show static image
  if (prefersReducedMotion && imageRef.current) {
    return (
      <img
        src={sprite.path}
        alt={alt}
        className={className}
        style={{
          width: size || sprite.width,
          height: size || sprite.height,
          objectFit: 'contain'
        }}
        loading={priority === 'high' ? 'eager' : 'lazy'}
      />
    );
  }

  return (
    <canvas
      ref={canvasRef}
      className={className}
      width={size || sprite.width}
      height={size || sprite.height}
      style={{
        width: size || sprite.width,
        height: size || sprite.height,
        imageRendering: 'pixelated'
      }}
      aria-label={alt}
    />
  );
}
