// FILE: assets-workspace/integration/src/utils/assetPreloader.ts
import type { SpriteDefinition } from '../constants/assetMapping';
import { PRELOAD_ASSETS } from '../constants/assetMapping';

export class AssetPreloader {
  private loadedAssets: Map<string, HTMLImageElement> = new Map();
  private loadingPromises: Map<string, Promise<HTMLImageElement>> = new Map();

  async preload(assets: SpriteDefinition[]): Promise<void> {
    const promises = assets.map(asset => this.loadImage(asset.path));
    await Promise.all(promises);
  }

  async loadImage(path: string): Promise<HTMLImageElement> {
    // Return cached if available
    if (this.loadedAssets.has(path)) {
      return this.loadedAssets.get(path)!;
    }

    // Return existing promise if already loading
    if (this.loadingPromises.has(path)) {
      return this.loadingPromises.get(path)!;
    }

    // Create new load promise
    const promise = new Promise<HTMLImageElement>((resolve, reject) => {
      const img = new Image();
      img.src = path;

      img.onload = () => {
        this.loadedAssets.set(path, img);
        this.loadingPromises.delete(path);
        resolve(img);
      };

      img.onerror = () => {
        this.loadingPromises.delete(path);
        reject(new Error(\`Failed to load image: \${path}\`));
      };
    });

    this.loadingPromises.set(path, promise);
    return promise;
  }

  getImage(path: string): HTMLImageElement | null {
    return this.loadedAssets.get(path) || null;
  }

  isLoaded(path: string): boolean {
    return this.loadedAssets.has(path);
  }

  clearCache(): void {
    this.loadedAssets.clear();
    this.loadingPromises.clear();
  }
}

// Global preloader instance
export const assetPreloader = new AssetPreloader();

// Preload critical assets on app start
export async function preloadCriticalAssets(): Promise<void> {
  await assetPreloader.preload(PRELOAD_ASSETS);
}
