import { useEffect, useRef } from "react";
import { GameLoop } from "@/game/GameLoop";
import { SceneManager } from "@/game/SceneManager";
import { InputManager } from "@/game/InputManager";

type GameCanvasProps = {
  className?: string;
};

const GameCanvas = ({ className }: GameCanvasProps) => {
  const canvasRef = useRef<HTMLCanvasElement | null>(null);
  const gameLoopRef = useRef<GameLoop | null>(null);
  const sceneManagerRef = useRef<SceneManager | null>(null);
  const inputManagerRef = useRef<InputManager | null>(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas || gameLoopRef.current) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;

    const resizeCanvas = () => {
      const rect = canvas.getBoundingClientRect();
      const dpr = window.devicePixelRatio || 1;
      canvas.width = Math.max(1, Math.floor(rect.width * dpr));
      canvas.height = Math.max(1, Math.floor(rect.height * dpr));
      ctx.setTransform(dpr, 0, 0, dpr, 0, 0);
    };

    resizeCanvas();
    window.addEventListener("resize", resizeCanvas);

    const sceneManager = new SceneManager();
    const inputManager = new InputManager(canvas);
    const gameLoop = new GameLoop(
      (dt) => sceneManager.update(dt),
      () => sceneManager.render(ctx)
    );

    sceneManagerRef.current = sceneManager;
    inputManagerRef.current = inputManager;
    gameLoopRef.current = gameLoop;
    gameLoop.start();

    return () => {
      window.removeEventListener("resize", resizeCanvas);
      gameLoop.stop();
      inputManagerRef.current?.dispose?.();
      inputManagerRef.current?.destroy?.();
      inputManagerRef.current = null;
      gameLoopRef.current = null;
      sceneManagerRef.current = null;
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      className={["block h-full w-full", className].filter(Boolean).join(" ")}
    />
  );
};

export default GameCanvas;
