fn validate_stop_status(status: &str) -> Result<(), String> {
    match status {
        "completed" | "aborted" => Ok(()),
        _ => Err("Invalid Deep Work status. Must be 'completed' or 'aborted'".to_string()),
    }
}

const DEFAULT_BLOCKED_ITEMS: &[&str] = &[
    "system_notifications",
    "social_media_sites",
    "non_work_tabs",
];

#[derive(Debug, FromRow)]
struct DistractionBlockRow {
    user_id: String,
    enabled: bool,
    blocked_items: String,
    applied_at: String,
    note: String,
}

async fn persist_distraction_block_state(
    db: &DatabaseManager,
    user_id: &str,
    enable: bool,
) -> Result<DistractionBlockState, String> {
    let applied_at = chrono::Utc::now().to_rfc3339();
    let blocked_items: Vec<String> = DEFAULT_BLOCKED_ITEMS
        .iter()
        .map(|s| s.to_string())
        .collect();
    let blocked_json = serde_json::to_string(&blocked_items)
        .map_err(|e| format!("Failed to encode blocks: {}", e))?;
    let note = if enable {
        "Focus mode enabled - suppressing noisy surfaces"
    } else {
        "Focus mode disabled"
    };

    sqlx::query(
        r#"
        INSERT INTO alice_distraction_blocks (user_id, enabled, blocked_items, applied_at, note)
        VALUES (?, ?, ?, ?, ?)
        ON CONFLICT(user_id) DO UPDATE SET
            enabled = excluded.enabled,
            blocked_items = excluded.blocked_items,
            applied_at = excluded.applied_at,
            note = excluded.note
    "#,
    )
    .bind(user_id)
    .bind(enable)
    .bind(&blocked_json)
    .bind(&applied_at)
    .bind(note)
    .execute(&db.write_pool)
    .await
    .map_err(|e| format!("Failed to persist distraction block state: {}", e))?;

    Ok(DistractionBlockState {
        enabled: enable,
        blocked_items,
        applied_at,
        note: note.to_string(),
    })
}

async fn get_distraction_block_state(
    db: &DatabaseManager,
    user_id: &str,
) -> Result<DistractionBlockState, String> {
    let row = sqlx::query_as::<_, DistractionBlockRow>(
        r#"
        SELECT user_id, enabled, blocked_items, applied_at, note
        FROM alice_distraction_blocks
        WHERE user_id = ?
        LIMIT 1
    "#,
    )
    .bind(user_id)
    .fetch_optional(&db.read_pool)
    .await
    .map_err(|e| format!("Failed to read distraction block state: {}", e))?;

    if let Some(row) = row {
        let blocked_items: Vec<String> =
            serde_json::from_str(&row.blocked_items).unwrap_or_else(|_| Vec::new());
        Ok(DistractionBlockState {
            enabled: row.enabled,
            blocked_items,
            applied_at: row.applied_at,
            note: row.note,
        })
    } else {
        Ok(DistractionBlockState {
            enabled: false,
            blocked_items: Vec::new(),
            applied_at: chrono::Utc::now().to_rfc3339(),
            note: "Distraction blocking inactive".to_string(),
        })
    }
}

async fn alice_start_deep_work_internal(
    db: &DatabaseManager,
    request: &StartDeepWorkRequest,
) -> Result<AliceDeepWorkSession, String> {
    // Check if there's already an active session
    let check_query = r#"
        SELECT session_id FROM alice_deep_work_sessions
        WHERE user_id = ? AND status = 'active'
        LIMIT 1
    "#;

    let active_exists = sqlx::query(check_query)
        .bind(&request.user_id)
        .fetch_optional(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to check active sessions: {}", e))?;

    if active_exists.is_some() {
        return Err("An active Deep Work session already exists".to_string());
    }

    let session_id = format!("deepwork_{}", chrono::Utc::now().timestamp_millis());
    let created_at = chrono::Utc::now().to_rfc3339();
    let start_time = created_at.clone();

    let query = r#"
        INSERT INTO alice_deep_work_sessions (
            session_id, user_id, start_time, goal, status, created_at
        ) VALUES (?, ?, ?, ?, ?, ?)
    "#;

    sqlx::query(query)
        .bind(&session_id)
        .bind(&request.user_id)
        .bind(&start_time)
        .bind(&request.goal)
        .bind("active")
        .bind(&created_at)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to start Deep Work session: {}", e))?;

    Ok(AliceDeepWorkSession {
        session_id,
        user_id: request.user_id.clone(),
        start_time,
        end_time: None,
        duration_seconds: None,
        goal: request.goal.clone(),
        status: "active".to_string(),
        created_at,
    })
}

async fn alice_stop_deep_work_internal(
    db: &DatabaseManager,
    request: &StopDeepWorkRequest,
) -> Result<AliceDeepWorkSession, String> {
    validate_stop_status(&request.status)?;

    // Get the active session
    let get_query = r#"
        SELECT session_id, user_id, start_time, end_time, duration_seconds, goal, status, created_at
        FROM alice_deep_work_sessions
        WHERE user_id = ? AND status = 'active'
        ORDER BY created_at DESC
        LIMIT 1
    "#;

    let session_row = sqlx::query_as::<_, AliceDeepWorkSession>(get_query)
        .bind(&request.user_id)
        .fetch_optional(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to fetch active session: {}", e))?
        .ok_or("No active Deep Work session found")?;

    // Calculate duration
    let end_time = chrono::Utc::now();
    let start_time = chrono::DateTime::parse_from_rfc3339(&session_row.start_time)
        .map_err(|e| format!("Invalid start time format: {}", e))?
        .with_timezone(&chrono::Utc);

    let duration = end_time
        .signed_duration_since(start_time)
        .num_seconds()
        .max(0);
    let end_time_str = end_time.to_rfc3339();

    let update_query = r#"
        UPDATE alice_deep_work_sessions
        SET end_time = ?, duration_seconds = ?, status = ?
        WHERE session_id = ?
    "#;

    sqlx::query(update_query)
        .bind(&end_time_str)
        .bind(duration)
        .bind(&request.status)
        .bind(&session_row.session_id)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to stop Deep Work session: {}", e))?;

    Ok(AliceDeepWorkSession {
        session_id: session_row.session_id,
        user_id: session_row.user_id,
        start_time: session_row.start_time,
        end_time: Some(end_time_str),
        duration_seconds: Some(duration),
        goal: session_row.goal,
        status: request.status.clone(),
        created_at: session_row.created_at,
    })
}

