// Memory Allocation System for AI Conferences
// Integrates Core Module conferences with Vault's namespace system
// Provides quota enforcement and lifecycle management for conference memory

use serde::{Deserialize, Serialize};

use super::conference::ConferenceType;

/// Allocation status
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
pub enum AllocationStatus {
    Active,
    Archived,
    Deallocated,
}

/// Type of memory namespace allocation
#[derive(Debug, Clone, Serialize, Deserialize)]
#[serde(tag = "type")]
pub enum NamespaceType {
    Shared,                          // Shared conference memory
    Individual { agent_id: String }, // Per-participant memory
}

/// Memory allocation record for a conference namespace
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MemoryAllocation {
    pub allocation_id: String,
    pub conference_id: String,
    pub namespace_type: NamespaceType,
    pub vault_namespace_id: String, // Vault namespace ID
    pub namespace_id: String,       // Alias for compatibility
    pub quota: usize,               // Maximum entries allowed
    pub current_usage: usize,       // Current entry count
    pub status: AllocationStatus,   // Current status
    pub created_at: String,
    pub updated_at: String,
    pub archived_at: Option<String>,
}

/// Helper functions for memory quota calculation
pub struct MemoryQuotaHelper;

impl MemoryQuotaHelper {
    /// Get shared memory quota for conference type
    pub fn shared_memory_quota(conference_type: &ConferenceType) -> usize {
        match conference_type {
            ConferenceType::Brainstorm => 5000,
            ConferenceType::ProblemSolving => 3000,
            ConferenceType::Review => 1000,
            ConferenceType::Planning => 4000,
            ConferenceType::Research => 10000,
        }
    }

    /// Get individual memory quota for conference type
    pub fn individual_memory_quota(conference_type: &ConferenceType) -> usize {
        match conference_type {
            ConferenceType::Brainstorm => 1000,
            ConferenceType::ProblemSolving => 800,
            ConferenceType::Review => 200,
            ConferenceType::Planning => 1000,
            ConferenceType::Research => 2000,
        }
    }
}

// Requests for Tauri commands

#[derive(Debug, Deserialize)]
pub struct AllocateMemoryRequest {
    #[serde(rename = "conferenceId")]
    pub conference_id: String,
}

#[derive(Debug, Deserialize)]
pub struct DeallocateMemoryRequest {
    #[serde(rename = "conferenceId")]
    pub conference_id: String,
    pub archive: bool,
}

#[derive(Debug, Deserialize)]
pub struct GetMemoryUsageRequest {
    #[serde(rename = "namespaceId")]
    pub namespace_id: String,
}

#[derive(Debug, Deserialize)]
pub struct CheckQuotaRequest {
    #[serde(rename = "namespaceId")]
    pub namespace_id: String,
    pub quota: usize,
}

// Response types

#[derive(Debug, Serialize)]
pub struct MemoryAllocationResponse {
    pub allocations: Vec<MemoryAllocation>,
}

#[derive(Debug, Serialize)]
pub struct MemoryUsageResponse {
    pub usage: usize,
}

#[derive(Debug, Serialize)]
pub struct QuotaCheckResponse {
    pub within_quota: bool,
    pub usage: usize,
    pub quota: usize,
}

// Additional types required by ConferenceManager

/// Memory quota configuration for conference types
#[derive(Debug, Clone)]
pub struct MemoryQuotaConfig {
    pub shared_quota: usize,
    pub individual_quota: usize,
}

impl MemoryQuotaConfig {
    pub fn for_conference_type(conference_type: ConferenceType) -> Self {
        Self {
            shared_quota: MemoryQuotaHelper::shared_memory_quota(&conference_type),
            individual_quota: MemoryQuotaHelper::individual_memory_quota(&conference_type),
        }
    }
}

/// Result of memory allocation operation
#[derive(Debug, Serialize)]
pub struct AllocationResult {
    pub conference_id: String,
    pub allocations: Vec<MemoryAllocation>,
    pub shared_namespace_id: String,
    pub individual_namespace_ids: std::collections::HashMap<String, String>,
    pub success: bool,
    pub error: Option<String>,
}

/// Result of memory deallocation operation
#[derive(Debug, Serialize)]
pub struct DeallocationResult {
    pub conference_id: String,
    pub success: bool,
    pub archived_count: usize,
    pub deleted_namespaces_count: usize,
    pub error: Option<String>,
}

/// Options for archiving memory
#[derive(Debug, Deserialize, Default)]
pub struct ArchiveOptions {
    pub destination: Option<String>,
    #[serde(default)]
    pub compress: bool,
    #[serde(default = "default_preserve_data")]
    pub preserve_data: bool,
}

fn default_preserve_data() -> bool {
    true
}

/// Allocation statistics
#[derive(Debug, Serialize)]
pub struct AllocationStats {
    pub total_quota: usize,
    pub total_usage: usize,
    pub remaining: usize,
    pub usage_percentage: f32,
    pub over_quota: bool,
}

/// Per-participant memory statistics
#[derive(Debug, Serialize)]
pub struct ParticipantMemoryStats {
    pub agent_id: String,
    pub namespace_id: String,
    pub quota: usize,
    pub usage: usize,
    pub remaining: usize,
    pub usage_percentage: f32,
}

/// Memory usage statistics
#[derive(Debug, Serialize)]
pub struct MemoryUsageStats {
    pub conference_id: String,
    pub shared_memory: AllocationStats,
    pub individual_memory: AllocationStats,
    pub total_allocations: usize,
    pub active_allocations: usize,
    pub total_quota: usize,
    pub total_usage: usize,
    pub participant_stats: Vec<ParticipantMemoryStats>,
    pub calculated_at: String,
}

// Update MemoryAllocation struct to include status
impl MemoryAllocation {
    pub fn new_shared(conference_id: String, quota: usize) -> Self {
        let vault_namespace_id = format!("conf_{}_shared", conference_id);
        let now = chrono::Utc::now().to_rfc3339();
        Self {
            allocation_id: format!("alloc_{}_{}", conference_id, "shared"),
            conference_id,
            namespace_type: NamespaceType::Shared,
            vault_namespace_id: vault_namespace_id.clone(),
            namespace_id: vault_namespace_id,
            quota,
            current_usage: 0,
            status: AllocationStatus::Active,
            created_at: now.clone(),
            updated_at: now,
            archived_at: None,
        }
    }

    pub fn new_individual(conference_id: String, agent_id: String, quota: usize) -> Self {
        let vault_namespace_id = format!("conf_{}_agent_{}", conference_id, agent_id);
        let now = chrono::Utc::now().to_rfc3339();
        Self {
            allocation_id: format!("alloc_{}_{}", conference_id, agent_id),
            conference_id,
            namespace_type: NamespaceType::Individual {
                agent_id: agent_id.clone(),
            },
            vault_namespace_id: vault_namespace_id.clone(),
            namespace_id: vault_namespace_id,
            quota,
            current_usage: 0,
            status: AllocationStatus::Active,
            created_at: now.clone(),
            updated_at: now,
            archived_at: None,
        }
    }

    /// Calculate remaining quota
    pub fn remaining_quota(&self) -> usize {
        if self.current_usage >= self.quota {
            0
        } else {
            self.quota - self.current_usage
        }
    }

    /// Calculate usage percentage
    pub fn usage_percentage(&self) -> f32 {
        if self.quota == 0 {
            0.0
        } else {
            (self.current_usage as f32 / self.quota as f32) * 100.0
        }
    }
}

