    /// Update conference state (start, pause, resume, conclude, archive)
    pub async fn update_conference_state(
        &self,
        conference_id: &str,
        new_state: ConferenceState,
    ) -> Result<Conference, String> {
        let mut conference = self.get_conference(conference_id).await?;

        // Apply state transition
        match new_state {
            ConferenceState::Active => {
                if conference.state == ConferenceState::Planning {
                    conference.start()?;
                } else if conference.state == ConferenceState::Paused {
                    conference.resume()?;
                } else {
                    return Err(format!(
                        "Cannot activate conference from state {:?}",
                        conference.state
                    ));
                }
            }
            ConferenceState::Paused => conference.pause()?,
            ConferenceState::Concluded => conference.conclude()?,
            ConferenceState::Archived => conference.archive()?,
            _ => return Err(format!("Invalid state transition to {:?}", new_state)),
        }

        // Update database
        let db = self.db.read().await;
        let pool = &db.as_ref().unwrap().write_pool;

        sqlx::query(
            r#"
            UPDATE core_conferences
            SET state = ?, started_at = ?, concluded_at = ?
            WHERE conference_id = ?
            "#,
        )
        .bind(serde_json::to_string(&conference.state).unwrap())
        .bind(&conference.started_at)
        .bind(&conference.concluded_at)
        .bind(conference_id)
        .execute(pool)
        .await
        .map_err(|e| format!("Failed to update conference state: {}", e))?;

        Ok(conference)
    }

