    /// Allocate memory for a conference (creates Vault namespaces)
    pub async fn allocate_memory(&self, conference_id: &str) -> Result<AllocationResult, String> {
        // 1. Get conference
        let conference = self.get_conference(conference_id).await?;

        // 2. Get quota configuration
        let quota_config = memory_allocation::MemoryQuotaConfig::for_conference_type(
            conference.conference_type.clone(),
        );

        let mut allocations = Vec::new();
        let mut individual_namespace_ids = HashMap::new();

        // 3. Create shared memory allocation
        let shared_allocation = memory_allocation::MemoryAllocation::new_shared(
            conference_id.to_string(),
            quota_config.shared_quota,
        );
        let shared_namespace_id = shared_allocation.vault_namespace_id.clone();

        // Store in database
        self.save_allocation(&shared_allocation).await?;
        allocations.push(shared_allocation);

        // 4. Create individual memory allocations for each participant
        for participant in &conference.participants {
            let individual_allocation = memory_allocation::MemoryAllocation::new_individual(
                conference_id.to_string(),
                participant.agent_id.clone(),
                quota_config.individual_quota,
            );

            individual_namespace_ids.insert(
                participant.agent_id.clone(),
                individual_allocation.vault_namespace_id.clone(),
            );

            // Store in database
            self.save_allocation(&individual_allocation).await?;
            allocations.push(individual_allocation);
        }

        // 5. Update conference with shared_memory_id
        let db = self.db.read().await;
        let pool = &db.as_ref().unwrap().write_pool;

        sqlx::query("UPDATE core_conferences SET shared_memory_id = ? WHERE conference_id = ?")
            .bind(&shared_namespace_id)
            .bind(conference_id)
            .execute(pool)
            .await
            .map_err(|e| format!("Failed to update conference memory ID: {}", e))?;

        // 6. Update participants with individual_memory_id
        for (agent_id, namespace_id) in &individual_namespace_ids {
            sqlx::query(
                "UPDATE core_conference_participants SET individual_memory_id = ? WHERE conference_id = ? AND agent_id = ?"
            )
            .bind(namespace_id)
            .bind(conference_id)
            .bind(agent_id)
            .execute(pool)
            .await
            .map_err(|e| format!("Failed to update participant memory ID: {}", e))?;
        }

        Ok(AllocationResult {
            conference_id: conference_id.to_string(),
            allocations,
            shared_namespace_id,
            individual_namespace_ids,
            success: true,
            error: None,
        })
    }

    /// Save a memory allocation to database
    async fn save_allocation(
