/// Monitor responsible for proactive system triggers
pub struct ProactiveMonitor {
    db: Arc<RwLock<Option<DatabaseManager>>>,
    event_emitter: EventEmitter,
    is_enabled: Arc<RwLock<bool>>,
    settings_manager: Arc<SettingsManager>,
    last_input_time: Arc<Mutex<Instant>>,
}

impl ProactiveMonitor {
    pub fn new(
        db: Arc<RwLock<Option<DatabaseManager>>>,
        event_emitter: EventEmitter,
        settings_manager: Arc<SettingsManager>,
    ) -> Self {
        Self {
            db,
            event_emitter,
            // DEFAULT TO FALSE (OFF) as per safety requirements
            is_enabled: Arc::new(RwLock::new(false)),
            settings_manager,
            last_input_time: Arc::new(Mutex::new(Instant::now())),
        }
    }

    /// Enable or disable the proactive core
    pub async fn set_enabled(&self, enabled: bool) {
        let mut lock = self.is_enabled.write().await;
        *lock = enabled;
    }

    /// Check if proactive core is enabled
    pub async fn is_enabled(&self) -> bool {
        *self.is_enabled.read().await
    }

    /// Start the monitoring loop (runs in background)
    pub async fn start_loop(&self) {
        let is_enabled = self.is_enabled.clone();
        let event_emitter = self.event_emitter.clone();
        let settings_manager = self.settings_manager.clone();
        let last_input_time = self.last_input_time.clone();

        println!("[ProactiveMonitor] Loop initialized (State: Disabled by default)");

        tokio::spawn(async move {
            let mut temporal_interval = tokio::time::interval(Duration::from_secs(60)); // Check temporal triggers every minute
            let mut activity_interval = tokio::time::interval(Duration::from_secs(10)); // Check activity triggers every 10 seconds

            loop {
                tokio::select! {
                    _ = temporal_interval.tick() => {
                        // 1. Safety Check: Is Core Enabled?
                        if !*is_enabled.read().await {
                            continue; // Skip silently if disabled
                        }

                        // 2. Evaluate Temporal Triggers
                        if let Some(event) = Self::evaluate_temporal_triggers().await {
                            println!("[ProactiveMonitor] Temporal trigger fired: {:?}", event.trigger_type);
                            let payload = Self::event_to_spec_payload(&event);
                            if let Err(e) = event_emitter.emit_event("core://proactive-event", &payload) {
                                eprintln!("[ProactiveMonitor] Failed to emit event: {}", e);
                            }
                        }
                    }
                    _ = activity_interval.tick() => {
                        // 1. Safety Check: Is Core Enabled?
                        if !*is_enabled.read().await {
                            continue; // Skip silently if disabled
                        }

                        // 2. Update last input time
                        Self::update_last_input_time(&last_input_time);

                        // 3. Evaluate Activity Triggers
                        let settings = settings_manager.load();
                        if let Some(event) = Self::evaluate_activity_triggers(&settings, &last_input_time).await {
                            println!("[ProactiveMonitor] Activity trigger fired: {:?}", event.trigger_type);
                            let payload = Self::event_to_spec_payload(&event);
                            if let Err(e) = event_emitter.emit_event("core://proactive-event", &payload) {
                                eprintln!("[ProactiveMonitor] Failed to emit event: {}", e);
                            }
                        }
                    }
                }
            }
        });
    }

    /// Logic to check time-based triggers
    async fn evaluate_temporal_triggers() -> Option<ProactiveEvent> {
        Self::evaluate_temporal_triggers_at(Local::now())
    }

    pub fn evaluate_temporal_triggers_at(now: DateTime<Local>) -> Option<ProactiveEvent> {
        let hour = now.hour();
        let minute = now.minute();

        // Trigger: Morning Briefing at 09:00
        if hour == 9 && minute == 0 {
            return Some(ProactiveEvent {
                id: uuid::Uuid::new_v4().to_string(),
                trigger_type: TriggerType::Temporal("Daily Briefing".into()),
                priority: RoutingPriority::Normal,
                suggested_persona: AgentId::Alden,
                payload: json!({
                    "message": "Good morning! Ready to review today's itinerary?",
                    "actions": ["Start Review", "Snooze"]
                }),
            });
        }

        // Trigger: End of Day Wrap-up at 17:00 (5 PM)
        if hour == 17 && minute == 0 {
            return Some(ProactiveEvent {
                id: uuid::Uuid::new_v4().to_string(),
                trigger_type: TriggerType::Temporal("Wrap-up".into()),
                priority: RoutingPriority::Normal,
                suggested_persona: AgentId::Alden,
                payload: json!({
                    "message": "It's 5 PM. Time to wrap up pending tasks?",
                    "actions": ["Start Wrap-up", "Continue Working"]
                }),
            });
        }

        None
    }

    /// Convert a ProactiveEvent to the spec-compliant payload format
    pub fn event_to_spec_payload(event: &ProactiveEvent) -> Value {
        let persona_str = match event.suggested_persona {
            AgentId::User => "user".to_string(),
            AgentId::Alden => "alden".to_string(),
            AgentId::Core => "core".to_string(),
            AgentId::Alice => "alice".to_string(),
        };

        let priority_str = match event.priority {
            RoutingPriority::Low => "low".to_string(),
            RoutingPriority::Normal => "normal".to_string(),
            RoutingPriority::High => "high".to_string(),
        };

        json!({
            "eventId": event.id,
            "persona": persona_str,
            "message": event.payload.get("message").unwrap_or(&json!("")).as_str().unwrap_or(""),
            "priority": priority_str,
            "actions": event.payload.get("actions").unwrap_or(&json!([]))
        })
    }

    /// Update the last input time based on current user activity
    fn update_last_input_time(last_input_time: &Arc<Mutex<Instant>>) {
        #[cfg(target_os = "windows")]
        {
            let device_state = DeviceState::new();
            let keys = device_state.get_keys();

            // Check for keyboard or mouse activity
            if !keys.is_empty() {
                let mut last_input = last_input_time.lock().unwrap();
                *last_input = Instant::now();
            }
        }

        #[cfg(not(target_os = "windows"))]
        {
            // For non-Windows systems, we'll use a simple timer approach
            // This could be enhanced with platform-specific implementations
            let mut last_input = last_input_time.lock().unwrap();
            if last_input.elapsed() > Duration::from_secs(1) {
                *last_input = Instant::now();
            }
        }
    }

    /// Evaluate activity-based triggers (idle time and active window)
    async fn evaluate_activity_triggers(
        settings: &SettingsConfig,
        last_input_time: &Arc<Mutex<Instant>>,
    ) -> Option<ProactiveEvent> {
        let now = Local::now();
        let hour = now.hour();

        // Check idle time trigger
        {
            let last_input = last_input_time.lock().unwrap();
            let idle_duration = last_input.elapsed();

            if idle_duration.as_secs() >= settings.proactive.idle_timeout_seconds as u64 {
                return Some(ProactiveEvent {
                    id: uuid::Uuid::new_v4().to_string(),
                    trigger_type: TriggerType::Contextual("User Idle".to_string()),
                    priority: RoutingPriority::Normal,
                    suggested_persona: AgentId::Alden,
                    payload: json!({
                        "message": "You've been idle for a while. Would you like to take a break or resume work?",
                        "actions": ["Take Break", "Resume Work", "Dismiss"]
                    }),
                });
            }
        }

        // Check distraction apps during work hours (9 AM - 5 PM)
        if hour >= 9 && hour <= 17 {
            #[cfg(target_os = "windows")]
            {
                if let Ok(active_window) = get_active_window() {
                    let window_title = active_window.title.to_lowercase();

                    for distraction_app in &settings.proactive.distraction_apps {
                        if window_title.contains(&distraction_app.to_lowercase()) {
                            return Some(ProactiveEvent {
                                id: uuid::Uuid::new_v4().to_string(),
                                trigger_type: TriggerType::Contextual(
                                    "Digital Wellbeing".to_string(),
                                ),
                                priority: RoutingPriority::High,
                                suggested_persona: AgentId::Alice,
                                payload: json!({
                                    "message": format!("It looks like you're using {} during work hours. Want to stay focused?", distraction_app),
                                    "actions": ["Get Back to Work", "Set a Timer", "Continue"]
                                }),
                            });
                        }
                    }
                }
            }
        }

        None
    }
}

