/// Get current session for user
#[tauri::command]
pub async fn core_get_current_session(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    user_id: String,
) -> Result<Option<CoreSession>, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let query = r#"
        SELECT session_id, user_id, active_agents, context, started_at, last_activity
        FROM core_sessions
        WHERE user_id = ?
        ORDER BY last_activity DESC
        LIMIT 1
    "#;

    let result = sqlx::query_as::<_, CoreSessionRow>(query)
        .bind(&user_id)
        .fetch_optional(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to fetch current session: {}", e))?;

    if let Some(row) = result {
        let active_agents: Vec<String> = serde_json::from_str(&row.active_agents)
            .map_err(|e| format!("Failed to parse active agents: {}", e))?;
        let context: HashMap<String, serde_json::Value> = serde_json::from_str(&row.context)
            .map_err(|e| format!("Failed to parse context: {}", e))?;

        Ok(Some(CoreSession {
            session_id: row.session_id,
            user_id: row.user_id,
            active_agents,
            context,
            started_at: row.started_at,
            last_activity: row.last_activity,
        }))
    } else {
        Ok(None)
    }
}

/// Create new session
#[tauri::command]
pub async fn core_create_session(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: CreateSessionRequest,
) -> Result<CoreSession, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let session_id = format!("session_{}", chrono::Utc::now().timestamp_millis());
    let started_at = chrono::Utc::now().to_rfc3339();
    let context = request.context.unwrap_or_default();

    let query = r#"
        INSERT INTO core_sessions (
            session_id, user_id, active_agents, context, started_at, last_activity
        ) VALUES (?, ?, ?, ?, ?, ?)
    "#;

    let active_agents_json =
        serde_json::to_string(&Vec::<String>::new()).map_err(|e| e.to_string())?;
    let context_json = serde_json::to_string(&context).map_err(|e| e.to_string())?;

    sqlx::query(query)
        .bind(&session_id)
        .bind(&request.user_id)
        .bind(&active_agents_json)
        .bind(&context_json)
        .bind(&started_at)
        .bind(&started_at)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to create Core session: {}", e))?;

    Ok(CoreSession {
        session_id,
        user_id: request.user_id,
        active_agents: Vec::new(),
        context,
        started_at: started_at.clone(),
        last_activity: started_at,
    })
}

/// Update session context
#[tauri::command]
pub async fn core_update_session_context(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    session_id: String,
    context: HashMap<String, serde_json::Value>,
) -> Result<CoreSession, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let last_activity = chrono::Utc::now().to_rfc3339();

    let query = r#"
        UPDATE core_sessions
        SET context = ?, last_activity = ?
        WHERE session_id = ?
    "#;

    let context_json = serde_json::to_string(&context).map_err(|e| e.to_string())?;

    sqlx::query(query)
        .bind(&context_json)
        .bind(&last_activity)
        .bind(&session_id)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to update session context: {}", e))?;

    // Get updated session
    let get_query = r#"
        SELECT session_id, user_id, active_agents, context, started_at, last_activity
        FROM core_sessions
        WHERE session_id = ?
    "#;

    let row = sqlx::query_as::<_, CoreSessionRow>(get_query)
        .bind(&session_id)
        .fetch_one(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to fetch updated session: {}", e))?;

    let active_agents: Vec<String> = serde_json::from_str(&row.active_agents)
        .map_err(|e| format!("Failed to parse active agents: {}", e))?;
    let context_parsed: HashMap<String, serde_json::Value> = serde_json::from_str(&row.context)
        .map_err(|e| format!("Failed to parse context: {}", e))?;

    Ok(CoreSession {
        session_id: row.session_id,
        user_id: row.user_id,
        active_agents,
        context: context_parsed,
        started_at: row.started_at,
        last_activity: row.last_activity,
    })
}

/// Add agent to session
#[tauri::command]
pub async fn core_add_agent_to_session(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    session_id: String,
    agent_id: String,
) -> Result<CoreSession, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    // First get current session
    let get_query = r#"
        SELECT session_id, user_id, active_agents, context, started_at, last_activity
        FROM core_sessions
        WHERE session_id = ?
    "#;

    let row = sqlx::query_as::<_, CoreSessionRow>(get_query)
        .bind(&session_id)
        .fetch_one(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to fetch session: {}", e))?;

    let mut active_agents: Vec<String> = serde_json::from_str(&row.active_agents)
        .map_err(|e| format!("Failed to parse active agents: {}", e))?;
    let context: HashMap<String, serde_json::Value> = serde_json::from_str(&row.context)
        .map_err(|e| format!("Failed to parse context: {}", e))?;

    // Add agent if not already present
    if !active_agents.contains(&agent_id) {
        active_agents.push(agent_id.clone());
    }

    let last_activity = chrono::Utc::now().to_rfc3339();

    let update_query = r#"
        UPDATE core_sessions
        SET active_agents = ?, last_activity = ?
        WHERE session_id = ?
    "#;

    let active_agents_json = serde_json::to_string(&active_agents).map_err(|e| e.to_string())?;

    sqlx::query(update_query)
        .bind(&active_agents_json)
        .bind(&last_activity)
        .bind(&session_id)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to add agent to session: {}", e))?;

    let mut session = CoreSession {
        session_id: row.session_id,
        user_id: row.user_id,
        active_agents,
        context,
        started_at: row.started_at,
        last_activity: row.last_activity,
    };

    session.last_activity = last_activity;
    Ok(session)
}

