/// Send message with persona-specific routing and context isolation
#[tauri::command]
pub async fn send_message(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: CreateMessageRequest,
) -> Result<CoreMessage, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    // Validate session exists
    let session_query = r#"
        SELECT session_id, user_id, active_agents, context, started_at, last_activity
        FROM core_sessions
        WHERE session_id = ?
    "#;

    let session_row = sqlx::query_as::<_, CoreSessionRow>(session_query)
        .bind(&request.session_id)
        .fetch_optional(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to validate session: {}", e))?;

    if session_row.is_none() {
        return Err("Session not found".to_string());
    }

    // Enforce zero-trust boundaries: validate from_agent and to_agent
    let from_agent = request.from_agent.as_deref().unwrap_or("user");
    let to_agent = request.to_agent.as_deref().unwrap_or("alden");

    // Validate agents are different (no self-messaging)
    if from_agent == to_agent {
        return Err("Agents cannot send messages to themselves".to_string());
    }

    // Create message with persona-specific context isolation
    let message_id = format!("msg_{}", chrono::Utc::now().timestamp_millis());
    let timestamp = chrono::Utc::now().to_rfc3339();
    let priority = request.priority.unwrap_or("normal".to_string());

    // Isolate context for each persona
    let mut isolated_content = request.content.clone();
    if let (Some(from), Some(to)) = (&request.from_agent, &request.to_agent) {
        // Apply persona-specific context filtering
        isolated_content = apply_persona_context_filter(isolated_content, from, to)?;
    }

    let query = r#"
        INSERT INTO core_messages (
            message_id, session_id, from_agent, to_agent, content, timestamp, priority
        ) VALUES (?, ?, ?, ?, ?, ?, ?)
    "#;

    let content_json = serde_json::to_string(&isolated_content).map_err(|e| e.to_string())?;

    sqlx::query(query)
        .bind(&message_id)
        .bind(&request.session_id)
        .bind(&request.from_agent)
        .bind(&request.to_agent)
        .bind(&content_json)
        .bind(&timestamp)
        .bind(&priority)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to send message: {}", e))?;

    // Update session last activity
    let update_query = r#"
        UPDATE core_sessions
        SET last_activity = ?
        WHERE session_id = ?
    "#;

    sqlx::query(update_query)
        .bind(&timestamp)
        .bind(&request.session_id)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to update session activity: {}", e))?;

    Ok(CoreMessage {
        message_id,
        session_id: request.session_id,
        from_agent: request.from_agent,
        to_agent: request.to_agent,
        content: isolated_content,
        timestamp,
        priority,
    })
}

/// Get chat history with persona-specific privacy filtering
#[tauri::command]
pub async fn get_chat_history(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    session_id: String,
    requesting_agent: String,
    limit: Option<i64>,
    offset: Option<i64>,
) -> Result<Vec<CoreMessage>, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    // Validate session exists
    let session_query = r#"
        SELECT session_id, user_id, active_agents, context, started_at, last_activity
        FROM core_sessions
        WHERE session_id = ?
    "#;

    let session_row = sqlx::query_as::<_, CoreSessionRow>(session_query)
        .bind(&session_id)
        .fetch_optional(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to validate session: {}", e))?;

    if session_row.is_none() {
        return Err("Session not found".to_string());
    }

    // Validate requesting agent is authorized for this session
    let active_agents: Vec<String> = serde_json::from_str(&session_row.unwrap().active_agents)
        .map_err(|e| format!("Failed to parse active agents: {}", e))?;

    if !active_agents.contains(&requesting_agent) && requesting_agent != "user" {
        return Err("Agent not authorized to access this session".to_string());
    }

    // Build query with privacy filtering
    let limit_val = limit.unwrap_or(50);
    let offset_val = offset.unwrap_or(0);

    let query = r#"
        SELECT message_id, session_id, from_agent, to_agent, content, timestamp, priority
        FROM core_messages
        WHERE session_id = ?
        AND (
            from_agent = ? OR to_agent = ? OR from_agent IS NULL OR to_agent IS NULL
        )
        ORDER BY timestamp DESC
        LIMIT ? OFFSET ?
    "#;

    let rows = sqlx::query_as::<_, CoreMessageRow>(query)
        .bind(&session_id)
        .bind(&requesting_agent)
        .bind(&requesting_agent)
        .bind(limit_val)
        .bind(offset_val)
        .fetch_all(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to fetch chat history: {}", e))?;

    let mut messages = Vec::new();
    for row in rows {
        let mut content: serde_json::Value = serde_json::from_str(&row.content)
            .map_err(|e| format!("Failed to parse message content: {}", e))?;

        // Apply privacy filtering based on requesting agent
        content = apply_privacy_filter(content, &requesting_agent, &row.from_agent, &row.to_agent)?;

        messages.push(CoreMessage {
            message_id: row.message_id,
            session_id: row.session_id,
            from_agent: row.from_agent,
            to_agent: row.to_agent,
            content,
            timestamp: row.timestamp,
            priority: row.priority,
        });
    }

    Ok(messages)
}

/// Apply persona-specific context filtering to message content
fn apply_persona_context_filter(
    content: serde_json::Value,
    from_agent: &str,
    to_agent: &str,
) -> Result<serde_json::Value, String> {
    // Create a filtered copy of the content
    let mut filtered_content = content.clone();

    // Remove sensitive context based on agent boundaries
    if let Some(obj) = filtered_content.as_object_mut() {
        // Remove agent-internal context that shouldn't be shared
        obj.remove(format!("{}_internal", from_agent).as_str());
        obj.remove(format!("{}_private", from_agent).as_str());
        obj.remove(format!("{}_secrets", from_agent).as_str());

        // Add routing metadata
        obj.insert(
            "routing_metadata".to_string(),
            serde_json::json!({
                "from_agent": from_agent,
                "to_agent": to_agent,
                "filtered_at": chrono::Utc::now().to_rfc3339(),
                "trust_level": "zero_trust"
            }),
        );
    }

    Ok(filtered_content)
}

