use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::sync::Arc;
use tauri::State;
use tokio::sync::RwLock;

use crate::db_manager::DatabaseManager;

/// Stream metadata for tracking stream information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct StreamMetadata {
    pub stream_id: String,
    pub stream_type: StreamType,
    pub created_at: String,
    pub bytes_transferred: u64,
    pub active: bool,
    pub chunk_size: u32,
    pub total_chunks: u32,
}

/// Types of streams supported
#[derive(Debug, Clone, Serialize, Deserialize)]
#[serde(rename_all = "lowercase")]
pub enum StreamType {
    Avatar,
    Audio,
    Data,
    Event,
}

/// Stream chunk for data transfer
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct StreamChunk {
    pub stream_id: String,
    pub chunk_index: u32,
    pub data: Vec<u8>,
    pub metadata: Option<HashMap<String, String>>,
    pub timestamp: String,
}

/// Stream status information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct StreamStatus {
    pub active: bool,
    pub bytes_transferred: u64,
    pub chunks_completed: u32,
    pub total_chunks: u32,
    pub error: Option<String>,
}

/// Streaming manager for handling active streams
pub struct StreamingManager {
    streams: Arc<RwLock<HashMap<String, StreamMetadata>>>,
}

impl StreamingManager {
    pub fn new() -> Self {
        Self {
            streams: Arc::new(RwLock::new(HashMap::new())),
        }
    }

    /// Create a new stream with metadata
    pub async fn create_stream(
        &self,
        stream_id: String,
        stream_type: StreamType,
        chunk_size: u32,
        total_chunks: u32,
    ) -> Result<StreamMetadata, String> {
        let metadata = StreamMetadata {
            stream_id: stream_id.clone(),
            stream_type,
            created_at: chrono::Utc::now().to_rfc3339(),
            bytes_transferred: 0,
            active: true,
            chunk_size,
            total_chunks,
        };

        let mut streams = self.streams.write().await;
        streams.insert(stream_id, metadata.clone());

        Ok(metadata)
    }

    /// Close a stream and clean up resources
    pub async fn close_stream(&self, stream_id: &str) -> Result<StreamStatus, String> {
        let mut streams = self.streams.write().await;

        if let Some(mut metadata) = streams.remove(stream_id) {
            metadata.active = false;

            let status = StreamStatus {
                active: false,
                bytes_transferred: metadata.bytes_transferred,
                chunks_completed: metadata.total_chunks, // Assume all chunks completed
                total_chunks: metadata.total_chunks,
                error: None,
            };

            Ok(status)
        } else {
            Err(format!("Stream {} not found", stream_id))
        }
    }

    /// Get stream status
    pub async fn get_stream_status(&self, stream_id: &str) -> Result<StreamStatus, String> {
        let streams = self.streams.read().await;

        if let Some(metadata) = streams.get(stream_id) {
            Ok(StreamStatus {
                active: metadata.active,
                bytes_transferred: metadata.bytes_transferred,
                chunks_completed: 0, // TODO: Track completed chunks
                total_chunks: metadata.total_chunks,
                error: None,
            })
        } else {
            Err(format!("Stream {} not found", stream_id))
        }
    }

    /// Process incoming stream chunk
    pub async fn process_chunk(&self, chunk: StreamChunk) -> Result<(), String> {
        let mut streams = self.streams.write().await;

        if let Some(metadata) = streams.get_mut(&chunk.stream_id) {
            metadata.bytes_transferred += chunk.data.len() as u64;
            Ok(())
        } else {
            Err(format!("Stream {} not found", chunk.stream_id))
        }
    }
}

