/// Stream avatar data
#[tauri::command]
pub async fn streaming_stream_avatar(
    audio_data: Vec<u8>,
    metadata: Option<HashMap<String, String>>,
    state: State<'_, StreamingState>,
) -> Result<String, String> {
    // Create avatar stream
    let stream_id = format!("avatar_{}", chrono::Utc::now().timestamp_millis());

    let stream_metadata = state
        .manager
        .create_stream(
            stream_id.clone(),
            StreamType::Avatar,
            1024, // 1KB chunks for avatar data
            (audio_data.len() / 1024 + 1) as u32,
        )
        .await?;

    // Process audio data in chunks
    let chunk_size = stream_metadata.chunk_size as usize;
    let total_chunks = (audio_data.len() / chunk_size) + 1;

    for (i, chunk) in audio_data.chunks(chunk_size).enumerate() {
        let stream_chunk = StreamChunk {
            stream_id: stream_id.clone(),
            chunk_index: i as u32,
            data: chunk.to_vec(),
            metadata: metadata.clone(),
            timestamp: chrono::Utc::now().to_rfc3339(),
        };

        state.manager.process_chunk(stream_chunk).await?;
    }

    // Close the stream
    state.manager.close_stream(&stream_id).await?;

    Ok(stream_id)
}

/// Stream generic data in chunks
#[tauri::command]
