) -> Result<String, String> {
    let stream_id =
        stream_id.unwrap_or_else(|| format!("data_{}", chrono::Utc::now().timestamp_millis()));

    let stream_metadata = state
        .manager
        .create_stream(
            stream_id.clone(),
            StreamType::Data,
            chunk_size,
            (data.len() / chunk_size as usize + 1) as u32,
        )
        .await?;

    // Process data in chunks
    let chunk_size = chunk_size as usize;

    for (i, chunk) in data.chunks(chunk_size).enumerate() {
        let stream_chunk = StreamChunk {
            stream_id: stream_id.clone(),
            chunk_index: i as u32,
            data: chunk.to_vec(),
            metadata: None,
            timestamp: chrono::Utc::now().to_rfc3339(),
        };

        state.manager.process_chunk(stream_chunk).await?;
    }

