/// Clean up inactive streams
#[tauri::command]
pub async fn streaming_cleanup(
    older_than_ms: u64,
    state: State<'_, StreamingState>,
) -> Result<usize, String> {
    let mut streams = state.manager.streams.write().await;
    let cutoff_time = chrono::Utc::now().timestamp_millis() - older_than_ms as i64;

    let initial_count = streams.len();
    streams.retain(|_, metadata| {
        metadata
            .created_at
            .parse::<chrono::DateTime<chrono::Utc>>()
            .map(|dt| dt.timestamp_millis() > cutoff_time)
            .unwrap_or(true)
    });

    let cleaned_count = initial_count - streams.len();
    Ok(cleaned_count)
}

