/// Connection Management for Synapse Integrations
///
/// Manages external service connections with encrypted credential storage
use crate::db_manager::DatabaseManager;
use crate::synapse::encryption::CredentialEncryption;
use crate::synapse::models::*;
use crate::synapse::security::SecurityBoundary;
use std::collections::HashMap;
use std::sync::Arc;
use std::time::Duration;
use tokio::sync::RwLock;

pub struct ConnectionManager {
    db: Arc<RwLock<Option<DatabaseManager>>>,
    encryption: Arc<CredentialEncryption>,
    security: Arc<SecurityBoundary>,
}

impl ConnectionManager {
    pub fn new(
        db: Arc<RwLock<Option<DatabaseManager>>>,
        encryption: Arc<CredentialEncryption>,
        security: Arc<SecurityBoundary>,
    ) -> Self {
        Self {
            db,
            encryption,
            security,
        }
    }

    /// Initialize database schema for connections
    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = r#"
            CREATE TABLE IF NOT EXISTS synapse_connections (
                connection_id TEXT PRIMARY KEY NOT NULL,
                plugin_id TEXT NOT NULL,
                service_name TEXT NOT NULL,
                endpoint TEXT NOT NULL,
                protocol TEXT NOT NULL,
                credentials_encrypted BLOB NOT NULL,
                status TEXT NOT NULL DEFAULT 'disconnected',
                last_activity TEXT NOT NULL,
                created_at TEXT NOT NULL,
                FOREIGN KEY (plugin_id) REFERENCES synapse_plugins(plugin_id)
            )
        "#;

        sqlx::query(query)
            .execute(&db.write_pool)
            .await
            .map_err(|e| format!("Failed to create synapse_connections table: {}", e))?;

        Ok(())
    }

    /// Create a new connection with encrypted credentials
    pub async fn create_connection(
        &self,
        request: CreateConnectionRequest,
    ) -> Result<SynapseConnection, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // Encrypt credentials
        let encrypted_creds = self
            .encryption
            .encrypt_credentials(&request.credentials)
            .await
            .map_err(|e| format!("Failed to encrypt credentials: {}", e))?;

        let connection_id = format!("conn_{}", chrono::Utc::now().timestamp_millis());
        let created_at = chrono::Utc::now().to_rfc3339();
        let last_activity = created_at.clone();

        let protocol_json = serde_json::to_string(&request.protocol)
            .map_err(|e| format!("Failed to serialize protocol: {}", e))?;

        let status_json = serde_json::to_string(&ConnectionStatus::Disconnected)
            .map_err(|e| format!("Failed to serialize status: {}", e))?;

        let query = r#"
            INSERT INTO synapse_connections (
                connection_id, plugin_id, service_name, endpoint,
                protocol, credentials_encrypted, status,
                last_activity, created_at
            ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)
        "#;

        sqlx::query(query)
            .bind(&connection_id)
            .bind(&request.plugin_id)
            .bind(&request.service_name)
            .bind(&request.endpoint)
            .bind(&protocol_json)
            .bind(&encrypted_creds)
            .bind(&status_json)
            .bind(&last_activity)
            .bind(&created_at)
            .execute(&db.write_pool)
            .await
            .map_err(|e| format!("Failed to create connection: {}", e))?;

        // Log security event
        self.security
            .log_event(
                Some(request.plugin_id.clone()),
                Some(connection_id.clone()),
                SecurityEventType::ConnectionCreated,
                SecuritySeverity::Info,
                format!("Connection created for {}", request.service_name),
            )
            .await?;

        Ok(SynapseConnection {
            connection_id,
            plugin_id: request.plugin_id,
            service_name: request.service_name,
            endpoint: request.endpoint,
            protocol: request.protocol,
            status: ConnectionStatus::Disconnected,
            last_activity,
            created_at,
        })
    }

    /// Get a single connection by ID (without credentials)
    pub async fn get_connection(&self, connection_id: &str) -> Result<SynapseConnection, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = r#"
            SELECT connection_id, plugin_id, service_name, endpoint,
                   protocol, credentials_encrypted, status,
                   last_activity, created_at
            FROM synapse_connections
            WHERE connection_id = ?
        "#;

        let row = sqlx::query_as::<_, SynapseConnectionRow>(query)
            .bind(connection_id)
            .fetch_one(&db.read_pool)
            .await
            .map_err(|e| format!("Connection not found: {}", e))?;

        Ok(row.into())
    }

    /// Get decrypted credentials for a connection (internal use only)
    pub async fn get_connection_credentials(
        &self,
        connection_id: &str,
    ) -> Result<HashMap<String, String>, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = r#"
            SELECT credentials_encrypted
            FROM synapse_connections
            WHERE connection_id = ?
        "#;

        let (encrypted_creds,): (Vec<u8>,) = sqlx::query_as(query)
            .bind(connection_id)
            .fetch_one(&db.read_pool)
            .await
            .map_err(|e| format!("Failed to fetch credentials: {}", e))?;

        self.encryption.decrypt_credentials(&encrypted_creds).await
    }

    /// List all connections for a plugin
    pub async fn list_connections(
        &self,
        plugin_id: Option<String>,
    ) -> Result<Vec<SynapseConnection>, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = if let Some(pid) = plugin_id {
            sqlx::query_as::<_, SynapseConnectionRow>(
                r#"
                SELECT connection_id, plugin_id, service_name, endpoint,
                       protocol, credentials_encrypted, status,
                       last_activity, created_at
                FROM synapse_connections
                WHERE plugin_id = ?
                ORDER BY created_at DESC
            "#,
            )
            .bind(pid)
        } else {
            sqlx::query_as::<_, SynapseConnectionRow>(
                r#"
                SELECT connection_id, plugin_id, service_name, endpoint,
                       protocol, credentials_encrypted, status,
                       last_activity, created_at
                FROM synapse_connections
                ORDER BY created_at DESC
            "#,
            )
        };

        let rows = query
            .fetch_all(&db.read_pool)
            .await
            .map_err(|e| format!("Failed to list connections: {}", e))?;

        Ok(rows.into_iter().map(|row| row.into()).collect())
    }

    /// Update connection status
    pub async fn update_status(
        &self,
        connection_id: &str,
        status: ConnectionStatus,
    ) -> Result<SynapseConnection, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let last_activity = chrono::Utc::now().to_rfc3339();
        let status_json = serde_json::to_string(&status)
            .map_err(|e| format!("Failed to serialize status: {}", e))?;

        let query = r#"
            UPDATE synapse_connections
            SET status = ?, last_activity = ?
            WHERE connection_id = ?
        "#;

        sqlx::query(query)
            .bind(&status_json)
            .bind(&last_activity)
            .bind(connection_id)
            .execute(&db.write_pool)
            .await
            .map_err(|e| format!("Failed to update connection status: {}", e))?;

        // Log failed connections
        if matches!(status, ConnectionStatus::Failed) {
            self.security
                .log_event(
                    None,
                    Some(connection_id.to_string()),
                    SecurityEventType::ConnectionFailed,
                    SecuritySeverity::Warning,
                    "Connection failed".to_string(),
                )
                .await?;
        }

        self.get_connection(connection_id).await
    }

    /// Test a connection (make a simple request to verify connectivity)
    pub async fn test_connection(
        &self,
        connection_id: &str,
    ) -> Result<TestConnectionResponse, String> {
        let connection = self.get_connection(connection_id).await?;

        // Update status to testing
        self.update_status(connection_id, ConnectionStatus::Testing)
            .await?;

        // Make HTTP request to endpoint
        let client = reqwest::Client::builder()
            .timeout(Duration::from_secs(10))
            .build()
            .map_err(|e| format!("Failed to create HTTP client: {}", e))?;

        let start = std::time::Instant::now();
        let result = match client.get(&connection.endpoint).send().await {
            Ok(response) => {
                let latency = start.elapsed().as_millis() as u64;
                let status_code = response.status().as_u16();

                // Update status to connected
                self.update_status(connection_id, ConnectionStatus::Connected)
                    .await?;

                TestConnectionResponse {
                    success: true,
                    latency_ms: Some(latency),
                    status_code: Some(status_code),
                    error: None,
                }
            }
            Err(e) => {
                // Update status to failed
                self.update_status(connection_id, ConnectionStatus::Failed)
                    .await?;

                TestConnectionResponse {
                    success: false,
                    latency_ms: None,
                    status_code: None,
                    error: Some(e.to_string()),
                }
            }
        };

        Ok(result)
    }

    /// Delete a connection
    pub async fn delete_connection(&self, connection_id: &str) -> Result<(), String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = r#"DELETE FROM synapse_connections WHERE connection_id = ?"#;

        sqlx::query(query)
            .bind(connection_id)
            .execute(&db.write_pool)
            .await
            .map_err(|e| format!("Failed to delete connection: {}", e))?;

        Ok(())
    }

    /// Update credentials for an existing connection
    pub async fn update_credentials(
        &self,
        connection_id: &str,
        new_credentials: HashMap<String, String>,
    ) -> Result<(), String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // Encrypt new credentials
        let encrypted_creds = self
            .encryption
            .encrypt_credentials(&new_credentials)
            .await?;

        let query = r#"
            UPDATE synapse_connections
            SET credentials_encrypted = ?
            WHERE connection_id = ?
        "#;

        sqlx::query(query)
            .bind(&encrypted_creds)
            .bind(connection_id)
            .execute(&db.write_pool)
            .await
            .map_err(|e| format!("Failed to update credentials: {}", e))?;

        Ok(())
    }
}

