/// Credential Encryption for Synapse Integrations
///
/// Uses AES-256-GCM for encrypting sensitive credentials before storage
use aes_gcm::{
    aead::{Aead, KeyInit, OsRng},
    Aes256Gcm, Nonce,
};
use bincode::{config::standard, Decode, Encode};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::sync::Arc;

use crate::key_manager::SecureKeyManager;

const NONCE_SIZE: usize = 12; // 96 bits for GCM

#[derive(Debug, Clone, Serialize, Deserialize, Encode, Decode)]
pub struct EncryptedCredentials {
    pub nonce: Vec<u8>,
    pub ciphertext: Vec<u8>,
}

pub struct CredentialEncryption {
    key_manager: Arc<SecureKeyManager>,
}

impl CredentialEncryption {
    pub fn new(key_manager: Arc<SecureKeyManager>) -> Self {
        Self { key_manager }
    }

    /// Encrypt credentials before storing in database
    pub async fn encrypt_credentials(
        &self,
        credentials: &HashMap<String, String>,
    ) -> Result<Vec<u8>, String> {
        // Serialize credentials to JSON
        let json_data = serde_json::to_string(credentials)
            .map_err(|e| format!("Failed to serialize credentials: {}", e))?;

        // Get encryption key from KeyManager
        let key_bytes = self
            .key_manager
            .get_vault_key()
            .await
            .map_err(|e| format!("Failed to get encryption key: {}", e))?;

        // Create cipher
        let cipher = Aes256Gcm::new_from_slice(&key_bytes)
            .map_err(|e| format!("Failed to create cipher: {}", e))?;

        // Generate random nonce
        let mut nonce_bytes = vec![0u8; NONCE_SIZE];
        use aes_gcm::aead::rand_core::RngCore;
        OsRng.fill_bytes(&mut nonce_bytes);
        let nonce = Nonce::clone_from_slice(&nonce_bytes);

        // Encrypt
        let ciphertext = cipher
            .encrypt(&nonce, json_data.as_bytes())
            .map_err(|e| format!("Encryption failed: {}", e))?;

        // Combine nonce + ciphertext for storage
        let encrypted = EncryptedCredentials {
            nonce: nonce_bytes,
            ciphertext,
        };

        // Serialize to bytes
        bincode::encode_to_vec(&encrypted, standard())
            .map_err(|e| format!("Failed to serialize encrypted data: {}", e))
    }

    /// Decrypt credentials from database
    pub async fn decrypt_credentials(
        &self,
        encrypted_data: &[u8],
    ) -> Result<HashMap<String, String>, String> {
        // Deserialize encrypted blob
        let (encrypted, _): (EncryptedCredentials, usize) =
            bincode::decode_from_slice(encrypted_data, standard())
                .map_err(|e| format!("Failed to deserialize encrypted data: {}", e))?;

        // Get decryption key from KeyManager
        let key_bytes = self
            .key_manager
            .get_vault_key()
            .await
            .map_err(|e| format!("Failed to get decryption key: {}", e))?;

        // Create cipher
        let cipher = Aes256Gcm::new_from_slice(&key_bytes)
            .map_err(|e| format!("Failed to create cipher: {}", e))?;

        let nonce = Nonce::clone_from_slice(&encrypted.nonce);

        // Decrypt
        let plaintext = cipher
            .decrypt(&nonce, encrypted.ciphertext.as_ref())
            .map_err(|e| format!("Decryption failed: {}", e))?;

        // Deserialize JSON
        let json_str = String::from_utf8(plaintext).map_err(|e| format!("Invalid UTF-8: {}", e))?;

        serde_json::from_str(&json_str)
            .map_err(|e| format!("Failed to deserialize credentials: {}", e))
    }

    /// Validate that credentials can be encrypted and decrypted
    pub async fn validate_encryption_roundtrip(
        &self,
        credentials: &HashMap<String, String>,
    ) -> Result<bool, String> {
        let encrypted = self.encrypt_credentials(credentials).await?;
        let decrypted = self.decrypt_credentials(&encrypted).await?;

        Ok(decrypted == *credentials)
    }
}

