/**
 * Tool Closet Module - Phase 3
 *
 * Provides tool registry and assignment system for AI conferences.
 * Agents receive assigned tools (Tauri commands, MCP servers) based on their role.
 *
 * @module core::tool_closet
 * @version 1.0.0
 */
use serde::{Deserialize, Serialize};
use uuid::Uuid;

use super::conference::{ConferenceType, ParticipantRole};

// ============================================================================
// TOOL TYPES
// ============================================================================

/// Type of tool
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
#[serde(rename_all = "snake_case")]
pub enum ToolType {
    /// Tauri command (e.g., "alden_tasks_create")
    TauriCommand { command_name: String },
    /// MCP server endpoint
    McpServer { server_id: String, endpoint: String },
    /// External API endpoint
    ApiEndpoint { url: String, method: String },
}

/// Tool capability/permission level
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
#[serde(rename_all = "snake_case")]
pub enum ToolCapability {
    /// Read-only operations
    ReadOnly,
    /// Write/modify operations
    Write,
    /// Administrative operations
    Admin,
    /// Execution/action operations
    Execute,
}

/// Tool category for organization
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
#[serde(rename_all = "snake_case")]
pub enum ToolCategory {
    TaskManagement,
    Calendar,
    Email,
    Memory,
    Knowledge,
    Communication,
    Analysis,
    Planning,
    Research,
    Security,
    System,
    Custom,
}

/// Tool entity in the registry
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Tool {
    /// Unique tool identifier
    pub tool_id: String,
    /// Human-readable tool name
    pub name: String,
    /// Tool description
    pub description: String,
    /// Type of tool
    pub tool_type: ToolType,
    /// Required capability level
    pub required_capability: ToolCapability,
    /// Tool category
    pub category: ToolCategory,
    /// Whether tool is enabled
    pub enabled: bool,
    /// Tags for searchability
    pub tags: Vec<String>,
    /// When tool was registered
    pub created_at: String,
}

impl Tool {
    pub fn new(
        name: String,
        description: String,
        tool_type: ToolType,
        category: ToolCategory,
    ) -> Self {
        Self {
            tool_id: Uuid::new_v4().to_string(),
            name,
            description,
            tool_type,
            required_capability: ToolCapability::ReadOnly,
            category,
            enabled: true,
            tags: Vec::new(),
            created_at: chrono::Utc::now().to_rfc3339(),
        }
    }

    /// Create a tool from a Tauri command
    pub fn from_tauri_command(
        command_name: &str,
        description: &str,
        category: ToolCategory,
    ) -> Self {
        let tool_type = ToolType::TauriCommand {
            command_name: command_name.to_string(),
        };

        Self::new(
            command_name.to_string(),
            description.to_string(),
            tool_type,
            category,
        )
    }
}

// ============================================================================
// TOOL ASSIGNMENT
// ============================================================================

/// Tool assignment to a conference participant
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ToolAssignment {
    /// Unique assignment identifier
    pub assignment_id: String,
    /// Conference ID
    pub conference_id: String,
    /// Agent/participant ID
    pub agent_id: String,
    /// Tool ID
    pub tool_id: String,
    /// When assigned
    pub assigned_at: String,
    /// Usage count
    pub usage_count: i32,
    /// Last used timestamp
    pub last_used: Option<String>,
}

impl ToolAssignment {
    pub fn new(conference_id: String, agent_id: String, tool_id: String) -> Self {
        Self {
            assignment_id: Uuid::new_v4().to_string(),
            conference_id,
            agent_id,
            tool_id,
            assigned_at: chrono::Utc::now().to_rfc3339(),
            usage_count: 0,
            last_used: None,
        }
    }
}

