/// Ollama Client for local inference
pub struct OllamaClient {
    base_url: String,
    model: String,
    client: reqwest::Client,
}

impl OllamaClient {
    pub fn new(base_url: String, model: String) -> Self {
        Self {
            base_url,
            model,
            client: reqwest::Client::new(),
        }
    }

    pub async fn generate_embedding(&self, prompt: &str) -> Result<Vec<f64>, String> {
        let url = format!("{}/api/embeddings", self.base_url);
        let body = serde_json::json!({
            "model": self.model,
            "prompt": prompt
        });

        let res = self
            .client
            .post(&url)
            .json(&body)
            .send()
            .await
            .map_err(|e| format!("Ollama request failed: {}", e))?;

        if !res.status().is_success() {
            return Err(format!("Ollama API error: {}", res.status()));
        }

        let response_json: serde_json::Value = res
            .json()
            .await
            .map_err(|e| format!("Failed to parse Ollama response: {}", e))?;

        let embedding = response_json["embedding"]
            .as_array()
            .ok_or("Missing 'embedding' field in response")?
            .iter()
            .map(|v| v.as_f64().unwrap_or(0.0))
            .collect();

        Ok(embedding)
    }
}

/// RAG Engine manager
pub struct RagEngine {
    db: Arc<RwLock<Option<DatabaseManager>>>,
