        assert!(!filtered.as_object().unwrap().contains_key("alice_secrets"));
        assert!(!filtered.as_object().unwrap().contains_key("alice_private"));

        // Should keep shared context
        assert!(filtered.as_object().unwrap().contains_key("shared_context"));

        // Should add routing metadata with zero-trust level
        let routing_meta = filtered.get("routing_metadata").unwrap();
        assert_eq!(routing_meta.get("trust_level").unwrap(), "zero_trust");
    }
}

