/**
 * Core Workflow Orchestration Module
 *
 * Dynamic agentic workflow system supporting multiple execution patterns:
 * - Sequential: Linear task execution
 * - Parallel: Concurrent independent tasks
 * - Loop/Iterative: Repeated execution with conditions
 * - Conditional: Branching logic (if/else)
 * - State Machine: Rule-based state transitions
 * - Rules-Driven: Complex decision trees
 * - Collaborative: Multi-agent coordination
 *
 * @module core::workflow
 * @version 1.0.0
 */
use serde::{Deserialize, Serialize};
use std::collections::HashMap;

/// Workflow execution patterns
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
#[serde(rename_all = "snake_case")]
pub enum WorkflowPattern {
    /// Tasks execute one after another (A Æ’+' B Æ’+' C)
    Sequential,
    /// Independent tasks run concurrently (A || B || C)
    Parallel,
    /// Tasks repeat until condition is met (loop { A, B } until X)
    Loop {
        /// Maximum iterations to prevent infinite loops
        max_iterations: usize,
        /// Condition to check after each iteration (JSON expression)
        exit_condition: String,
    },
    /// Conditional branching (if X then A else B)
    Conditional {
        /// Condition to evaluate (JSON expression)
        condition: String,
        /// Workflow to execute if true
        on_true: Box<WorkflowPattern>,
        /// Workflow to execute if false
        on_false: Box<WorkflowPattern>,
    },
    /// State machine with transitions
    StateMachine {
        /// Initial state
        initial_state: String,
        /// State transition rules
        transitions: Vec<StateTransition>,
    },
    /// Rules-driven execution
    RulesDriven {
        /// Rule set to evaluate
        rules: Vec<ExecutionRule>,
        /// Default action if no rules match
        default_action: String,
    },
    /// Collaborative multi-agent workflow
    Collaborative {
        /// Coordination strategy
        strategy: CollaborationStrategy,
    },
}

/// State transition in a state machine
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
pub struct StateTransition {
    /// Source state
    pub from_state: String,
    /// Destination state
    pub to_state: String,
    /// Event that triggers transition
    pub event: String,
    /// Optional condition (JSON expression)
    pub condition: Option<String>,
    /// Action to execute during transition
    pub action: Option<String>,
}

/// Execution rule for rules-driven workflows
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
pub struct ExecutionRule {
    /// Rule identifier
    pub rule_id: String,
    /// Rule name
    pub name: String,
    /// Condition (JSON expression)
    pub condition: String,
    /// Action to execute if condition is true
    pub action: String,
    /// Priority (higher = evaluated first)
    pub priority: i32,
}

/// Collaboration strategies for multi-agent workflows
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
#[serde(rename_all = "snake_case")]
pub enum CollaborationStrategy {
    /// Agents vote on decisions (majority wins)
    Voting {
        /// Minimum votes required
        quorum: usize,
    },
    /// Agents work in parallel, results are merged
    ParallelMerge {
        /// Merge strategy (concat, average, max, custom)
        merge_strategy: String,
    },
    /// Round-robin: agents take turns
    RoundRobin,
    /// Leader-follower: one agent coordinates others
    LeaderFollower {
        /// Agent ID of leader
        leader: String,
    },
    /// Consensus: all agents must agree
    Consensus {
        /// Timeout in seconds
        timeout: u64,
    },
}

/// Workflow step definition
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct WorkflowStep {
    /// Step identifier
    pub step_id: String,
    /// Step name
    pub name: String,
    /// Description of what this step does
    pub description: String,
    /// Agent assigned to execute this step
    pub assigned_agent: String,
    /// Tools required for this step
    pub required_tools: Vec<String>,
    /// Input parameters (JSON schema)
    pub inputs: HashMap<String, serde_json::Value>,
    /// Expected outputs (JSON schema)
    pub outputs: HashMap<String, serde_json::Value>,
    /// Execution timeout in seconds
    pub timeout: Option<u64>,
    /// Retry configuration
    pub retry: Option<RetryConfig>,
}

/// Retry configuration for workflow steps
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct RetryConfig {
    /// Maximum retry attempts
    pub max_attempts: usize,
    /// Delay between retries in seconds
    pub delay_seconds: u64,
    /// Backoff strategy (linear, exponential, fixed)
    pub backoff_strategy: String,
}

/// Complete workflow definition
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Workflow {
    /// Workflow identifier
    pub workflow_id: String,
    /// Workflow name
    pub name: String,
    /// Description
    pub description: String,
    /// Execution pattern
    pub pattern: WorkflowPattern,
    /// Workflow steps
    pub steps: Vec<WorkflowStep>,
    /// Conference this workflow belongs to
    pub conference_id: Option<String>,
    /// Current execution state
    pub state: WorkflowState,
    /// Created timestamp
    pub created_at: String,
    /// Started timestamp
    pub started_at: Option<String>,
    /// Completed timestamp
    pub completed_at: Option<String>,
}

/// Workflow execution state
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
#[serde(rename_all = "snake_case")]
pub enum WorkflowState {
    /// Workflow is defined but not started
    Pending,
    /// Workflow is currently executing
    Running,
    /// Workflow is paused
    Paused,
    /// Workflow completed successfully
    Completed,
    /// Workflow failed
    Failed {
