    pub step_id: String,
    /// Agent that executed it
    pub agent_id: String,
    /// Execution state
    pub state: StepExecutionState,
    /// Input data
    pub inputs: HashMap<String, serde_json::Value>,
    /// Output data
    pub outputs: HashMap<String, serde_json::Value>,
    /// Started timestamp
    pub started_at: String,
    /// Completed timestamp
    pub completed_at: Option<String>,
    /// Error message if failed
    pub error: Option<String>,
    /// Retry count
    pub retry_count: usize,
}

/// Step execution state
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
#[serde(rename_all = "snake_case")]
pub enum StepExecutionState {
    Pending,
    Running,
    Completed,
    Failed,
    Skipped,
}

#[allow(dead_code)]
impl Workflow {
    /// Create a new workflow
    pub fn new(
        name: String,
        description: String,
        pattern: WorkflowPattern,
        conference_id: Option<String>,
    ) -> Self {
        Self {
            workflow_id: uuid::Uuid::new_v4().to_string(),
            name,
            description,
            pattern,
            steps: Vec::new(),
            conference_id,
            state: WorkflowState::Pending,
            created_at: chrono::Utc::now().to_rfc3339(),
            started_at: None,
            completed_at: None,
        }
    }

    /// Add a step to the workflow
    pub fn add_step(&mut self, step: WorkflowStep) {
        self.steps.push(step);
    }

    /// Start workflow execution
    pub fn start(&mut self) -> Result<(), String> {
        if self.state != WorkflowState::Pending {
            return Err(format!("Cannot start workflow in state {:?}", self.state));
        }

        if self.steps.is_empty() {
            return Err("Cannot start workflow with no steps".to_string());
        }

        self.state = WorkflowState::Running;
        self.started_at = Some(chrono::Utc::now().to_rfc3339());
        Ok(())
    }

    /// Pause workflow execution
    pub fn pause(&mut self) -> Result<(), String> {
        if self.state != WorkflowState::Running {
            return Err(format!("Cannot pause workflow in state {:?}", self.state));
        }

        self.state = WorkflowState::Paused;
        Ok(())
    }

    /// Resume workflow execution
    pub fn resume(&mut self) -> Result<(), String> {
        if self.state != WorkflowState::Paused {
            return Err(format!("Cannot resume workflow in state {:?}", self.state));
        }

        self.state = WorkflowState::Running;
        Ok(())
    }

    /// Complete workflow execution
    pub fn complete(&mut self) -> Result<(), String> {
        if self.state != WorkflowState::Running {
            return Err(format!(
                "Cannot complete workflow in state {:?}",
                self.state
            ));
        }

        self.state = WorkflowState::Completed;
        self.completed_at = Some(chrono::Utc::now().to_rfc3339());
        Ok(())
    }

    /// Fail workflow execution
    pub fn fail(&mut self, error: String, failed_step: String) -> Result<(), String> {
        self.state = WorkflowState::Failed { error, failed_step };
        self.completed_at = Some(chrono::Utc::now().to_rfc3339());
        Ok(())
    }
}

