{"command":"$path = 'docs/04_data/imported/appendix_f_end_to_end_data_flow_examples.md'; $lines = Get-Content $path;
# Update step 8 text
for ($i=0; $i -lt $lines.Count; $i++) {
  if ($lines[$i] -match '^8\. \*\*Vault Writeback:\*\*') {
    $lines[$i+1] = '   T+1.1s ƒ?" Core writes approved results to Vault (e.g., session summary to communal memory). If write permission is not granted, the result is stored in the quarantine ledger for review.'
  }
}
# Update permission boundaries to mention quarantine
for ($i=0; $i -lt $lines.Count; $i++) {
  if ($lines[$i] -eq '**Permission Boundaries:**') {
    if ($lines[$i+2] -match 'All events logged by Sentry') {
      $lines[$i+2] = '- All events logged by Sentry; unapproved writes remain in quarantine until user review.'
    }
    break
  }
}
# Update incident flow to mention quarantine ledger
for ($i=0; $i -lt $lines.Count; $i++) {
  if ($lines[$i] -match '\*\*If terminate:\*\*') {
    $lines[$i] = '   - **If terminate:** Sentry force-kills process, quarantines changes. Vault rolls back to last safe state if write was involved. Quarantined items remain in the ledger until review or retention purge. User can always restore/rollback via UI or admin panel.'
    break
  }
}
Set-Content -Path $path -Value $lines -Encoding utf8","workdir":"g:\MythologIQ\Hearthlink"}

{"command":"$path = 'docs/hearthlink_system_documentation_master.md'; $lines = Get-Content $path; $replacements = @{
'Appendix D: Technical Product Requirements Document (Technical PRD)' = 'Appendix D: Technical Product Requirements Document (Technical PRD) (Updated 2025-12-31 by Codex)';
'Appendix E: Model Context Protocol (MCP) Specification' = 'Appendix E: Model Context Protocol (MCP) Specification (Updated 2025-12-31 by Codex)';
'Appendix F: End-to-End Data Flow Examples' = 'Appendix F: End-to-End Data Flow Examples (Updated 2025-12-31 by Codex)';
};
$lines = $lines | ForEach-Object { $line = $_; foreach ($k in $replacements.Keys) { if ($line -eq "- **$k**" -or $line -eq "- $k") { $line = $line -replace [regex]::Escape($k), $replacements[$k]; } } $line };
Set-Content -Path $path -Value $lines -Encoding utf8","workdir":"g:\MythologIQ\Hearthlink"}

{"command":"$path = 'docs/hearthlink_system_documentation_master.md'; $lines = Get-Content $path; $lines = $lines | ForEach-Object {
  $_ = $_ -replace '- \*\*Appendix D\*\*: Technical Product Requirements Document \(Technical PRD\)', '- **Appendix D**: Technical Product Requirements Document (Technical PRD) (Updated 2025-12-31 by Codex)';
  $_ = $_ -replace '- \*\*Appendix E\*\*: Model Context Protocol \(MCP\) Specification', '- **Appendix E**: Model Context Protocol (MCP) Specification (Updated 2025-12-31 by Codex)';
  $_ = $_ -replace '- \*\*Appendix F\*\*: End-to-End Data Flow Examples', '- **Appendix F**: End-to-End Data Flow Examples (Updated 2025-12-31 by Codex)';
  $_
}; Set-Content -Path $path -Value $lines -Encoding utf8","workdir":"g:\MythologIQ\Hearthlink"}

{"command":"$path = 'docs/04_data/imported/appendix_f_end_to_end_data_flow_examples.md'; $lines = Get-Content $path;
for ($i=0; $i -lt $lines.Count; $i++) {
  if ($lines[$i] -match '^8\. \*\*Vault Writeback:\*\*') {
    $lines[$i+1] = '   T+1.1s - Core writes approved results to Vault (e.g., session summary to communal memory). If write permission is not granted, the result is stored in the quarantine ledger for review.'
