fn safe_join(base: &Path, user_path: &str) -> Result<PathBuf, String> {
    let path = base.join(user_path);

    // Ensure the path is within base directory
    let canonical = path.canonicalize()
        .map_err(|_| "Invalid path".to_string())?;

