async fn alice_start_deep_work_internal(
    db: &DatabaseManager,
    request: &StartDeepWorkRequest,
) -> Result<AliceDeepWorkSession, String> {
    // Check if there's already an active session
    let check_query = r#"
        SELECT session_id FROM alice_deep_work_sessions
        WHERE user_id = ? AND status = 'active'
        LIMIT 1
    "#;

    let active_exists = sqlx::query(check_query)
        .bind(&request.user_id)
        .fetch_optional(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to check active sessions: {}", e))?;

    if active_exists.is_some() {
        return Err("An active Deep Work session already exists".to_string());
    }

    let session_id = format!("deepwork_{}", chrono::Utc::now().timestamp_millis());
    let created_at = chrono::Utc::now().to_rfc3339();
    let start_time = created_at.clone();

    let query = r#"
        INSERT INTO alice_deep_work_sessions (
            session_id, user_id, start_time, goal, status, created_at
        ) VALUES (?, ?, ?, ?, ?, ?)
    "#;

    sqlx::query(query)
        .bind(&session_id)
        .bind(&request.user_id)
        .bind(&start_time)
        .bind(&request.goal)
        .bind("active")
        .bind(&created_at)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to start Deep Work session: {}", e))?;

    Ok(AliceDeepWorkSession {
        session_id,
        user_id: request.user_id.clone(),
        start_time,
        end_time: None,
        duration_seconds: None,
        goal: request.goal.clone(),
        status: "active".to_string(),
        created_at,
    })
}

async fn alice_stop_deep_work_internal(
    db: &DatabaseManager,
    request: &StopDeepWorkRequest,
) -> Result<AliceDeepWorkSession, String> {
    validate_stop_status(&request.status)?;

    // Get the active session
    let get_query = r#"
        SELECT session_id, user_id, start_time, end_time, duration_seconds, goal, status, created_at
        FROM alice_deep_work_sessions
        WHERE user_id = ? AND status = 'active'
        ORDER BY created_at DESC
        LIMIT 1
    "#;

    let session_row = sqlx::query_as::<_, AliceDeepWorkSession>(get_query)
        .bind(&request.user_id)
        .fetch_optional(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to fetch active session: {}", e))?
        .ok_or("No active Deep Work session found")?;

    // Calculate duration
    let end_time = chrono::Utc::now();
    let start_time = chrono::DateTime::parse_from_rfc3339(&session_row.start_time)
        .map_err(|e| format!("Invalid start time format: {}", e))?
        .with_timezone(&chrono::Utc);

    let duration = end_time
        .signed_duration_since(start_time)
        .num_seconds()
        .max(0);
    let end_time_str = end_time.to_rfc3339();

    let update_query = r#"
        UPDATE alice_deep_work_sessions
        SET end_time = ?, duration_seconds = ?, status = ?
        WHERE session_id = ?
    "#;

    sqlx::query(update_query)
        .bind(&end_time_str)
        .bind(duration)
        .bind(&request.status)
        .bind(&session_row.session_id)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to stop Deep Work session: {}", e))?;

    Ok(AliceDeepWorkSession {
        session_id: session_row.session_id,
        user_id: session_row.user_id,
        start_time: session_row.start_time,
        end_time: Some(end_time_str),
        duration_seconds: Some(duration),
        goal: session_row.goal,
        status: request.status.clone(),
        created_at: session_row.created_at,
    })
}

