/**
 * Task Manager Component
 *
 * Comprehensive task management interface with:
 * - Task list with filtering
 * - Task creation and editing
 * - Priority and status management
 * - Analytics dashboard
 *
 * @version 1.0.0
 * @date 2025-10-10
 */

import React, { useState, useCallback, useEffect } from 'react';
import confetti from 'canvas-confetti';
import { toast } from 'sonner';

import type { Task, CreateTaskRequest, TaskUpdates } from '../../types/agents';
import { useAldenBridge } from '../../hooks/useBridge';

import { LoadingSpinner } from '../shared/LoadingSpinner';
import { RecurringTaskDialog } from './RecurringTaskDialog';

export const TaskManager: React.FC = () => {
  const { bridge, loading, error } = useAldenBridge();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingTask, setEditingTask] = useState<Task | null>(null);
  const [filter, setFilter] = useState<'all' | 'todo' | 'in-progress' | 'completed'>('all');
  const [operationLoading, setOperationLoading] = useState(false);
  const [operationError, setOperationError] = useState<string | null>(null);
  const [recurringTask, setRecurringTask] = useState<Task | null>(null);
  const [showRecurringDialog, setShowRecurringDialog] = useState(false);

  const loadTasks = useCallback(async () => {
    if (!bridge) return;
    try {
      setOperationError(null);
      const result = await bridge.getTasks();
      if (result.success && result.data) {
        setTasks(result.data);
      } else {
        setOperationError(!result.success ? result.error : 'Failed to load tasks');
      }
    } catch (err) {
      setOperationError(err instanceof Error ? err.message : 'Failed to load tasks');
    }
  }, [bridge]);

  useEffect(() => {
    loadTasks();
  }, [loadTasks]);

  const handleCreateTask = async (taskData: CreateTaskRequest) => {
    if (!bridge) return;

    setOperationLoading(true);
    setOperationError(null);

    try {
      const result = await bridge.createTask(taskData);
      if (result.success) {
        setShowCreateForm(false);
        await loadTasks();
      } else {
        setOperationError(result.error || 'Failed to create task');
      }
    } catch (err) {
      setOperationError(err instanceof Error ? err.message : 'Failed to create task');
    } finally {
      setOperationLoading(false);
    }
  };

  const handleUpdateTask = async (taskId: string, updates: TaskUpdates) => {
    if (!bridge) return;

    setOperationLoading(true);
    setOperationError(null);

    try {
      const result = await bridge.updateTask(taskId, updates);
      if (result.success) {
        // Check if this is a completion update
        const taskBeingUpdated = tasks.find(t => t.id === taskId);
        if (updates.status === 'completed' && taskBeingUpdated) {
          triggerCompletionCelebration(taskBeingUpdated);
        }

        setEditingTask(null);
        await loadTasks();
      } else {
        setOperationError(result.error || 'Failed to update task');
      }
    } catch (err) {
      setOperationError(err instanceof Error ? err.message : 'Failed to update task');
    } finally {
      setOperationLoading(false);
    }
  };

  const triggerCompletionCelebration = (task: Task) => {
    // Celebration messages (dopamine boost)
    const celebrationMessages = [
      'Great work! You crushed it!',
      'Task completed! Keep up the momentum!',
      'Nice! One step closer to your goals!',
      "You're absolutely crushing it!",
      'Fantastic! Task complete!',
      'Victory! Task marked done!',
      'Yes! Another one down!',
      'Awesome effort! Task complete!',
    ];

    // Select random message
    const randomMessage = celebrationMessages[
      Math.floor(Math.random() * celebrationMessages.length)
    ];

    // Trigger confetti animation
    try {
      confetti({
        particleCount: 100,
        spread: 70,
        origin: { y: 0.6 }, // Center-bottom of screen
        gravity: 1.2,
        decay: 0.94,
        ticks: 200,
      });
    } catch (err) {
      console.warn('Confetti animation failed:', err);
    }

    // Show success toast with task title
    toast.success(randomMessage, {
      description: `"${task.title}" completed`,
      duration: 3000,
    });

    // Optional: Play success sound (graceful fallback if unavailable)
    try {
      const audio = new Audio('/sounds/success.mp3');
      audio.volume = 0.3; // Not too loud to avoid startling
      audio.play().catch(() => {
        // Silently ignore audio play errors
      });
    } catch (err) {
      // Silently ignore any audio initialization errors
    }
  };

  const handleDeleteTask = async (taskId: string) => {
    if (!bridge) return;

    if (!confirm('Are you sure you want to delete this task?')) {
      return;
    }

    setOperationLoading(true);
    setOperationError(null);

    try {
      const result = await bridge.deleteTask(taskId);
      if (result.success) {
        await loadTasks();
      } else {
        setOperationError(result.error || 'Failed to delete task');
      }
    } catch (err) {
      setOperationError(err instanceof Error ? err.message : 'Failed to delete task');
    } finally {
      setOperationLoading(false);
    }
  };

  const handleRecurringSuccess = async () => {
    await loadTasks();
    setShowRecurringDialog(false);
    setRecurringTask(null);
  };

  const filteredTasks = filter === 'all'
    ? tasks
    : tasks.filter(task => task.status === filter);

  if (loading) {
    return (
      <div className="flex items-center justify-center p-8">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (error) {
    return <div className="p-4 text-red-600">Error: {error.message}</div>;
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Task Manager</h2>
        <div className="flex space-x-2">
          <button
            onClick={loadTasks}
            disabled={operationLoading}
            className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-500 disabled:opacity-50 disabled:cursor-not-allowed"
            aria-label="Refresh tasks"
          >
            {operationLoading ? 'Refreshing...' : 'Refresh'}
          </button>
          <button
            onClick={() => setShowCreateForm(true)}
            disabled={operationLoading}
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            + New Task
          </button>
        </div>
      </div>

      {/* Error Display */}
      {operationError && (
        <div className="p-4 bg-red-50 border border-red-200 rounded-lg flex items-start space-x-3">
          <svg className="w-5 h-5 text-red-600 mt-0.5" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
          </svg>
          <div className="flex-1">
            <p className="text-red-800 font-medium">Operation Failed</p>
            <p className="text-red-600 text-sm">{operationError}</p>
          </div>
          <button
            onClick={() => setOperationError(null)}
            className="text-red-600 hover:text-red-800"
            aria-label="Dismiss error"
          >
            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
            </svg>
          </button>
        </div>
      )}

      {/* Loading Indicator */}
      {operationLoading && (
        <div className="flex items-center space-x-2 text-blue-600">
          <LoadingSpinner size="sm" />
          <span>Processing...</span>
        </div>
      )}

      {/* Filter */}
      <div className="flex space-x-2">
        {(['all', 'todo', 'in-progress', 'completed'] as const).map((filterOption) => (
          <button
            key={filterOption}
            onClick={() => setFilter(filterOption)}
            className={`px-4 py-2 rounded ${
              filter === filterOption
                ? 'bg-blue-600 text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            {filterOption === 'all' ? 'All' : filterOption.replace('-', ' ')}
