      const result = await bridge.syncEmailAccount(selectedAccountId);

      if (result.success) {
        setSuccessMessage(`Synced ${result.data} new messages`);
        setTimeout(() => setSuccessMessage(null), 3000);
        await loadMessages();
      } else {
        setError(result.error || 'Failed to sync account');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to sync account');
    } finally {
      setSyncing(false);
    }
  };

  const handleComposeSent = async () => {
    setShowCompose(false);
    setReplyToMessage(null);
    setSuccessMessage('Email sent successfully!');
    setTimeout(() => setSuccessMessage(null), 3000);
    await loadMessages();
  };

  const handleAccountSetupSuccess = async () => {
    await loadAccounts();
    setShowAccountSetup(false);
  };

  if (loading && accounts.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center p-8 gap-4">
        <LoadingSpinner size="lg" />
        <div className="text-gray-400">Loading email...</div>
      </div>
    );
  }

  if (accounts.length === 0 && !loading) {
    return (
      <div className="p-6 space-y-4">
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
          <p className="text-blue-800">No email accounts configured yet.</p>
          <p className="text-blue-600 text-sm mt-2">
            Add an email account to get started with your inbox.
          </p>
          <button
            onClick={() => setShowAccountSetup(true)}
            className="mt-4 px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors"
          >
            + Add Account
          </button>
        </div>
        <EmailAccountSetup
          isOpen={showAccountSetup}
          onClose={() => setShowAccountSetup(false)}
          onSuccess={handleAccountSetupSuccess}
        />
      </div>
    );
  }

  // Render message item
  const renderMessageItem = (message: EmailMessage, index: number) => {
    const isSelected = selectedMessage?.id === message.id;

    return (
      <div
        key={message.id}
        onClick={() => {
          setSelectedMessage(message);
          setSelectedIndex(index);
        }}
        onKeyDown={(e) => {
          if (e.key === 'Enter' || e.key === ' ') {
            e.preventDefault();
            setSelectedMessage(message);
            setSelectedIndex(index);
          }
        }}
        tabIndex={0}
        role="option"
        aria-selected={isSelected}
        className={`p-4 border rounded cursor-pointer transition-colors ${
          isSelected
            ? 'bg-blue-50 border-blue-300 ring-2 ring-blue-500'
            : 'bg-white border-gray-200 hover:bg-gray-50'
        } ${!message.read ? 'border-l-4 border-l-blue-600' : ''}`}
      >
        <div className="flex items-start justify-between">
          <div className="flex-1 min-w-0">
            <div className="flex items-center space-x-2">
              {/* Star indicator */}
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  handleToggleStar(message.id);
                }}
                className={`p-1 rounded hover:bg-gray-100 ${message.starred ? 'text-yellow-500' : 'text-gray-300'}`}
                aria-label={message.starred ? 'Remove star' : 'Add star'}
              >
                <svg className="w-4 h-4" fill={message.starred ? 'currentColor' : 'none'} stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11.049 2.927c.3-.921 1.603-.921 1.902 0l1.519 4.674a1 1 0 00.95.69h4.915c.969 0 1.371 1.24.588 1.81l-3.976 2.888a1 1 0 00-.363 1.118l1.518 4.674c.3.922-.755 1.688-1.538 1.118l-3.976-2.888a1 1 0 00-1.176 0l-3.976 2.888c-.783.57-1.838-.197-1.538-1.118l1.518-4.674a1 1 0 00-.363-1.118l-3.976-2.888c-.784-.57-.38-1.81.588-1.81h4.914a1 1 0 00.951-.69l1.519-4.674z" />
                </svg>
              </button>
              <h3 className={`text-sm ${!message.read ? 'font-semibold' : 'font-medium'} text-gray-900 truncate`}>
                {highlightSearchMatch(message.from, searchQuery)}
              </h3>
              {message.attachments && message.attachments.length > 0 && (
                <svg className="w-4 h-4 text-gray-500 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20" aria-label="Has attachments">
                  <path fillRule="evenodd" d="M8 4a3 3 0 00-3 3v4a5 5 0 0010 0V7a1 1 0 112 0v4a7 7 0 11-14 0V7a5 5 0 0110 0v4a3 3 0 11-6 0V7a1 1 0 012 0v4a1 1 0 102 0V7a3 3 0 00-3-3z" clipRule="evenodd" />
                </svg>
              )}
            </div>
            <p className="text-sm text-gray-700 truncate mt-1">
              {highlightSearchMatch(message.subject, searchQuery)}
            </p>
            <p className="text-xs text-gray-500 truncate mt-1">
              {highlightSearchMatch(
                message.body ? message.body.substring(0, 80) : 'No preview available',
                searchQuery
              )}...
            </p>
          </div>
          <div className="text-right ml-4 flex-shrink-0">
            <div className="text-xs text-gray-500">
              {new Date(message.received_date || '').toLocaleDateString()}
            </div>
            {!message.read && (
              <div className="w-2 h-2 bg-blue-600 rounded-full mt-2 ml-auto" aria-label="Unread"></div>
            )}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div
      ref={containerRef}
      className="p-6 space-y-4 h-full flex flex-col"
      role="application"
      aria-label="Email inbox"
    >
      {/* Header */}
      <div className="flex justify-between items-center flex-wrap gap-2">
        <h2 className="text-2xl font-bold text-gray-900">Email Inbox</h2>
        <div className="flex space-x-2 flex-wrap gap-2">
          {/* View Mode Toggle */}
          <div className="flex border border-gray-300 rounded-md overflow-hidden">
            <button
              onClick={() => setViewMode('list')}
              className={`px-3 py-2 text-sm transition-colors ${
                viewMode === 'list'
                  ? 'bg-blue-600 text-white'
                  : 'bg-white text-gray-700 hover:bg-gray-50'
              }`}
              aria-label="List view"
              aria-pressed={viewMode === 'list'}
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 10h16M4 14h16M4 18h16" />
          …427 tokens truncated…>
          <button
            onClick={handleSync}
            disabled={!selectedAccountId || syncing || loading}
            className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 disabled:opacity-50 disabled:cursor-not-allowed flex items-center space-x-2"
          >
            {syncing ? (
              <>
                <LoadingSpinner size="sm" />
                <span>Syncing...</span>
              </>
            ) : (
              <span>Sync</span>
            )}
          </button>
          {/* Keyboard shortcuts help button */}
          <button
            onClick={() => setShowShortcutsHelp(prev => !prev)}
            className="px-3 py-2 bg-gray-100 text-gray-700 rounded hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-500"
            aria-label="Show keyboard shortcuts (Press ?)"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.228 9c.549-1.165 2.03-2 3.772-2 2.21 0 4 1.343 4 3 0 1.4-1.278 2.575-3.006 2.907-.542.104-.994.54-.994 1.093m0 3h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
          </button>
        </div>
      </div>

      {/* Keyboard Shortcuts Help Modal */}
      <PremiumModal
        isOpen={showShortcutsHelp}
        onClose={() => setShowShortcutsHelp(false)}
        title="Keyboard Shortcuts"
        size="sm"
      >
        <div className="space-y-1">
          {KEYBOARD_SHORTCUTS.map(({ key, action }) => (
            <div key={key} className="flex justify-between items-center py-2 border-b border-border/40 last:border-0">
              <span className="text-foreground text-sm">{action}</span>
              <kbd className="px-2 py-1 bg-muted border border-border rounded text-xs font-mono text-muted-foreground min-w-[24px] text-center">
                {key}
              </kbd>
            </div>
          ))}
        </div>
      </PremiumModal>

      {/* Account Selector and Search */}
      <div className="flex items-center space-x-4 flex-wrap gap-2">
        <div className="flex items-center space-x-2">
          <label htmlFor="account-select" className="text-sm font-medium text-gray-700">Account:</label>
          <select
            id="account-select"
            value={selectedAccountId}
            onChange={(e) => setSelectedAccountId(e.target.value)}
            disabled={loading}
            className="px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <option value="">Select an account...</option>
            {accounts.map((account) => (
              <option key={account.id} value={account.id || ''}>
                {account.email_address}
              </option>
            ))}
          </select>
        </div>

        {/* Search Bar */}
        <div className="flex-1 max-w-md">
          <div className="relative">
            <input
              type="search"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search emails..."
              className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              aria-label="Search emails"
            />
            <svg
              className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
            {searchQuery && (
              <button
                onClick={() => setSearchQuery('')}
                className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600"
                aria-label="Clear search"
              >
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            )}
          </div>
          {searchQuery && (
            <p className="text-xs text-gray-500 mt-1">
              {filteredMessages.length} result{filteredMessages.length !== 1 ? 's' : ''} found
            </p>
          )}
        </div>
      </div>

      {/* Quick Actions Bar */}
      {selectedMessage && (
        <div className="flex items-center space-x-2 p-2 bg-gray-50 border border-gray-200 rounded-lg" role="toolbar" aria-label="Email actions">
          <button
            onClick={() => handleArchive(selectedMessage.id)}
            className="px-3 py-1.5 text-sm bg-white border border-gray-300 rounded hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500 flex items-center space-x-1"
            aria-label="Archive email"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 8h14M5 8a2 2 0 110-4h14a2 2 0 110 4M5 8v10a2 2 0 002 2h10a2 2 0 002-2V8m-9 4h4" />
            </svg>
            <span>Archive</span>
          </button>

          <button
            onClick={() => handleMarkUnread(selectedMessage.id)}
            className="px-3 py-1.5 text-sm bg-white border border-gray-300 rounded hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500 flex items-center space-x-1"
            aria-label={selectedMessage.read ? 'Mark as unread' : 'Mark as read'}
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
            </svg>
            <span>{selectedMessage.read ? 'Unread' : 'Read'}</span>
          </button>

          <button
            onClick={() => handleToggleStar(selectedMessage.id)}
            className={`px-3 py-1.5 text-sm bg-white border border-gray-300 rounded hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500 flex items-center space-x-1 ${
              selectedMessage.starred ? 'text-yellow-600' : ''
            }`}
            aria-label={selectedMessage.starred ? 'Remove star' : 'Add star'}
          >
            <svg className="w-4 h-4" fill={selectedMessage.starred ? 'currentColor' : 'none'} stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11.049 2.927c.3-.921 1.603-.921 1.902 0l1.519 4.674a1 1 0 00.95.69h4.915c.969 0 1.371 1.24.588 1.81l-3.976 2.888a1 1 0 00-.363 1.118l1.518 4.674c.3.922-.755 1.688-1.538 1.118l-3.976-2.888a1 1 0 00-1.176 0l-3.976 2.888c-.783.57-1.838-.197-1.538-1.118l1.518-4.674a1 1 0 00-.363-1.118l-3.976-2.888c-.784-.57-.38-1.81.588-1.81h4.914a1 1 0 00.951-.69l1.519-4.674z" />
            </svg>
            <span>{selectedMessage.starred ? 'Unstar' : 'Star'}</span>
          </button>

          <div className="relative" ref={folderDropdownRef}>
            <button
              onClick={() => setShowFolderDropdown(prev => !prev)}
              className="px-3 py-1.5 text-sm bg-white border border-gray-300 rounded hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500 flex items-center space-x-1"
              aria-label="Move to folder"
              aria-expanded={showFolderDropdown}
              aria-haspopup="listbox"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2h-6l-2-2H5a2 2 0 00-2 2z" />
              </svg>
              <span>Move</span>
              <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>
            {showFolderDropdown && (
              <div
                className="absolute top-full left-0 mt-1 bg-white border border-gray-200 rounded-md shadow-lg z-10 min-w-[120px]"
                role="listbox"
                aria-label="Select folder"
              >
                {FOLDER_OPTIONS.map(folder => (
                  <button
                    key={folder}
                    onClick={() => handleMoveToFolder(selectedMessage.id, folder)}
                    className="w-full px-3 py-2 text-sm text-left hover:bg-gray-100 first:rounded-t-md last:rounded-b-md"
                    role="option"
                    aria-selected={selectedMessage.folder === folder}
                  >
                    {folder}
                  </button>
                ))}
              </div>
            )}
          </div>

          <div className="border-l border-gray-300 h-6 mx-2" aria-hidden="true"></div>

          <button
            onClick={handleReply}
            className="px-3 py-1.5 text-sm bg-white border border-gray-300 rounded hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500 flex items-center space-x-1"
            aria-label="Reply (Press R)"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h10a8 8 0 018 8v2M3 10l6 6m-6-6l6-6" />
            </svg>
            <span>Reply</span>
          </button>

          <button
            onClick={handleReplyAll}
            className="px-3 py-1.5 text-sm bg-white border border-gray-300 rounded hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500 flex items-center space-x-1"
            aria-label="Reply All (Press A)"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h10a8 8 0 018 8v2M3 10l6 6m-6-6l6-6M7 10h10a8 8 0 018 8v2" />
            </svg>
            <span>Reply All</span>
          </button>

          <button
            onClick={handleForward}
            className="px-3 py-1.5 text-sm bg-white border border-gray-300 rounded hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500 flex items-center space-x-1"
            aria-label="Forward (Press F)"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
            </svg>
            <span>Forward</span>
          </button>

          <button
            onClick={() => handleDeleteEmail(selectedMessage.id)}
            className="px-3 py-1.5 text-sm bg-white border border-red-300 text-red-600 rounded hover:bg-red-50 focus:outline-none focus:ring-2 focus:ring-red-500 flex items-center space-x-1"
            aria-label="Delete (Press Delete)"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
            </svg>
            <span>Delete</span>
          </button>
        </div>
      )}

      {/* Success Message */}
      {successMessage && (
        <div className="p-4 bg-green-50 border border-green-200 rounded-lg flex items-start space-x-3" role="alert">
          <svg className="w-5 h-5 text-green-600 mt-0.5" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
          </svg>
          <div className="flex-1">
            <p className="text-green-800 font-medium">{successMessage}</p>
          </div>
          <button
            onClick={() => setSuccessMessage(null)}
            className="text-green-600 hover:text-green-800"
            aria-label="Dismiss"
          >
            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
            </svg>
          </button>
        </div>
      )}

      {/* Error Message */}
      {error && (
        <div className="p-4 bg-red-50 border border-red-200 rounded-lg flex items-start space-x-3" role="alert">
          <svg className="w-5 h-5 text-red-600 mt-0.5" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
          </svg>
          <div className="flex-1">
            <p className="text-red-800 font-medium">Error</p>
            <p className="text-red-600 text-sm">{error}</p>
          </div>
          <button
            onClick={() => setError(null)}
            className="text-red-600 hover:text-red-800"
            aria-label="Dismiss"
          >
            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
            </svg>
          </button>
        </div>
      )}

      {/* Loading Indicator */}
      {loading && (
        <div className="flex items-center space-x-2 text-blue-600">
          <LoadingSpinner size="sm" />
          <span>Loading messages...</span>
        </div>
      )}

      {/* Main Content Area */}
      <div className={`flex-1 overflow-hidden ${viewMode === 'split' ? 'flex gap-4' : ''}`}>
        {/* Message List */}
        <div
          className={`space-y-2 overflow-y-auto ${viewMode === 'split' ? 'w-2/5 border-r border-gray-200 pr-4' : ''}`}
          role="listbox"
          aria-label="Email messages"
        >
          {filteredMessages.length === 0 && !loading ? (
            <div className="text-center py-8 text-gray-500">
              {searchQuery ? 'No emails match your search.' : 'No emails yet. Click Sync to fetch messages.'}
            </div>
          ) : (
            filteredMessages.map((message, index) => renderMessageItem(message, index))
          )}
        </div>

        {/* Split View Preview Pane */}
        {viewMode === 'split' && (
          <div className="w-3/5 overflow-y-auto">
            {selectedMessage ? (
              <div className="bg-white rounded-lg border border-gray-200 p-6">
                {/* Preview Header */}
                <div className="border-b border-gray-200 pb-4 mb-4">
                  <h2 className="text-xl font-bold text-gray-900 mb-2">{selectedMessage.subject || '(No subject)'}</h2>
                  <div className="flex items-center justify-between text-sm text-gray-600">
                    <div>
                      <span className="font-medium">From:</span> {selectedMessage.from || 'Unknown'}
                    </div>
                    <div>
                      {new Date(selectedMessage.received_date || '').toLocaleString()}
                    </div>
                  </div>
                  {selectedMessage.to && selectedMessage.to.length > 0 && (
                    <div className="text-sm text-gray-600 mt-1">
                      <span className="font-medium">To:</span> {selectedMessage.to.join(', ')}
                    </div>
                  )}
                  {selectedMessage.cc && selectedMessage.cc.length > 0 && (
                    <div className="text-sm text-gray-600 mt-1">
                      <span className="font-medium">CC:</span> {selectedMessage.cc.join(', ')}
                    </div>
                  )}
                </div>

                {/* Attachments */}
                {selectedMessage.attachments && selectedMessage.attachments.length > 0 && (
                  <div className="bg-yellow-50 p-3 rounded mb-4">
                    <p className="text-sm font-medium text-yellow-900 mb-1">Attachments</p>
                    <p className="text-sm text-yellow-800">
                      {selectedMessage.attachments.length} attachment(s) - Download support coming soon
                    </p>
                  </div>
                )}

                {/* Preview Body */}
                <div className="prose prose-sm max-w-none">
                  {selectedMessage.body_html ? (
                    <div
                      dangerouslySetInnerHTML={{ __html: sanitizeHtml(selectedMessage.body_html) }}
                      className="text-gray-800"
                    />
                  ) : (
                    <p className="text-gray-800 whitespace-pre-wrap">
                      {selectedMessage.body || '(No content)'}
                    </p>
                  )}
                </div>
              </div>
            ) : (
