/**
 * Hearthlink UI Component Library
 * Consistent, reusable components with unified styling and interactions
 */

import React, { useRef, useEffect } from 'react';
import type {
  ButtonHTMLAttributes,
  CSSProperties,
  InputHTMLAttributes,
  ReactNode,
  TextareaHTMLAttributes,
} from 'react';
import { HearthlinkColors, HearthlinkAnimations, useHearthlinkTheme } from '../../styles/HearthlinkDesignSystem';

// Button Component
interface HearthlinkButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'ghost' | 'danger';
  size?: 'sm' | 'md' | 'lg';
  loading?: boolean;
  icon?: ReactNode;
  children: ReactNode;
}

export const HearthlinkButton: React.FC<HearthlinkButtonProps> = ({
  variant = 'primary',
  size = 'md',
  loading = false,
  icon,
  children,
  disabled,
  className = '',
  style = {},
  ...props
}) => {
  const theme = useHearthlinkTheme();

  const getVariantStyles = () => {
    switch (variant) {
      case 'primary':
        return theme.components.button.primary;
      case 'secondary':
        return theme.components.button.secondary;
      case 'ghost':
        return theme.components.button.ghost;
      case 'danger':
        return {
          backgroundColor: `${HearthlinkColors.status.error}20`,
          borderColor: HearthlinkColors.status.error,
          color: HearthlinkColors.status.error,
          '&:hover:not(:disabled)': {
            backgroundColor: `${HearthlinkColors.status.error}30`,
            transform: 'translateY(-1px)',
            boxShadow: `0 4px 12px ${HearthlinkColors.status.error}30`
          }
        };
      default:
        return theme.components.button.primary;
    }
  };

  const getSizeStyles = () => {
    switch (size) {
      case 'sm':
        return { padding: '0.5rem 1rem', fontSize: '0.875rem' };
      case 'lg':
        return { padding: '1rem 2rem', fontSize: '1.125rem' };
      default:
        return { padding: '0.75rem 1.5rem', fontSize: '1rem' };
    }
  };

  const buttonStyle = {
    ...theme.components.button.base,
    ...getVariantStyles(),
    ...getSizeStyles(),
    ...style
  };

  return (
    <button
      className={`hlink-focus-visible ${className}`}
      style={buttonStyle}
      disabled={disabled || loading}
      {...props}
    >
      {loading ? (
        <div
          style={{
            width: '16px',
            height: '16px',
            border: '2px solid currentColor',
            borderTop: '2px solid transparent',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite'
          }}
        />
      ) : icon}
      {children}
    </button>
  );
};

// Card Component
interface HearthlinkCardProps {
  children: ReactNode;
  interactive?: boolean;
  hover?: boolean;
  className?: string;
  style?: CSSProperties;
  onClick?: () => void;
}

export const HearthlinkCard: React.FC<HearthlinkCardProps> = ({
  children,
  interactive = false,
  hover = true,
  className = '',
  style = {},
  onClick
}) => {
  const theme = useHearthlinkTheme();

  const cardStyle = {
    ...theme.components.card.base,
    ...(interactive ? theme.components.card.interactive : {}),
    ...(!hover ? { '&:hover': {} } : {}),
    ...style
  };

  return (
    <div
      className={`hlink-focus-visible ${className}`}
      style={cardStyle}
      onClick={onClick}
      onKeyDown={interactive && onClick ? (e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          e.preventDefault();
          onClick();
        }
      } : undefined}
      role={interactive ? 'button' : undefined}
      tabIndex={interactive ? 0 : undefined}
    >
      {children}
    </div>
  );
};

// Input Component
interface HearthlinkInputProps extends InputHTMLAttributes<HTMLInputElement> {
  label?: string;
  error?: string;
  icon?: ReactNode;
}

export const HearthlinkInput: React.FC<HearthlinkInputProps> = ({
  label,
