// Modal Component
interface HearthlinkModalProps {
  isOpen: boolean;
  onClose: () => void;
  title?: string;
  children: ReactNode;
  size?: 'sm' | 'md' | 'lg' | 'xl';
}

export const HearthlinkModal: React.FC<HearthlinkModalProps> = ({
  isOpen,
  onClose,
  title,
  children,
  size = 'md'
}) => {
  const theme = useHearthlinkTheme();
  const modalRef = useRef<HTMLDivElement>(null);

  const getSizeStyles = () => {
    switch (size) {
      case 'sm':
        return { maxWidth: '400px' };
      case 'lg':
        return { maxWidth: '700px' };
      case 'xl':
        return { maxWidth: '900px' };
      default:
        return { maxWidth: '500px' };
    }
  };

  // Handle ESC key and outside clicks
  useEffect(() => {
    if (!isOpen) return;

    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') onClose();
    };

    const handleClickOutside = (e: MouseEvent) => {
      if (modalRef.current && !modalRef.current.contains(e.target as Node)) {
        onClose();
      }
    };

    document.addEventListener('keydown', handleEscape);
    document.addEventListener('mousedown', handleClickOutside);
    document.body.style.overflow = 'hidden';

    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.removeEventListener('mousedown', handleClickOutside);
      document.body.style.overflow = 'unset';
    };
  }, [isOpen, onClose]);

  if (!isOpen) return null;

  return (
    <div style={theme.components.modal.overlay}>
      <div
        ref={modalRef}
        style={{
          ...theme.components.modal.content,
          ...getSizeStyles()
        }}
      >
        {title && (
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '1.5rem',
            paddingBottom: '1rem',
            borderBottom: `1px solid ${HearthlinkColors.ui.borderSecondary}`
          }}>
            <h2 style={{
              margin: 0,
              fontSize: '1.25rem',
              fontWeight: 600,
              color: HearthlinkColors.ui.textPrimary
            }}>
              {title}
            </h2>
            <button
              onClick={onClose}
              style={{
                background: 'none',
                border: 'none',
                color: HearthlinkColors.ui.textSecondary,
                cursor: 'pointer',
                fontSize: '1.5rem',
                padding: '0.25rem',
                transition: HearthlinkAnimations.transition.colors
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.color = HearthlinkColors.ui.textPrimary;
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.color = HearthlinkColors.ui.textSecondary;
              }}
            >
              A-
            </button>
          </div>
        )}
        {children}
      </div>
    </div>
  );
};

// Badge Component
interface HearthlinkBadgeProps {
  children: ReactNode;
  variant?: 'default' | 'success' | 'warning' | 'error' | 'info';
  size?: 'sm' | 'md';
}

export const HearthlinkBadge: React.FC<HearthlinkBadgeProps> = ({
  children,
  variant = 'default',
  size = 'md'
}) => {
  const getVariantStyles = () => {
    switch (variant) {
      case 'success':
        return {
          backgroundColor: `${HearthlinkColors.status.success}20`,
          color: HearthlinkColors.status.success,
          border: `1px solid ${HearthlinkColors.status.success}50`
        };
      case 'warning':
        return {
          backgroundColor: `${HearthlinkColors.status.warning}20`,
          color: HearthlinkColors.status.warning,
          border: `1px solid ${HearthlinkColors.status.warning}50`
        };
      case 'error':
        return {
          backgroundColor: `${HearthlinkColors.status.error}20`,
          color: HearthlinkColors.status.error,
          border: `1px solid ${HearthlinkColors.status.error}50`
        };
      case 'info':
        return {
          backgroundColor: `${HearthlinkColors.status.info}20`,
          color: HearthlinkColors.status.info,
          border: `1px solid ${HearthlinkColors.status.info}50`
        };
      default:
        return {
          backgroundColor: `${HearthlinkColors.ui.textMuted}20`,
          color: HearthlinkColors.ui.textSecondary,
          border: `1px solid ${HearthlinkColors.ui.textMuted}50`
        };
    }
  };

  const getSizeStyles = () => {
    switch (size) {
      case 'sm':
        return {
          fontSize: '0.75rem',
          padding: '0.25rem 0.5rem'
        };
      default:
        return {
          fontSize: '0.875rem',
          padding: '0.375rem 0.75rem'
        };
    }
  };

  return (
    <span
      style={{
        display: 'inline-flex',
        alignItems: 'center',
        borderRadius: '0.375rem',
        fontWeight: 500,
        transition: HearthlinkAnimations.transition.all,
        ...getVariantStyles(),
        ...getSizeStyles()
      }}
    >
      {children}
    </span>
  );
};

// Loading Spinner Component
interface HearthlinkSpinnerProps {
  size?: 'sm' | 'md' | 'lg';
  color?: string;
}

export const HearthlinkSpinner: React.FC<HearthlinkSpinnerProps> = ({
  size = 'md',
  color = HearthlinkColors.starcraft.blue
}) => {
  const getSizeValue = () => {
    switch (size) {
      case 'sm': return '16px';
      case 'lg': return '32px';
      default: return '24px';
    }
  };

  return (
    <div
      style={{
        width: getSizeValue(),
        height: getSizeValue(),
        border: '2px solid transparent',
        borderTop: `2px solid ${color}`,
        borderRadius: '50%',
        animation: 'spin 1s linear infinite'
      }}
    />
  );
};

// Progress Bar Component
interface HearthlinkProgressProps {
  value: number;
  max?: number;
  color?: string;
  showLabel?: boolean;
  size?: 'sm' | 'md' | 'lg';
}

export const HearthlinkProgress: React.FC<HearthlinkProgressProps> = ({
  value,
  max = 100,
  color = HearthlinkColors.starcraft.blue,
  showLabel = false,
  size = 'md'
}) => {
  const percentage = Math.min((value / max) * 100, 100);

  const getHeightValue = () => {
    switch (size) {
      case 'sm': return '4px';
      case 'lg': return '12px';
      default: return '8px';
    }
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
      <div
        style={{
          flex: 1,
          backgroundColor: `${HearthlinkColors.starcraft.dark}80`,
          borderRadius: '0.25rem',
          overflow: 'hidden',
          height: getHeightValue(),
          border: `1px solid ${HearthlinkColors.ui.borderSecondary}`
        }}
      >
        <div
          style={{
            height: '100%',
            backgroundColor: color,
            width: `${percentage}%`,
            transition: HearthlinkAnimations.transition.all,
            boxShadow: `0 0 8px ${color}50`
          }}
        />
      </div>
      {showLabel && (
        <span style={{
          fontSize: '0.875rem',
          color: HearthlinkColors.ui.textSecondary,
          minWidth: '3rem',
          textAlign: 'right' as const
        }}>
          {Math.round(percentage)}%
        </span>
      )}
    </div>
  );
};

// Export all components
export const HearthlinkUI = {
  Button: HearthlinkButton,
  Card: HearthlinkCard,
  Input: HearthlinkInput,
  Textarea: HearthlinkTextarea,
  Modal: HearthlinkModal,
  Badge: HearthlinkBadge,
  Spinner: HearthlinkSpinner,
  Progress: HearthlinkProgress
};

export default HearthlinkUI;

